_MHD_static_inline unsigned int
digest_get_size (struct DigestAlgorithm *da)
{
  /* Probe: log entry and state to help reproduce Rule 8.13 scenario. */
  {
    FILE *log = fopen("/tmp/logs/2025.txt", "a");
    if (log)
    {
      fprintf(log, "ENTER digest_get_size: da=%p\n", (void *)da);
      if (da)
      {
        fprintf(log, "state before asserts: uninitialised=%d, algo_selected=%d, algo=%d\n",
                (int)da->uninitialised, (int)da->algo_selected, (int)da->algo);
      }
      else
      {
        fprintf(log, "state before asserts: da==NULL\n");
      }
      fclose(log);
    }
  }

  mhd_assert (! da->uninitialised);
  mhd_assert (da->algo_selected);
#ifdef MHD_MD5_SUPPORT
  if (MHD_DIGEST_BASE_ALGO_MD5 == da->algo)
  {
    FILE *log = fopen("/tmp/logs/2025.txt", "a");
    if (log)
    {
      fprintf(log, "branch taken: MD5 -> return %d\n", MD5_DIGEST_SIZE);
      fclose(log);
    }
    return MD5_DIGEST_SIZE;
  }
#endif /* MHD_MD5_SUPPORT */
#ifdef MHD_SHA256_SUPPORT
  if (MHD_DIGEST_BASE_ALGO_SHA256 == da->algo)
  {
    FILE *log = fopen("/tmp/logs/2025.txt", "a");
    if (log)
    {
      fprintf(log, "branch taken: SHA256 -> return %d\n", SHA256_DIGEST_SIZE);
      fclose(log);
    }
    return SHA256_DIGEST_SIZE;
  }
#endif /* MHD_SHA256_SUPPORT */
#ifdef MHD_SHA512_256_SUPPORT
  if (MHD_DIGEST_BASE_ALGO_SHA512_256 == da->algo)
  {
    FILE *log = fopen("/tmp/logs/2025.txt", "a");
    if (log)
    {
      fprintf(log, "branch taken: SHA512_256 -> return %d\n", SHA512_256_DIGEST_SIZE);
      fclose(log);
    }
    return SHA512_256_DIGEST_SIZE;
  }
#endif /* MHD_SHA512_256_SUPPORT */

  /* Log unexpected path before asserting to ensure evidence is recorded. */
  {
    FILE *log = fopen("/tmp/logs/2025.txt", "a");
    if (log)
    {
      fprintf(log, "unexpected branch: no matching algo (algo=%d) -> assert(0)\n", (int)da->algo);
      fclose(log);
    }
  }

  mhd_assert (0); /* May not happen */
  return 0;
}
