_MHD_static_inline void
digest_reset (struct DigestAlgorithm *da)
{
  /* Open log file to record control flow and state for rule 2.2 investigation */
  FILE *log = fopen("/tmp/logs/2658.txt", "a");
  if (log)
  {
    fprintf(log, "digest_reset: ENTER da=%p algo=%d ready_for_hashing=%d uninitialised=%d algo_selected=%d hashing=%d\n",
            (void *)da,
            (int) da->algo,
            (int) da->ready_for_hashing,
            (int) da->uninitialised,
            (int) da->algo_selected,
            (int) da->hashing);
    fflush(log);
  }

  mhd_assert (! da->uninitialised);
  if (log) { fprintf(log, "digest_reset: passed assert !da->uninitialised\n"); fflush(log); }
  mhd_assert (da->algo_selected);
  if (log) { fprintf(log, "digest_reset: passed assert da->algo_selected\n"); fflush(log); }
  mhd_assert (! da->hashing);
  if (log) { fprintf(log, "digest_reset: passed assert !da->hashing\n"); fflush(log); }
#ifdef MHD_MD5_SUPPORT
  if (MHD_DIGEST_BASE_ALGO_MD5 == da->algo)
  {
#ifdef MHD_MD5_HAS_FINISH
    if (log) { fprintf(log, "digest_reset: MD5 branch: expect !ready_for_hashing, current=%d\n", (int) da->ready_for_hashing); fflush(log); }
    mhd_assert (! da->ready_for_hashing);
#else  /* ! MHD_MD5_HAS_FINISH */
    if (log) { fprintf(log, "digest_reset: MD5 branch: expect ready_for_hashing, current=%d\n", (int) da->ready_for_hashing); fflush(log); }
    mhd_assert (da->ready_for_hashing);
#endif /* ! MHD_MD5_HAS_FINISH */
    if (log) { fprintf(log, "digest_reset: MD5: calling MHD_MD5_reset\n"); fflush(log); }
    MHD_MD5_reset (&da->ctx.md5_ctx);
    if (log) { fprintf(log, "digest_reset: MD5: returned from MHD_MD5_reset\n"); fflush(log); }
#ifdef _DEBUG
    if (log) { fprintf(log, "digest_reset: MD5: about to set da->ready_for_hashing = true (DEBUG build)\n"); fflush(log); }
    da->ready_for_hashing = true;
    if (log) { fprintf(log, "digest_reset: MD5: da->ready_for_hashing now=%d\n", (int) da->ready_for_hashing); fflush(log); }
#endif /* _DEBUG */
  }
  else
#endif /* MHD_MD5_SUPPORT */
#ifdef MHD_SHA256_SUPPORT
  if (MHD_DIGEST_BASE_ALGO_SHA256 == da->algo)
  {
#ifdef MHD_SHA256_HAS_FINISH
    if (log) { fprintf(log, "digest_reset: SHA256 branch: expect !ready_for_hashing, current=%d\n", (int) da->ready_for_hashing); fflush(log); }
    mhd_assert (! da->ready_for_hashing);
#else  /* ! MHD_SHA256_HAS_FINISH */
    if (log) { fprintf(log, "digest_reset: SHA256 branch: expect ready_for_hashing, current=%d\n", (int) da->ready_for_hashing); fflush(log); }
    mhd_assert (da->ready_for_hashing);
#endif /* ! MHD_SHA256_HAS_FINISH */
    if (log) { fprintf(log, "digest_reset: SHA256: calling MHD_SHA256_reset\n"); fflush(log); }
    MHD_SHA256_reset (&da->ctx.sha256_ctx);
    if (log) { fprintf(log, "digest_reset: SHA256: returned from MHD_SHA256_reset\n"); fflush(log); }
#ifdef _DEBUG
    if (log) { fprintf(log, "digest_reset: SHA256: about to set da->ready_for_hashing = true (DEBUG build)\n"); fflush(log); }
    da->ready_for_hashing = true;
    if (log) { fprintf(log, "digest_reset: SHA256: da->ready_for_hashing now=%d\n", (int) da->ready_for_hashing); fflush(log); }
#endif /* _DEBUG */
  }
  else
#endif /* MHD_SHA256_SUPPORT */
#ifdef MHD_SHA512_256_SUPPORT
  if (MHD_DIGEST_BASE_ALGO_SHA512_256 == da->algo)
  {
    if (log) { fprintf(log, "digest_reset: SHA512_256 branch: expect !ready_for_hashing, current=%d\n", (int) da->ready_for_hashing); fflush(log); }
    mhd_assert (! da->ready_for_hashing);
    if (log) { fprintf(log, "digest_reset: SHA512_256: calling MHD_SHA512_256_init\n"); fflush(log); }
    MHD_SHA512_256_init (&da->ctx.sha512_256_ctx);
    if (log) { fprintf(log, "digest_reset: SHA512_256: returned from MHD_SHA512_256_init\n"); fflush(log); }
#ifdef _DEBUG
    if (log) { fprintf(log, "digest_reset: SHA512_256: about to set da->ready_for_hashing = true (DEBUG build)\n"); fflush(log); }
    da->ready_for_hashing = true;
    if (log) { fprintf(log, "digest_reset: SHA512_256: da->ready_for_hashing now=%d\n", (int) da->ready_for_hashing); fflush(log); }
#endif
  }
  else
#endif /* MHD_SHA512_256_SUPPORT */
  {
#ifdef _DEBUG
    if (log) { fprintf(log, "digest_reset: default/bad-algo branch: about to set da->ready_for_hashing = false (DEBUG build)\n"); fflush(log); }
    da->ready_for_hashing = false;
    if (log) { fprintf(log, "digest_reset: default/bad-algo branch: da->ready_for_hashing now=%d\n", (int) da->ready_for_hashing); fflush(log); }
#endif
    if (log) { fprintf(log, "digest_reset: default/bad-algo branch: about to call mhd_assert(0)\n"); fflush(log); }
    mhd_assert (0); /* May not happen, bad algorithm */
  }

  if (log)
  {
    fprintf(log, "digest_reset: EXIT da=%p algo=%d ready_for_hashing=%d\n",
            (void *)da,
            (int) da->algo,
            (int) da->ready_for_hashing);
    fclose(log);
  }
}
