_MHD_static_inline void
digest_reset (struct DigestAlgorithm *da)
{
  /* Log entry and key state to help reproduce assertion/no-op scenarios. */
  FILE *mhd_log_file = fopen("/tmp/logs/2658.txt", "a");
  if (mhd_log_file != NULL)
  {
    fprintf(mhd_log_file, "digest_reset ENTER: da=%p\n", (void *)da);
    /* Only log members that are always referenced in all builds to avoid
       introducing references to conditionally-compiled members. */
    fprintf(mhd_log_file, "  pre-state: uninitialised=%d, algo_selected=%d, hashing=%d, algo=%d\n",
            (int) da->uninitialised,
            (int) da->algo_selected,
            (int) da->hashing,
            (int) da->algo);
    fflush(mhd_log_file);
  }

  /* Preserve original assertions and behavior. */
  mhd_assert (! da->uninitialised);
  if (mhd_log_file) { fprintf(mhd_log_file, "  passed: assert(!uninitialised)\n"); fflush(mhd_log_file); }

  mhd_assert (da->algo_selected);
  if (mhd_log_file) { fprintf(mhd_log_file, "  passed: assert(algo_selected)\n"); fflush(mhd_log_file); }

  mhd_assert (! da->hashing);
  if (mhd_log_file) { fprintf(mhd_log_file, "  passed: assert(!hashing)\n"); fflush(mhd_log_file); }

#ifdef MHD_MD5_SUPPORT
  if (MHD_DIGEST_BASE_ALGO_MD5 == da->algo)
  {
#ifdef MHD_MD5_HAS_FINISH
    mhd_assert (! da->ready_for_hashing);
    if (mhd_log_file) { fprintf(mhd_log_file, "  branch: MD5 (has finish) - assert(!ready_for_hashing) evaluated, ready_for_hashing=%d\n", (int) da->ready_for_hashing); fflush(mhd_log_file); }
#else  /* ! MHD_MD5_HAS_FINISH */
    mhd_assert (da->ready_for_hashing);
    if (mhd_log_file) { fprintf(mhd_log_file, "  branch: MD5 (no finish) - assert(ready_for_hashing) evaluated, ready_for_hashing=%d\n", (int) da->ready_for_hashing); fflush(mhd_log_file); }
#endif /* ! MHD_MD5_HAS_FINISH */
    MHD_MD5_reset (&da->ctx.md5_ctx);
    if (mhd_log_file) { fprintf(mhd_log_file, "  action: MHD_MD5_reset called\n"); fflush(mhd_log_file); }
#ifdef _DEBUG
    da->ready_for_hashing = true;
    if (mhd_log_file) { fprintf(mhd_log_file, "  debug: MD5 set ready_for_hashing=true\n"); fflush(mhd_log_file); }
#endif /* _DEBUG */
  }
  else
#endif /* MHD_MD5_SUPPORT */
#ifdef MHD_SHA256_SUPPORT
  if (MHD_DIGEST_BASE_ALGO_SHA256 == da->algo)
  {
#ifdef MHD_SHA256_HAS_FINISH
    mhd_assert (! da->ready_for_hashing);
    if (mhd_log_file) { fprintf(mhd_log_file, "  branch: SHA256 (has finish) - assert(!ready_for_hashing) evaluated, ready_for_hashing=%d\n", (int) da->ready_for_hashing); fflush(mhd_log_file); }
#else  /* ! MHD_SHA256_HAS_FINISH */
    mhd_assert (da->ready_for_hashing);
    if (mhd_log_file) { fprintf(mhd_log_file, "  branch: SHA256 (no finish) - assert(ready_for_hashing) evaluated, ready_for_hashing=%d\n", (int) da->ready_for_hashing); fflush(mhd_log_file); }
#endif /* ! MHD_SHA256_HAS_FINISH */
    MHD_SHA256_reset (&da->ctx.sha256_ctx);
    if (mhd_log_file) { fprintf(mhd_log_file, "  action: MHD_SHA256_reset called\n"); fflush(mhd_log_file); }
#ifdef _DEBUG
    da->ready_for_hashing = true;
    if (mhd_log_file) { fprintf(mhd_log_file, "  debug: SHA256 set ready_for_hashing=true\n"); fflush(mhd_log_file); }
#endif /* _DEBUG */
  }
  else
#endif /* MHD_SHA256_SUPPORT */
#ifdef MHD_SHA512_256_SUPPORT
  if (MHD_DIGEST_BASE_ALGO_SHA512_256 == da->algo)
  {
    mhd_assert (! da->ready_for_hashing);
    if (mhd_log_file) { fprintf(mhd_log_file, "  branch: SHA512_256 - assert(!ready_for_hashing) evaluated, ready_for_hashing=%d\n", (int) da->ready_for_hashing); fflush(mhd_log_file); }
    MHD_SHA512_256_init (&da->ctx.sha512_256_ctx);
    if (mhd_log_file) { fprintf(mhd_log_file, "  action: MHD_SHA512_256_init called\n"); fflush(mhd_log_file); }
#ifdef _DEBUG
    da->ready_for_hashing = true;
    if (mhd_log_file) { fprintf(mhd_log_file, "  debug: SHA512_256 set ready_for_hashing=true\n"); fflush(mhd_log_file); }
#endif
  }
  else
#endif /* MHD_SHA512_256_SUPPORT */
  {
#ifdef _DEBUG
    da->ready_for_hashing = false;
    if (mhd_log_file) { fprintf(mhd_log_file, "  debug: fallback - set ready_for_hashing=false\n"); fflush(mhd_log_file); }
#endif
    if (mhd_log_file) { fprintf(mhd_log_file, "  error: unexpected algorithm, about to assert(0)\n"); fflush(mhd_log_file); }
    mhd_assert (0); /* May not happen, bad algorithm */
  }

  if (mhd_log_file)
  {
    fprintf(mhd_log_file, "digest_reset EXIT: da=%p\n\n", (void *)da);
    fclose(mhd_log_file);
  }
}
