_MHD_static_inline void
digest_update (struct DigestAlgorithm *da,
               const void *data,
               size_t length)
{
  /* Preserve original preconditions */
  mhd_assert (! da->uninitialised);
  mhd_assert (da->algo_selected);
  mhd_assert (da->ready_for_hashing);

  /* Open log file for appending; best-effort logging (do not abort if logging unavailable) */
  FILE *___mhd_log_f = fopen("/tmp/logs/1998.txt", "a");
  if (___mhd_log_f != NULL)
  {
    fprintf(___mhd_log_f, "digest_update: entry da=%p uninitialised=%d algo_selected=%d ready_for_hashing=%d algo=%d length=%zu\n",
            (void *) da,
            (int) da->uninitialised,
            (int) da->algo_selected,
            (int) da->ready_for_hashing,
            (int) da->algo,
            length);
    fflush(___mhd_log_f);
  }

#ifdef MHD_MD5_SUPPORT
  if (MHD_DIGEST_BASE_ALGO_MD5 == da->algo)
  {
    if (___mhd_log_f) fprintf(___mhd_log_f, "digest_update: selected branch MD5\n");
    MHD_MD5_update (&da->ctx.md5_ctx, (const uint8_t *) data, length);
  }
  else
#endif /* MHD_MD5_SUPPORT */
#ifdef MHD_SHA256_SUPPORT
  if (MHD_DIGEST_BASE_ALGO_SHA256 == da->algo)
  {
    if (___mhd_log_f) fprintf(___mhd_log_f, "digest_update: selected branch SHA256\n");
    MHD_SHA256_update (&da->ctx.sha256_ctx, (const uint8_t *) data, length);
  }
  else
#endif /* MHD_SHA256_SUPPORT */
#ifdef MHD_SHA512_256_SUPPORT
  if (MHD_DIGEST_BASE_ALGO_SHA512_256 == da->algo)
  {
    if (___mhd_log_f) fprintf(___mhd_log_f, "digest_update: selected branch SHA512_256\n");
    MHD_SHA512_256_update (&da->ctx.sha512_256_ctx,
                           (const uint8_t *) data, length);
  }
  else
#endif /* MHD_SHA512_256_SUPPORT */
  mhd_assert (0);   /* May not happen */

#ifdef _DEBUG
  if (___mhd_log_f)
  {
    fprintf(___mhd_log_f, "digest_update: _DEBUG: about to set da->hashing (before=%d)\n", (int) da->hashing);
    fflush(___mhd_log_f);
  }
  da->hashing = true;
  if (___mhd_log_f)
  {
    fprintf(___mhd_log_f, "digest_update: _DEBUG: da->hashing now=%d\n", (int) da->hashing);
    fflush(___mhd_log_f);
  }
#endif

  if (___mhd_log_f) fclose(___mhd_log_f);
}
