_MHD_static_inline void
digest_update (struct DigestAlgorithm *da,
               const void *data,
               size_t length)
{
  /* Open log file for append. We try to record control flow and key state so
     that runs can demonstrate whether the terminal mhd_assert(0) is reached
     (which may be compiled out in production builds). */
  FILE *log = fopen("/tmp/logs/1998.txt", "a");
  if (log) {
    fprintf(log, "digest_update ENTER: da=%p uninitialised=%d algo_selected=%d ready_for_hashing=%d algo=%d length=%zu\n",
            (void *) da,
            (int) da->uninitialised,
            (int) da->algo_selected,
            (int) da->ready_for_hashing,
            (int) da->algo,
            length);
    fflush(log);
  }

  mhd_assert (! da->uninitialised);
  if (log) { fprintf(log, "assert: !uninitialised passed\n"); fflush(log); }
  mhd_assert (da->algo_selected);
  if (log) { fprintf(log, "assert: algo_selected passed\n"); fflush(log); }
  mhd_assert (da->ready_for_hashing);
  if (log) { fprintf(log, "assert: ready_for_hashing passed\n"); fflush(log); }

#ifdef MHD_MD5_SUPPORT
  if (MHD_DIGEST_BASE_ALGO_MD5 == da->algo) {
    if (log) { fprintf(log, "branch taken: MD5\n"); fflush(log); }
    MHD_MD5_update (&da->ctx.md5_ctx, (const uint8_t *) data, length);
    if (log) { fprintf(log, "MD5_update completed\n"); fflush(log); fclose(log); log = NULL; }
  } else
#endif /* MHD_MD5_SUPPORT */
#ifdef MHD_SHA256_SUPPORT
  if (MHD_DIGEST_BASE_ALGO_SHA256 == da->algo) {
    if (log) { fprintf(log, "branch taken: SHA256\n"); fflush(log); }
    MHD_SHA256_update (&da->ctx.sha256_ctx, (const uint8_t *) data, length);
    if (log) { fprintf(log, "SHA256_update completed\n"); fflush(log); fclose(log); log = NULL; }
  } else
#endif /* MHD_SHA256_SUPPORT */
#ifdef MHD_SHA512_256_SUPPORT
  if (MHD_DIGEST_BASE_ALGO_SHA512_256 == da->algo) {
    if (log) { fprintf(log, "branch taken: SHA512_256\n"); fflush(log); }
    MHD_SHA512_256_update (&da->ctx.sha512_256_ctx,
                           (const uint8_t *) data, length);
    if (log) { fprintf(log, "SHA512_256_update completed\n"); fflush(log); fclose(log); log = NULL; }
  } else
#endif /* MHD_SHA512_256_SUPPORT */
  {
    /* Unexpected algorithm value: record and then trigger the original assertion. */
    if (log) {
      fprintf(log, "unexpected algorithm value reached: algo=%d; about to call mhd_assert(0)\n", (int) da->algo);
      fflush(log);
      fclose(log);
      log = NULL;
    }
    mhd_assert (0);   /* May not happen */
  }

#ifdef _DEBUG
  da->hashing = true;
#endif
}
