static ssize_t
file_reader (void *cls,
             uint64_t pos,
             char *buf,
             size_t max)
{
  struct MHD_Response *response = cls;
  FILE *log = fopen("/tmp/logs/3869.txt", "a");
#if ! defined(_WIN32) || defined(__CYGWIN__)
  ssize_t n;
#else  /* _WIN32 && !__CYGWIN__ */
  const HANDLE fh = (HANDLE) (uintptr_t) _get_osfhandle (response->fd);
#endif /* _WIN32 && !__CYGWIN__ */
  const int64_t offset64 = (int64_t) (pos + response->fd_off);

  if (log)
  {
    fprintf(log, "ENTER file_reader: pos=%llu response_fd=%d fd_off=%lld offset64=%lld max=%zu\n",
            (unsigned long long) pos,
            (int) response->fd,
            (long long) response->fd_off,
            (long long) offset64,
            max);
    fflush(log);
  }

  if (offset64 < 0)
  {
    if (log)
    {
      fprintf(log, "RETURN file_reader: offset64 < 0 (%lld) -> MHD_CONTENT_READER_END_WITH_ERROR\n",
              (long long) offset64);
      fclose(log);
    }
    return MHD_CONTENT_READER_END_WITH_ERROR; /* seek to required position is not possible */
  }

#if ! defined(_WIN32) || defined(__CYGWIN__)
  if (max > SSIZE_MAX)
  {
    if (log)
    {
      fprintf(log, "Clamping max: original=%zu SSIZE_MAX=%zd\n", max, (ssize_t) SSIZE_MAX);
    }
    max = SSIZE_MAX; /* Clamp to maximum return value. */
  }

#if defined(HAVE_PREAD64)
  n = pread64 (response->fd, buf, max, offset64);
  if (log)
  {
    fprintf(log, "Called pread64(fd=%d, max=%zu, offset64=%lld) -> n=%zd\n",
            (int) response->fd, max, (long long) offset64, n);
    fflush(log);
  }
#elif defined(HAVE_PREAD)
  if (log)
  {
    fprintf(log, "Evaluation: sizeof(off_t)=%zu sizeof(uint64_t)=%zu left_expr=(sizeof(off_t) < sizeof(uint64_t))=%d offset64=%lld INT32_MAX=%d\n",
            (size_t) sizeof(off_t), (size_t) sizeof(uint64_t), (int) (sizeof(off_t) < sizeof(uint64_t)), (long long) offset64, INT32_MAX);
    fflush(log);
  }
  if ( (sizeof(off_t) < sizeof (uint64_t)) &&
       (offset64 > (uint64_t) INT32_MAX) )
  {
    if (log)
    {
      fprintf(log, "RETURN file_reader: condition for pread failure met -> MHD_CONTENT_READER_END_WITH_ERROR\n");
      fclose(log);
    }
    return MHD_CONTENT_READER_END_WITH_ERROR; /* Read at required position is not possible. */
  }

  n = pread (response->fd, buf, max, (off_t) offset64);
  if (log)
  {
    fprintf(log, "Called pread(fd=%d, max=%zu, offset64=%lld) -> n=%zd\n",
            (int) response->fd, max, (long long) offset64, n);
    fflush(log);
  }
#else  /* ! HAVE_PREAD */
#if defined(HAVE_LSEEK64)
  if (lseek64 (response->fd,
               offset64,
               SEEK_SET) != offset64)
  {
    if (log)
    {
      fprintf(log, "RETURN file_reader: lseek64 failed to reach offset %lld -> MHD_CONTENT_READER_END_WITH_ERROR\n",
              (long long) offset64);
      fclose(log);
    }
    return MHD_CONTENT_READER_END_WITH_ERROR; /* can't seek to required position */
  }
#else  /* ! HAVE_LSEEK64 */
  if (log)
  {
    fprintf(log, "Evaluation: sizeof(off_t)=%zu sizeof(uint64_t)=%zu left_expr=(sizeof(off_t) < sizeof(uint64_t))=%d offset64=%lld INT32_MAX=%d\n",
            (size_t) sizeof(off_t), (size_t) sizeof(uint64_t), (int) (sizeof(off_t) < sizeof(uint64_t)), (long long) offset64, INT32_MAX);
    fflush(log);
  }
  if ( (sizeof(off_t) < sizeof (uint64_t)) &&
       (offset64 > (uint64_t) INT32_MAX) )
  {
    if (log)
    {
      fprintf(log, "RETURN file_reader: condition for lseek failure met -> MHD_CONTENT_READER_END_WITH_ERROR\n");
      fclose(log);
    }
    return MHD_CONTENT_READER_END_WITH_ERROR; /* seek to required position is not possible */
  }

  if (lseek (response->fd,
             (off_t) offset64,
             SEEK_SET) != (off_t) offset64)
  {
    if (log)
    {
      fprintf(log, "RETURN file_reader: lseek failed to reach offset %lld -> MHD_CONTENT_READER_END_WITH_ERROR\n",
              (long long) offset64);
      fclose(log);
    }
    return MHD_CONTENT_READER_END_WITH_ERROR; /* can't seek to required position */
  }
#endif /* ! HAVE_LSEEK64 */
  n = read (response->fd,
            buf,
            max);
  if (log)
  {
    fprintf(log, "Called read(fd=%d, max=%zu) -> n=%zd\n",
            (int) response->fd, max, n);
    fflush(log);
  }

#endif /* ! HAVE_PREAD */
  if (0 == n)
  {
    if (log)
    {
      fprintf(log, "RETURN file_reader: n == 0 -> MHD_CONTENT_READER_END_OF_STREAM\n");
      fclose(log);
    }
    return MHD_CONTENT_READER_END_OF_STREAM;
  }
  if (n < 0)
  {
    if (log)
    {
      fprintf(log, "RETURN file_reader: n < 0 (n=%zd) -> MHD_CONTENT_READER_END_WITH_ERROR\n", n);
      fclose(log);
    }
    return MHD_CONTENT_READER_END_WITH_ERROR;
  }
  if (log)
  {
    fprintf(log, "RETURN file_reader: success -> n=%zd\n", n);
    fclose(log);
  }
  return n;
#else /* _WIN32 && !__CYGWIN__ */
  if (INVALID_HANDLE_VALUE == fh)
  {
    if (log)
    {
      fprintf(log, "RETURN file_reader: INVALID_HANDLE_VALUE -> MHD_CONTENT_READER_END_WITH_ERROR\n");
      fclose(log);
    }
    return MHD_CONTENT_READER_END_WITH_ERROR; /* Value of 'response->fd' is not valid. */
  }
  else
  {
    OVERLAPPED f_ol = {0, 0, {{0, 0}}, 0};   /* Initialize to zero. */
    ULARGE_INTEGER pos_uli;
    DWORD toRead = (max > INT32_MAX) ? INT32_MAX : (DWORD) max;
    DWORD resRead;

    pos_uli.QuadPart = (uint64_t) offset64;   /* Simple transformation 64bit -> 2x32bit. */
    f_ol.Offset = pos_uli.LowPart;
    f_ol.OffsetHigh = pos_uli.HighPart;
    if (log)
    {
      fprintf(log, "Windows branch: fh=%p toRead=%u offsetLow=%u offsetHigh=%u\n",
              (void *) fh, (unsigned) toRead, (unsigned) f_ol.Offset, (unsigned) f_ol.OffsetHigh);
      fflush(log);
    }
    if (! ReadFile (fh, (void *) buf, toRead, &resRead, &f_ol))
    {
      if (log)
      {
        fprintf(log, "RETURN file_reader: ReadFile failed -> MHD_CONTENT_READER_END_WITH_ERROR\n");
        fclose(log);
      }
      return MHD_CONTENT_READER_END_WITH_ERROR;   /* Read error. */
    }
    if (0 == resRead)
    {
      if (log)
      {
        fprintf(log, "RETURN file_reader: resRead == 0 -> MHD_CONTENT_READER_END_OF_STREAM\n");
        fclose(log);
      }
      return MHD_CONTENT_READER_END_OF_STREAM;
    }
    if (log)
    {
      fprintf(log, "RETURN file_reader: Windows success -> resRead=%u\n", (unsigned) resRead);
      fclose(log);
    }
    return (ssize_t) resRead;
  }
#endif /* _WIN32 && !__CYGWIN__ */
}
