static ssize_t
file_reader (void *cls,
             uint64_t pos,
             char *buf,
             size_t max)
{
  struct MHD_Response *response = cls;
#if ! defined(_WIN32) || defined(__CYGWIN__)
  ssize_t n;
#else  /* _WIN32 && !__CYGWIN__ */
  const HANDLE fh = (HANDLE) (uintptr_t) _get_osfhandle (response->fd);
#endif /* _WIN32 && !__CYGWIN__ */
  const int64_t offset64 = (int64_t) (pos + response->fd_off);

  /* Logging probe file - append mode */
  {
    FILE *logf = fopen("/tmp/logs/3869.txt", "a");
    if (logf)
    {
      fprintf(logf, "ENTER file_reader: pos=%llu, max=%llu, response->fd=%d, response->fd_off=%lld, computed offset64=%lld\n",
              (unsigned long long) pos,
              (unsigned long long) max,
              (int) response->fd,
              (long long) response->fd_off,
              (long long) offset64);
      fclose(logf);
    }
  }

  if (offset64 < 0)
  {
    FILE *logf = fopen("/tmp/logs/3869.txt", "a");
    if (logf)
    {
      fprintf(logf, "RETURN: offset64 < 0 (%lld) -> MHD_CONTENT_READER_END_WITH_ERROR\n", (long long) offset64);
      fclose(logf);
    }
    return MHD_CONTENT_READER_END_WITH_ERROR; /* seek to required position is not possible */
  }

#if ! defined(_WIN32) || defined(__CYGWIN__)
  if (max > SSIZE_MAX)
    max = SSIZE_MAX; /* Clamp to maximum return value. */

  /* Log sizeof comparison and current values that influence invariant check */
  {
    FILE *logf = fopen("/tmp/logs/3869.txt", "a");
    if (logf)
    {
      unsigned long long sz_off_t = (unsigned long long) sizeof(off_t);
      unsigned long long sz_u64 = (unsigned long long) sizeof(uint64_t);
      int cmp = (sizeof(off_t) < sizeof(uint64_t));
      fprintf(logf, "PLATFORM CHECK: sizeof(off_t)=%llu, sizeof(uint64_t)=%llu, (sizeof(off_t) < sizeof(uint64_t))=%d, offset64=%lld, INT32_MAX=%d\n",
              sz_off_t, sz_u64, cmp, (long long) offset64, (int) INT32_MAX);
      fclose(logf);
    }
  }

#if defined(HAVE_PREAD64)
  n = pread64 (response->fd, buf, max, offset64);
  {
    FILE *logf = fopen("/tmp/logs/3869.txt", "a");
    if (logf)
    {
      fprintf(logf, "BRANCH: HAVE_PREAD64 -> pread64 returned %lld\n", (long long) n);
      fclose(logf);
    }
  }
#elif defined(HAVE_PREAD)
  if ( (sizeof(off_t) < sizeof (uint64_t)) &&
       (offset64 > (uint64_t) INT32_MAX) )
  {
    FILE *logf = fopen("/tmp/logs/3869.txt", "a");
    if (logf)
    {
      fprintf(logf, "CONDITION: (sizeof(off_t) < sizeof(uint64_t)) && (offset64 > INT32_MAX) is true -> cannot pread, returning END_WITH_ERROR\n");
      fprintf(logf, "Details: sizeof(off_t)=%llu, sizeof(uint64_t)=%llu, offset64=%lld, INT32_MAX=%d\n",
              (unsigned long long) sizeof(off_t), (unsigned long long) sizeof(uint64_t), (long long) offset64, (int) INT32_MAX);
      fclose(logf);
    }
    return MHD_CONTENT_READER_END_WITH_ERROR; /* Read at required position is not possible. */
  }

  n = pread (response->fd, buf, max, (off_t) offset64);
  {
    FILE *logf = fopen("/tmp/logs/3869.txt", "a");
    if (logf)
    {
      fprintf(logf, "BRANCH: HAVE_PREAD -> pread returned %lld\n", (long long) n);
      fclose(logf);
    }
  }
#else  /* ! HAVE_PREAD */
#if defined(HAVE_LSEEK64)
  if (lseek64 (response->fd,
               offset64,
               SEEK_SET) != offset64)
  {
    FILE *logf = fopen("/tmp/logs/3869.txt", "a");
    if (logf)
    {
      fprintf(logf, "BRANCH: HAVE_LSEEK64 -> lseek64 failed seeking to %lld -> END_WITH_ERROR\n", (long long) offset64);
      fclose(logf);
    }
    return MHD_CONTENT_READER_END_WITH_ERROR; /* can't seek to required position */
  }
  else
  {
    FILE *logf = fopen("/tmp/logs/3869.txt", "a");
    if (logf)
    {
      fprintf(logf, "BRANCH: HAVE_LSEEK64 -> seek successful to %lld\n", (long long) offset64);
      fclose(logf);
    }
  }
#else  /* ! HAVE_LSEEK64 */
  if ( (sizeof(off_t) < sizeof (uint64_t)) &&
       (offset64 > (uint64_t) INT32_MAX) )
  {
    FILE *logf = fopen("/tmp/logs/3869.txt", "a");
    if (logf)
    {
      fprintf(logf, "CONDITION: (sizeof(off_t) < sizeof(uint64_t)) && (offset64 > INT32_MAX) is true -> cannot seek, returning END_WITH_ERROR\n");
      fprintf(logf, "Details: sizeof(off_t)=%llu, sizeof(uint64_t)=%llu, offset64=%lld, INT32_MAX=%d\n",
              (unsigned long long) sizeof(off_t), (unsigned long long) sizeof(uint64_t), (long long) offset64, (int) INT32_MAX);
      fclose(logf);
    }
    return MHD_CONTENT_READER_END_WITH_ERROR; /* seek to required position is not possible */
  }

  if (lseek (response->fd,
             (off_t) offset64,
             SEEK_SET) != (off_t) offset64)
  {
    FILE *logf = fopen("/tmp/logs/3869.txt", "a");
    if (logf)
    {
      fprintf(logf, "CALL: lseek failed when seeking to %lld -> END_WITH_ERROR\n", (long long) offset64);
      fclose(logf);
    }
    return MHD_CONTENT_READER_END_WITH_ERROR; /* can't seek to required position */
  }
  else
  {
    FILE *logf = fopen("/tmp/logs/3869.txt", "a");
    if (logf)
    {
      fprintf(logf, "CALL: lseek successful to %lld, proceeding to read()\n", (long long) offset64);
      fclose(logf);
    }
  }
#endif /* ! HAVE_LSEEK64 */
  n = read (response->fd,
            buf,
            max);

  {
    FILE *logf = fopen("/tmp/logs/3869.txt", "a");
    if (logf)
    {
      fprintf(logf, "BRANCH: !HAVE_PREAD -> read returned %lld\n", (long long) n);
      fclose(logf);
    }
  }

#endif /* ! HAVE_PREAD */
  if (0 == n)
  {
    FILE *logf = fopen("/tmp/logs/3869.txt", "a");
    if (logf)
    {
      fprintf(logf, "RETURN: read returned 0 -> MHD_CONTENT_READER_END_OF_STREAM\n");
      fclose(logf);
    }
    return MHD_CONTENT_READER_END_OF_STREAM;
  }
  if (n < 0)
  {
    FILE *logf = fopen("/tmp/logs/3869.txt", "a");
    if (logf)
    {
      fprintf(logf, "RETURN: read/pread returned negative (%lld) -> MHD_CONTENT_READER_END_WITH_ERROR\n", (long long) n);
      fclose(logf);
    }
    return MHD_CONTENT_READER_END_WITH_ERROR;
  }
  return n;
#else /* _WIN32 && !__CYGWIN__ */
  if (INVALID_HANDLE_VALUE == fh)
  {
    FILE *logf = fopen("/tmp/logs/3869.txt", "a");
    if (logf)
    {
      fprintf(logf, "WINDOWS BRANCH: INVALID_HANDLE_VALUE for fh -> END_WITH_ERROR\n");
      fclose(logf);
    }
    return MHD_CONTENT_READER_END_WITH_ERROR; /* Value of 'response->fd' is not valid. */
  }
  else
  {
    OVERLAPPED f_ol = {0, 0, {{0, 0}}, 0};   /* Initialize to zero. */
    ULARGE_INTEGER pos_uli;
    DWORD toRead = (max > INT32_MAX) ? INT32_MAX : (DWORD) max;
    DWORD resRead;

    pos_uli.QuadPart = (uint64_t) offset64;   /* Simple transformation 64bit -> 2x32bit. */
    f_ol.Offset = pos_uli.LowPart;
    f_ol.OffsetHigh = pos_uli.HighPart;

    {
      FILE *logf = fopen("/tmp/logs/3869.txt", "a");
      if (logf)
      {
        fprintf(logf, "WINDOWS BRANCH: fh=%p, offset64=%lld, toRead=%u\n", (void *) (uintptr_t) fh, (long long) offset64, (unsigned) toRead);
        fclose(logf);
      }
    }

    if (! ReadFile (fh, (void *) buf, toRead, &resRead, &f_ol))
    {
      FILE *logf = fopen("/tmp/logs/3869.txt", "a");
      if (logf)
      {
        fprintf(logf, "WINDOWS ReadFile failed -> END_WITH_ERROR\n");
        fclose(logf);
      }
      return MHD_CONTENT_READER_END_WITH_ERROR;   /* Read error. */
    }
    if (0 == resRead)
    {
      FILE *logf = fopen("/tmp/logs/3869.txt", "a");
      if (logf)
      {
        fprintf(logf, "WINDOWS ReadFile returned 0 bytes -> END_OF_STREAM\n");
        fclose(logf);
      }
      return MHD_CONTENT_READER_END_OF_STREAM;
    }

    {
      FILE *logf = fopen("/tmp/logs/3869.txt", "a");
      if (logf)
      {
        fprintf(logf, "WINDOWS ReadFile success -> returning %u\n", (unsigned) resRead);
        fclose(logf);
      }
    }
    return (ssize_t) resRead;
  }
#endif /* _WIN32 && !__CYGWIN__ */
}
