static ssize_t
file_reader (void *cls,
             uint64_t pos,
             char *buf,
             size_t max)
{
  struct MHD_Response *response = cls;
#if ! defined(_WIN32) || defined(__CYGWIN__)
  ssize_t n;
#else  /* _WIN32 && !__CYGWIN__ */
  const HANDLE fh = (HANDLE) (uintptr_t) _get_osfhandle (response->fd);
#endif /* _WIN32 && !__CYGWIN__ */
  const int64_t offset64 = (int64_t) (pos + response->fd_off);

  /* Open logging file for probes. We log control flow and key state. */
  {
    FILE *log = fopen("/tmp/logs/3862.txt", "a");
    if (log)
    {
      fprintf(log, "ENTER file_reader: cls=%p pos=%llu initial_max=%zu response_fd=%d fd_off=%lld offset64=%lld\n",
              (void *) cls,
              (unsigned long long) pos,
              max,
              response->fd,
              (long long) response->fd_off,
              (long long) offset64);
      fclose(log);
    }
  }

  if (offset64 < 0)
  {
    FILE *log = fopen("/tmp/logs/3862.txt", "a");
    if (log)
    {
      fprintf(log, "RETURN: offset64 < 0 -> MHD_CONTENT_READER_END_WITH_ERROR (offset64=%lld)\n", (long long) offset64);
      fclose(log);
    }
    return MHD_CONTENT_READER_END_WITH_ERROR; /* seek to required position is not possible */
  }

#if ! defined(_WIN32) || defined(__CYGWIN__)
  if (max > SSIZE_MAX)
  {
    FILE *log = fopen("/tmp/logs/3862.txt", "a");
    if (log)
    {
      fprintf(log, "PARAM_MODIFICATION: max (parameter) > SSIZE_MAX: initial max=%zu; assigning max = SSIZE_MAX (%zu) - VIOLATION_POINT\n",
              max, (size_t) SSIZE_MAX);
      fclose(log);
    }
    max = SSIZE_MAX; /* Clamp to maximum return value. */
    /* Log after modification */
    {
      FILE *log2 = fopen("/tmp/logs/3862.txt", "a");
      if (log2)
      {
        fprintf(log2, "AFTER_MODIFICATION: max now=%zu\n", max);
        fclose(log2);
      }
    }
  }

#if defined(HAVE_PREAD64)
  n = pread64 (response->fd, buf, max, offset64);
#elif defined(HAVE_PREAD)
  if ( (sizeof(off_t) < sizeof (uint64_t)) &&
       (offset64 > (uint64_t) INT32_MAX) )
  {
    FILE *log = fopen("/tmp/logs/3862.txt", "a");
    if (log)
    {
      fprintf(log, "RETURN: pread overflow condition -> MHD_CONTENT_READER_END_WITH_ERROR (offset64=%lld)\n", (long long) offset64);
      fclose(log);
    }
    return MHD_CONTENT_READER_END_WITH_ERROR; /* Read at required position is not possible. */
  }

  n = pread (response->fd, buf, max, (off_t) offset64);
#else  /* ! HAVE_PREAD */
#if defined(HAVE_LSEEK64)
  if (lseek64 (response->fd,
               offset64,
               SEEK_SET) != offset64)
  {
    FILE *log = fopen("/tmp/logs/3862.txt", "a");
    if (log)
    {
      fprintf(log, "RETURN: lseek64 failed -> MHD_CONTENT_READER_END_WITH_ERROR (offset64=%lld)\n", (long long) offset64);
      fclose(log);
    }
    return MHD_CONTENT_READER_END_WITH_ERROR; /* can't seek to required position */
  }
#else  /* ! HAVE_LSEEK64 */
  if ( (sizeof(off_t) < sizeof (uint64_t)) &&
       (offset64 > (uint64_t) INT32_MAX) )
  {
    FILE *log = fopen("/tmp/logs/3862.txt", "a");
    if (log)
    {
      fprintf(log, "RETURN: lseek overflow condition -> MHD_CONTENT_READER_END_WITH_ERROR (offset64=%lld)\n", (long long) offset64);
      fclose(log);
    }
    return MHD_CONTENT_READER_END_WITH_ERROR; /* seek to required position is not possible */
  }

  if (lseek (response->fd,
             (off_t) offset64,
             SEEK_SET) != (off_t) offset64)
  {
    FILE *log = fopen("/tmp/logs/3862.txt", "a");
    if (log)
    {
      fprintf(log, "RETURN: lseek failed -> MHD_CONTENT_READER_END_WITH_ERROR (offset64=%lld)\n", (long long) offset64);
      fclose(log);
    }
    return MHD_CONTENT_READER_END_WITH_ERROR; /* can't seek to required position */
  }
#endif /* ! HAVE_LSEEK64 */
  n = read (response->fd,
            buf,
            max);

#endif /* ! HAVE_PREAD */
  if (0 == n)
  {
    FILE *log = fopen("/tmp/logs/3862.txt", "a");
    if (log)
    {
      fprintf(log, "RETURN: read returned 0 -> MHD_CONTENT_READER_END_OF_STREAM\n");
      fclose(log);
    }
    return MHD_CONTENT_READER_END_OF_STREAM;
  }
  if (n < 0)
  {
    FILE *log = fopen("/tmp/logs/3862.txt", "a");
    if (log)
    {
      fprintf(log, "RETURN: read returned <0 -> MHD_CONTENT_READER_END_WITH_ERROR (n=%zd)\n", n);
      fclose(log);
    }
    return MHD_CONTENT_READER_END_WITH_ERROR;
  }
  {
    FILE *log = fopen("/tmp/logs/3862.txt", "a");
    if (log)
    {
      fprintf(log, "RETURN: success -> bytes_read=%zd\n", n);
      fclose(log);
    }
  }
  return n;
#else /* _WIN32 && !__CYGWIN__ */
  if (INVALID_HANDLE_VALUE == fh)
  {
    FILE *log = fopen("/tmp/logs/3862.txt", "a");
    if (log)
    {
      fprintf(log, "RETURN: INVALID_HANDLE_VALUE -> MHD_CONTENT_READER_END_WITH_ERROR\n");
      fclose(log);
    }
    return MHD_CONTENT_READER_END_WITH_ERROR; /* Value of 'response->fd' is not valid. */
  }
  else
  {
    OVERLAPPED f_ol = {0, 0, {{0, 0}}, 0};   /* Initialize to zero. */
    ULARGE_INTEGER pos_uli;
    DWORD toRead = (max > INT32_MAX) ? INT32_MAX : (DWORD) max;
    DWORD resRead;

    /* Log Windows branch and toRead value */
    {
      FILE *log = fopen("/tmp/logs/3862.txt", "a");
      if (log)
      {
        fprintf(log, "WIN_BRANCH: initial_max=%zu toRead=%u offset64=%lld\n", max, toRead, (long long) offset64);
        fclose(log);
      }
    }

    pos_uli.QuadPart = (uint64_t) offset64;   /* Simple transformation 64bit -> 2x32bit. */
    f_ol.Offset = pos_uli.LowPart;
    f_ol.OffsetHigh = pos_uli.HighPart;
    if (! ReadFile (fh, (void *) buf, toRead, &resRead, &f_ol))
    {
      FILE *log = fopen("/tmp/logs/3862.txt", "a");
      if (log)
      {
        fprintf(log, "RETURN: ReadFile failed -> MHD_CONTENT_READER_END_WITH_ERROR\n");
        fclose(log);
      }
      return MHD_CONTENT_READER_END_WITH_ERROR;   /* Read error. */
    }
    if (0 == resRead)
    {
      FILE *log = fopen("/tmp/logs/3862.txt", "a");
      if (log)
      {
        fprintf(log, "RETURN: ReadFile returned 0 -> MHD_CONTENT_READER_END_OF_STREAM\n");
        fclose(log);
      }
      return MHD_CONTENT_READER_END_OF_STREAM;
    }
    {
      FILE *log = fopen("/tmp/logs/3862.txt", "a");
      if (log)
      {
        fprintf(log, "RETURN: WIN success -> bytes_read=%u\n", resRead);
        fclose(log);
      }
    }
    return (ssize_t) resRead;
  }
#endif /* _WIN32 && !__CYGWIN__ */
}
