static ssize_t
file_reader (void *cls,
             uint64_t pos,
             char *buf,
             size_t max)
{
  struct MHD_Response *response = cls;
#if ! defined(_WIN32) || defined(__CYGWIN__)
  ssize_t n;
#else  /* _WIN32 && !__CYGWIN__ */
  const HANDLE fh = (HANDLE) (uintptr_t) _get_osfhandle (response->fd);
#endif /* _WIN32 && !__CYGWIN__ */
  const int64_t offset64 = (int64_t) (pos + response->fd_off);

  /* Log entry and initial state */
  do {
    FILE *logf = fopen("/tmp/logs/3865.txt", "a");
    if (logf)
    {
      fprintf(logf, "file_reader: entry cls=%p response=%p pos=%llu max=%zu\n",
              cls, (void *) response, (unsigned long long) pos, max);
      if (response)
      {
        /* Print fields conservatively */
        fprintf(logf, "file_reader: response->fd=%lld response->fd_off=%lld offset64=%lld\n",
                (long long) response->fd, (long long) response->fd_off, (long long) offset64);
      }
      fclose(logf);
    }
  } while (0);

  if (offset64 < 0)
  {
    do {
      FILE *logf = fopen("/tmp/logs/3865.txt", "a");
      if (logf)
      {
        fprintf(logf, "file_reader: offset64 < 0 (offset64=%lld) -> MHD_CONTENT_READER_END_WITH_ERROR\n",
                (long long) offset64);
        fclose(logf);
      }
    } while (0);
    return MHD_CONTENT_READER_END_WITH_ERROR; /* seek to required position is not possible */
  }

#if ! defined(_WIN32) || defined(__CYGWIN__)
  if (max > SSIZE_MAX)
    max = SSIZE_MAX; /* Clamp to maximum return value. */

#if defined(HAVE_PREAD64)
  n = pread64 (response->fd, buf, max, offset64);
#elif defined(HAVE_PREAD)
  if ( (sizeof(off_t) < sizeof (uint64_t)) &&
       (offset64 > (uint64_t) INT32_MAX) )
  {
    do {
      FILE *logf = fopen("/tmp/logs/3865.txt", "a");
      if (logf)
      {
        fprintf(logf, "file_reader: offset64 > INT32_MAX with limited off_t -> MHD_CONTENT_READER_END_WITH_ERROR\n");
        fclose(logf);
      }
    } while (0);
    return MHD_CONTENT_READER_END_WITH_ERROR; /* Read at required position is not possible. */
  }

  n = pread (response->fd, buf, max, (off_t) offset64);
#else  /* ! HAVE_PREAD */
#if defined(HAVE_LSEEK64)
  if (lseek64 (response->fd,
               offset64,
               SEEK_SET) != offset64)
  {
    do {
      FILE *logf = fopen("/tmp/logs/3865.txt", "a");
      if (logf)
      {
        fprintf(logf, "file_reader: lseek64 failed for fd=%lld offset64=%lld -> MHD_CONTENT_READER_END_WITH_ERROR\n",
                (long long) response->fd, (long long) offset64);
        fclose(logf);
      }
    } while (0);
    return MHD_CONTENT_READER_END_WITH_ERROR; /* can't seek to required position */
  }
#else  /* ! HAVE_LSEEK64 */
  if ( (sizeof(off_t) < sizeof (uint64_t)) &&
       (offset64 > (uint64_t) INT32_MAX) )
  {
    do {
      FILE *logf = fopen("/tmp/logs/3865.txt", "a");
      if (logf)
      {
        fprintf(logf, "file_reader: offset64 > INT32_MAX with limited off_t (seek) -> MHD_CONTENT_READER_END_WITH_ERROR\n");
        fclose(logf);
      }
    } while (0);
    return MHD_CONTENT_READER_END_WITH_ERROR; /* seek to required position is not possible */
  }

  if (lseek (response->fd,
             (off_t) offset64,
             SEEK_SET) != (off_t) offset64)
  {
    do {
      FILE *logf = fopen("/tmp/logs/3865.txt", "a");
      if (logf)
      {
        fprintf(logf, "file_reader: lseek failed for fd=%lld offset64=%lld -> MHD_CONTENT_READER_END_WITH_ERROR\n",
                (long long) response->fd, (long long) offset64);
        fclose(logf);
      }
    } while (0);
    return MHD_CONTENT_READER_END_WITH_ERROR; /* can't seek to required position */
  }
#endif /* ! HAVE_LSEEK64 */
  n = read (response->fd,
            buf,
            max);

#endif /* ! HAVE_PREAD */
  if (0 == n)
  {
    do {
      FILE *logf = fopen("/tmp/logs/3865.txt", "a");
      if (logf)
      {
        fprintf(logf, "file_reader: read returned 0 -> MHD_CONTENT_READER_END_OF_STREAM\n");
        fclose(logf);
      }
    } while (0);
    return MHD_CONTENT_READER_END_OF_STREAM;
  }
  if (n < 0)
  {
    do {
      FILE *logf = fopen("/tmp/logs/3865.txt", "a");
      if (logf)
      {
        fprintf(logf, "file_reader: read/pread returned n=%zd -> MHD_CONTENT_READER_END_WITH_ERROR\n", n);
        fclose(logf);
      }
    } while (0);
    return MHD_CONTENT_READER_END_WITH_ERROR;
  }
  do {
    FILE *logf = fopen("/tmp/logs/3865.txt", "a");
    if (logf)
    {
      fprintf(logf, "file_reader: successful read n=%zd\n", n);
      fclose(logf);
    }
  } while (0);
  return n;
#else /* _WIN32 && !__CYGWIN__ */
  if (INVALID_HANDLE_VALUE == fh)
  {
    do {
      FILE *logf = fopen("/tmp/logs/3865.txt", "a");
      if (logf)
      {
        fprintf(logf, "file_reader: INVALID_HANDLE_VALUE (fh=%p) -> MHD_CONTENT_READER_END_WITH_ERROR\n", (void *) fh);
        fclose(logf);
      }
    } while (0);
    return MHD_CONTENT_READER_END_WITH_ERROR; /* Value of 'response->fd' is not valid. */
  }
  else
  {
    OVERLAPPED f_ol = {0, 0, {{0, 0}}, 0};   /* Initialize to zero. */
    ULARGE_INTEGER pos_uli;
    DWORD toRead = (max > INT32_MAX) ? INT32_MAX : (DWORD) max;
    DWORD resRead;

    pos_uli.QuadPart = (uint64_t) offset64;   /* Simple transformation 64bit -> 2x32bit. */
    f_ol.Offset = pos_uli.LowPart;
    f_ol.OffsetHigh = pos_uli.HighPart;
    if (! ReadFile (fh, (void *) buf, toRead, &resRead, &f_ol))
    {
      do {
        FILE *logf = fopen("/tmp/logs/3865.txt", "a");
        if (logf)
        {
          fprintf(logf, "file_reader: ReadFile failed fh=%p toRead=%u offset64=%lld -> MHD_CONTENT_READER_END_WITH_ERROR\n",
                  (void *) fh, (unsigned) toRead, (long long) offset64);
          fclose(logf);
        }
      } while (0);
      return MHD_CONTENT_READER_END_WITH_ERROR;   /* Read error. */
    }
    if (0 == resRead)
    {
      do {
        FILE *logf = fopen("/tmp/logs/3865.txt", "a");
        if (logf)
        {
          fprintf(logf, "file_reader: ReadFile returned 0 -> MHD_CONTENT_READER_END_OF_STREAM\n");
          fclose(logf);
        }
      } while (0);
      return MHD_CONTENT_READER_END_OF_STREAM;
    }
    do {
      FILE *logf = fopen("/tmp/logs/3865.txt", "a");
      if (logf)
      {
        fprintf(logf, "file_reader: ReadFile succeeded resRead=%u -> returning %zd\n", resRead, (ssize_t) resRead);
        fclose(logf);
      }
    } while (0);
    return (ssize_t) resRead;
  }
#endif /* _WIN32 && !__CYGWIN__ */
}
