static bool
find_auth_rq_header_ (const struct MHD_Connection *c, enum MHD_AuthType type,
                      struct _MHD_str_w_len *auth_value)
{
  const struct MHD_HTTP_Req_Header *h;
  const char *token;
  size_t token_len;
  FILE *logf = fopen("/tmp/logs/348.txt", "a");

  if (logf)
  {
    fprintf(logf, "find_auth_rq_header_: entered; conn=%p, state=%d, required_state=%d\n",
            (void *)c, (int)c->state, (int)MHD_CONNECTION_HEADERS_PROCESSED);
    /* log the evaluated assertion expression value */
    fprintf(logf, "find_auth_rq_header_: assertion expr (MHD_CONNECTION_HEADERS_PROCESSED <= c->state) == %d\n",
            (int)(MHD_CONNECTION_HEADERS_PROCESSED <= c->state));
  }

  mhd_assert (MHD_CONNECTION_HEADERS_PROCESSED <= c->state);
  if (MHD_CONNECTION_HEADERS_PROCESSED > c->state)
  {
    if (logf)
    {
      fprintf(logf, "find_auth_rq_header_: early return: headers not processed (state=%d)\n",
              (int)c->state);
      fclose(logf);
    }
    return false;
  }

#ifdef DAUTH_SUPPORT
  if (MHD_AUTHTYPE_DIGEST == type)
  {
    token = _MHD_AUTH_DIGEST_BASE;
    token_len = MHD_STATICSTR_LEN_ (_MHD_AUTH_DIGEST_BASE);
    if (logf)
      fprintf(logf, "find_auth_rq_header_: auth type=DIGEST token=%p token_len=%zu\n",
              (void *)token, token_len);
  }
  else /* combined with the next line */
#endif /* DAUTH_SUPPORT */
#ifdef BAUTH_SUPPORT
  if (MHD_AUTHTYPE_BASIC == type)
  {
    token = _MHD_AUTH_BASIC_BASE;
    token_len = MHD_STATICSTR_LEN_ (_MHD_AUTH_BASIC_BASE);
    if (logf)
      fprintf(logf, "find_auth_rq_header_: auth type=BASIC token=%p token_len=%zu\n",
              (void *)token, token_len);
  }
  else /* combined with the next line */
#endif /* BAUTH_SUPPORT */
  {
    if (logf)
    {
      fprintf(logf, "find_auth_rq_header_: unknown auth type=%d; asserting and returning false\n",
              (int)type);
      fclose(logf);
    }
    mhd_assert (0);
    return false;
  }

  if (logf)
    fprintf(logf, "find_auth_rq_header_: scanning headers (start)\n");

  for (h = c->rq.headers_received; NULL != h; h = h->next)
  {
    if (logf)
      fprintf(logf, "find_auth_rq_header_: header iter h=%p kind=%d header_size=%zu value_size=%zu header=%p value=%p\n",
              (void *)h, (int)h->kind, h->header_size, h->value_size,
              (void *)h->header, (void *)h->value);

    if (MHD_HEADER_KIND != h->kind)
      continue;
    if (MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_AUTHORIZATION) != h->header_size)
      continue;
    if (token_len > h->value_size)
      continue;
    if (! MHD_str_equal_caseless_bin_n_ (MHD_HTTP_HEADER_AUTHORIZATION,
                                         h->header,
                                         MHD_STATICSTR_LEN_ ( \
                                           MHD_HTTP_HEADER_AUTHORIZATION)))
      continue;
    if (! MHD_str_equal_caseless_bin_n_ (h->value, token, token_len))
      continue;
    /* Match only if token string is full header value or token is
     * followed by space or tab
     * Note: RFC 9110 (and RFC 7234) allows only space character, but
     * tab is supported here as well for additional flexibility and uniformity
     * as tabs are supported as separators between parameters.
     */
    if ((token_len == h->value_size) ||
        (' ' == h->value[token_len]) || ('\t'  == h->value[token_len]))
    {
      if (token_len != h->value_size)
      { /* Skip whitespace */
        auth_value->str = h->value + token_len + 1;
        auth_value->len = h->value_size - (token_len + 1);
      }
      else
      { /* No whitespace to skip */
        auth_value->str = h->value + token_len;
        auth_value->len = h->value_size - token_len;
      }
      if (logf)
      {
        fprintf(logf, "find_auth_rq_header_: match found h=%p auth_value.str=%p auth_value.len=%zu; returning true\n",
                (void *)h, (void *)auth_value->str, auth_value->len);
        fclose(logf);
      }
      return true; /* Found a match */
    }
  }

  if (logf)
  {
    fprintf(logf, "find_auth_rq_header_: no matching header found; returning false\n");
    fclose(logf);
  }
  return false; /* No matching header has been found */
}
