static bool
find_auth_rq_header_ (const struct MHD_Connection *c, enum MHD_AuthType type,
                      struct _MHD_str_w_len *auth_value)
{
  const struct MHD_HTTP_Req_Header *h;
  const char *token;
  size_t token_len;
  FILE *log = fopen("/tmp/logs/343.txt", "a");

  if (log)
  {
    fprintf(log, "[find_auth_rq_header_] ENTER: c=%p, type=%d, auth_value=%p, c->state=%d\n",
            (void *)c, (int)type, (void *)auth_value, (int)c->state);
    fflush(log);
  }

  mhd_assert (MHD_CONNECTION_HEADERS_PROCESSED <= c->state);
  if (MHD_CONNECTION_HEADERS_PROCESSED > c->state)
  {
    if (log)
    {
      fprintf(log, "[find_auth_rq_header_] early return: headers not processed (state=%d)\n",
              (int)c->state);
      fclose(log);
    }
    return false;
  }

#ifdef DAUTH_SUPPORT
  if (MHD_AUTHTYPE_DIGEST == type)
  {
    token = _MHD_AUTH_DIGEST_BASE;
    token_len = MHD_STATICSTR_LEN_ (_MHD_AUTH_DIGEST_BASE);
  }
  else /* combined with the next line */
#endif /* DAUTH_SUPPORT */
#ifdef BAUTH_SUPPORT
  if (MHD_AUTHTYPE_BASIC == type)
  {
    token = _MHD_AUTH_BASIC_BASE;
    token_len = MHD_STATICSTR_LEN_ (_MHD_AUTH_BASIC_BASE);
  }
  else /* combined with the next line */
#endif /* BAUTH_SUPPORT */
  {
    if (log)
    {
      fprintf(log, "[find_auth_rq_header_] unknown auth type: %d\n", (int)type);
      fclose(log);
    }
    mhd_assert (0);
    return false;
  }

  if (log)
  {
    fprintf(log, "[find_auth_rq_header_] token=%p, token_len=%zu\n", (void *)token, token_len);
    fflush(log);
  }

  for (h = c->rq.headers_received; NULL != h; h = h->next)
  {
    if (log)
    {
      fprintf(log, "[find_auth_rq_header_] examining header h=%p: kind=%d, header_size=%zu, value_size=%zu, header=%p, value=%p\n",
              (void *)h, (int)h->kind, h->header_size, h->value_size,
              (void *)h->header, (void *)h->value);
      fflush(log);
    }

    if (MHD_HEADER_KIND != h->kind)
    {
      if (log)
      {
        fprintf(log, "[find_auth_rq_header_] skip: not an HTTP header (kind=%d)\n", (int)h->kind);
        fflush(log);
      }
      continue;
    }
    if (MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_AUTHORIZATION) != h->header_size)
    {
      if (log)
      {
        fprintf(log, "[find_auth_rq_header_] skip: header_size mismatch (expected=%zu, got=%zu)\n",
                MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_AUTHORIZATION), h->header_size);
        fflush(log);
      }
      continue;
    }
    if (token_len > h->value_size)
    {
      if (log)
      {
        fprintf(log, "[find_auth_rq_header_] skip: token_len (%zu) > value_size (%zu)\n",
                token_len, h->value_size);
        fflush(log);
      }
      continue;
    }
    if (! MHD_str_equal_caseless_bin_n_ (MHD_HTTP_HEADER_AUTHORIZATION,
                                         h->header,
                                         MHD_STATICSTR_LEN_ ( \
                                           MHD_HTTP_HEADER_AUTHORIZATION)))
    {
      if (log)
      {
        fprintf(log, "[find_auth_rq_header_] skip: header name mismatch\n");
        fflush(log);
      }
      continue;
    }
    if (! MHD_str_equal_caseless_bin_n_ (h->value, token, token_len))
    {
      if (log)
      {
        fprintf(log, "[find_auth_rq_header_] skip: token prefix mismatch (token_len=%zu)\n", token_len);
        fflush(log);
      }
      continue;
    }

    /* Match only if token string is full header value or token is
     * followed by space or tab
     * Note: RFC 9110 (and RFC 7234) allows only space character, but
     * tab is supported here as well for additional flexibility and uniformity
     * as tabs are supported as separators between parameters.
     */
    /* To avoid accidental out-of-bounds reads during logging, guard any
     * indexing of h->value by checking token_len < h->value_size. The
     * original conditional relied on short-circuit semantics; the guarded
     * checks below are equivalent given the earlier token_len > h->value_size
     * continue. */
    int has_separator = 0;
    int sep_char = -1;
    if (token_len == h->value_size)
    {
      has_separator = 1; /* token occupies entire value */
    }
    else if (token_len < h->value_size)
    {
      sep_char = (int)(unsigned char)h->value[token_len];
      if (' ' == h->value[token_len] || '\t' == h->value[token_len])
        has_separator = 1;
    }

    if (log)
    {
      if (token_len < h->value_size)
        fprintf(log, "[find_auth_rq_header_] separator-check: token_len=%zu, value_size=%zu, char_at_token_idx=%d\n",
                token_len, h->value_size, sep_char);
      else
        fprintf(log, "[find_auth_rq_header_] separator-check: token_len=%zu, value_size=%zu (exact match)\n",
                token_len, h->value_size);
      fflush(log);
    }

    if (has_separator)
    {
      if (token_len != h->value_size)
      { /* Skip whitespace */
        const char *new_str = h->value + token_len + 1;
        size_t new_len = h->value_size - (token_len + 1);

        if (log)
        {
          fprintf(log, "[find_auth_rq_header_] assigning auth_value (skip ws): token_len=%zu, value_size=%zu, new_str=%p, new_len=%zu\n",
                  token_len, h->value_size, (void *)new_str, new_len);
          /* Detect the specific edge-case described: pointer one past end with zero length */
          if ((token_len + 1u) == h->value_size && new_len == 0u)
          {
            fprintf(log, "[find_auth_rq_header_] WARNING: new_str points one-past-end of h->value: h->value=%p, h->value_size=%zu, token_len=%zu -> new_str=%p, new_len=0\n",
                    (void *)h->value, h->value_size, token_len, (void *)new_str);
          }
          fflush(log);
        }

        auth_value->str = new_str;
        auth_value->len = new_len;
      }
      else
      { /* No whitespace to skip */
        const char *new_str = h->value + token_len;
        size_t new_len = h->value_size - token_len;

        if (log)
        {
          fprintf(log, "[find_auth_rq_header_] assigning auth_value (no ws): token_len=%zu, value_size=%zu, new_str=%p, new_len=%zu\n",
                  token_len, h->value_size, (void *)new_str, new_len);
          fflush(log);
        }

        auth_value->str = new_str;
        auth_value->len = new_len;
      }

      if (log)
      {
        fprintf(log, "[find_auth_rq_header_] FOUND match -> return true\n");
        fclose(log);
      }
      return true; /* Found a match */
    }
    else
    {
      if (log)
      {
        fprintf(log, "[find_auth_rq_header_] separator not found after token -> continue searching\n");
        fflush(log);
      }
    }
  }

  if (log)
  {
    fprintf(log, "[find_auth_rq_header_] No matching header found -> return false\n");
    fclose(log);
  }
  return false; /* No matching header has been found */
}
