static bool
find_auth_rq_header_ (const struct MHD_Connection *c, enum MHD_AuthType type,
                      struct _MHD_str_w_len *auth_value)
{
  const struct MHD_HTTP_Req_Header *h;
  const char *token;
  size_t token_len;

  FILE *log = fopen("/tmp/logs/345.txt", "a");
  if (log)
  {
    fprintf(log, "ENTER find_auth_rq_header_: c=%p type=%d auth_value=%p\n",
            (const void *)c, (int)type, (const void *)auth_value);
  }

  mhd_assert (MHD_CONNECTION_HEADERS_PROCESSED <= c->state);
  if (MHD_CONNECTION_HEADERS_PROCESSED > c->state)
  {
    if (log)
      fprintf(log, "EARLY RETURN: headers not processed (c->state=%d)\n", c->state);
    if (log) fclose(log);
    return false;
  }

#ifdef DAUTH_SUPPORT
  if (MHD_AUTHTYPE_DIGEST == type)
  {
    token = _MHD_AUTH_DIGEST_BASE;
    token_len = MHD_STATICSTR_LEN_ (_MHD_AUTH_DIGEST_BASE);
    if (log)
      fprintf(log, "Selected token: DIGEST base=%p len=%zu\n",
              (const void *)token, token_len);
  }
  else /* combined with the next line */
#endif /* DAUTH_SUPPORT */
#ifdef BAUTH_SUPPORT
  if (MHD_AUTHTYPE_BASIC == type)
  {
    token = _MHD_AUTH_BASIC_BASE;
    token_len = MHD_STATICSTR_LEN_ (_MHD_AUTH_BASIC_BASE);
    if (log)
      fprintf(log, "Selected token: BASIC base=%p len=%zu\n",
              (const void *)token, token_len);
  }
  else /* combined with the next line */
#endif /* BAUTH_SUPPORT */
  {
    mhd_assert (0);
    if (log)
      fprintf(log, "Unsupported auth type: %d -- asserting and returning false\n", (int)type);
    if (log) fclose(log);
    return false;
  }

  for (h = c->rq.headers_received; NULL != h; h = h->next)
  {
    if (log)
    {
      fprintf(log, "ITER header: h=%p kind=%d header_size=%zu value_size=%zu header=%p value=%p\n",
              (const void *)h, (int)h->kind, h->header_size, h->value_size,
              (const void *)h->header, (const void *)h->value);
    }

    if (MHD_HEADER_KIND != h->kind)
      continue;
    if (MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_AUTHORIZATION) != h->header_size)
      continue;
    if (token_len > h->value_size)
      continue;
    if (! MHD_str_equal_caseless_bin_n_ (MHD_HTTP_HEADER_AUTHORIZATION,
                                         h->header,
                                         MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_AUTHORIZATION)))
      continue;
    if (! MHD_str_equal_caseless_bin_n_ (h->value, token, token_len))
      continue;
    /* Match only if token string is full header value or token is
     * followed by space or tab
     * Note: RFC 9110 (and RFC 7234) allows only space character, but
     * tab is supported here as well for additional flexibility and uniformity
     * as tabs are supported as separators between parameters.
     */
    if ((token_len == h->value_size) ||
        (' ' == h->value[token_len]) || ('\t'  == h->value[token_len]))
    {
      if (log)
      {
        /* log the relevant bytes around the token boundary */
        size_t before = token_len;
        size_t avail = h->value_size;
        size_t show = (avail < (before + 32)) ? avail : (before + 32);
        fprintf(log, "MATCH candidate at h=%p token_len=%zu value_size=%zu show=%zu\n",
                (const void *)h, token_len, h->value_size, show);
        if (show > 0)
          fprintf(log, "value prefix (up to show bytes): '%.*s'\n", (int)show, h->value);
        if (before < avail)
          fprintf(log, "char at token boundary: index=%zu char='%c' (0x%02x)\n",
                  before,
                  (h->value[before] >= ' ' && h->value[before] <= '~') ? h->value[before] : '?',
                  (unsigned char)h->value[before]);
      }

      if (token_len != h->value_size)
      { /* Skip whitespace */
        /* compute assignment target for logging but perform real assignment as before */
        const char *assigned = h->value + token_len + 1;
        size_t assigned_len = h->value_size - (token_len + 1);
        if (log)
        {
          fprintf(log, "ASSIGN (skip ws): auth_value=%p assigned_ptr=%p assigned_len=%zu\n",
                  (const void *)auth_value, (const void *)assigned, assigned_len);
          if (assigned_len > 0)
            fprintf(log, "assigned snippet: '%.*s'\n", (int)((assigned_len < 32) ? assigned_len : 32), assigned);
        }
        if (auth_value)
        {
          auth_value->str = (char *) (h->value + token_len + 1);
          auth_value->len = assigned_len;
        }
        if (log) fclose(log);
        return true; /* Found a match */
      }
      else
      { /* No whitespace to skip */
        const char *assigned = h->value + token_len;
        size_t assigned_len = h->value_size - token_len;
        if (log)
        {
          fprintf(log, "ASSIGN (no ws): auth_value=%p assigned_ptr=%p assigned_len=%zu\n",
                  (const void *)auth_value, (const void *)assigned, assigned_len);
          if (assigned_len > 0)
            fprintf(log, "assigned snippet: '%.*s'\n", (int)((assigned_len < 32) ? assigned_len : 32), assigned);
        }
        if (auth_value)
        {
          auth_value->str = (char *) (h->value + token_len);
          auth_value->len = assigned_len;
        }
        if (log) fclose(log);
        return true; /* Found a match */
      }
    }
  }

  if (log)
  {
    fprintf(log, "NO MATCH: no matching header found\n");
    fclose(log);
  }
  return false; /* No matching header has been found */
}
