static int
find_boundary (struct MHD_PostProcessor *pp,
               const char *boundary,
               size_t blen,
               size_t *ioffptr,
               enum PP_State next_state,
               enum PP_State next_dash_state)
{
  FILE *log = fopen("/tmp/logs/3435.txt", "a");
  if (log)
  {
    fprintf(log, "ENTER find_boundary: pp=%p boundary=%p blen=%zu ioffptr=%p ioff=*%zu next_state=%d next_dash_state=%d\n",
            (void *)pp, (const void *)boundary, blen, (void *)ioffptr, (ioffptr ? *ioffptr : (size_t)0), (int)next_state, (int)next_dash_state);
  }

  char *buf = (char *) &pp[1];
  const char *dash;

  if (log)
  {
    fprintf(log, "buf=%p pp->buffer_pos=%zu pp->buffer_size=%zu\n", (void *)buf, pp->buffer_pos, pp->buffer_size);
    /* print first up to 64 bytes of the buffer for diagnostics */
    size_t toprint = pp->buffer_pos < 64 ? pp->buffer_pos : 64;
    fprintf(log, "buf bytes (first %zu):", toprint);
    for (size_t i = 0; i < toprint; ++i)
      fprintf(log, " %02x", (unsigned char) buf[i]);
    fprintf(log, "\n");
  }

  if (pp->buffer_pos < 2 + blen)
  {
    if (pp->buffer_pos == pp->buffer_size)
    {
      pp->state = PP_Error;     /* out of memory */
      if (log) fprintf(log, "Not enough data and buffer full -> set pp->state=PP_Error\n");
    }
    if (log) fprintf(log, "Not enough data: need %zu, have %zu -> return MHD_NO\n", 2 + blen, pp->buffer_pos);
    if (log) fclose(log);
    /* ++(*ioffptr); */
    return MHD_NO;              /* not enough data */
  }

  /* compute memcmp results separately so we can log them */
  int r1 = memcmp("--",
                  buf,
                  2);
  int r2 = memcmp(&buf[2],
                  boundary,
                  blen);
  if (log) fprintf(log, "memcmp results: r1=%d (" "--" "), r2=%d (boundary)\n", r1, r2);

  if ( (0 != r1) ||
       (0 != r2))
  {
    if (pp->state != PP_Init)
    {
      /* garbage not allowed */
      pp->state = PP_Error;
      if (log) fprintf(log, "Boundary mismatch and pp->state != PP_Init -> set pp->state=PP_Error\n");
    }
    else
    {
      /* skip over garbage (RFC 2046, 5.1.1) */
      dash = memchr (buf,
                     '-',
                     pp->buffer_pos);
      if (log) fprintf(log, "pp->state==PP_Init; memchr returned %p\n", (const void *)dash);
      if (NULL == dash)
      {
        (*ioffptr) += pp->buffer_pos;         /* skip entire buffer */
        if (log) fprintf(log, "No dash found -> skip entire buffer, *ioffptr now=%zu\n", *ioffptr);
      }
      else if (dash == buf)
      {
        (*ioffptr)++;                         /* at least skip one byte */
        if (log) fprintf(log, "Dash at buf -> increment *ioffptr to %zu\n", *ioffptr);
      }
      else
      {
        (*ioffptr) += (size_t) (dash - buf);  /* skip to first possible boundary */
        if (log) fprintf(log, "Dash at offset %td -> *ioffptr now=%zu\n", (ptrdiff_t)(dash - buf), *ioffptr);
      }
    }
    if (log) fclose(log);
    return MHD_NO;                            /* expected boundary */
  }
  /* remove boundary from buffer */
  (*ioffptr) += 2 + blen;
  if (log) fprintf(log, "Boundary matched -> advance *ioffptr by %zu -> now=%zu\n", 2 + blen, *ioffptr);
  /* next: start with headers */
  pp->skip_rn = RN_Dash;
  pp->state = next_state;
  pp->dash_state = next_dash_state;
  if (log) fprintf(log, "Set pp->skip_rn=%d pp->state=%d pp->dash_state=%d -> return MHD_YES\n",
                   (int) pp->skip_rn, (int) pp->state, (int) pp->dash_state);
  if (log) fclose(log);
  return MHD_YES;
}
