_MHD_static_inline enum MHD_DigestBaseAlgo
get_base_digest_algo (enum MHD_DigestAuthAlgo3 algo3)
{
  unsigned int base_algo;

  /* Preserve original computation (intentionally includes the redundant OR)
     so behaviour stays identical and the reported detection can be reproduced. */
  base_algo =
    ((unsigned int) algo3)
    & ~((unsigned int)
        (MHD_DIGEST_AUTH_ALGO3_NON_SESSION
         | MHD_DIGEST_AUTH_ALGO3_NON_SESSION));

  /* Logging probe: append control-flow and state information to the log file.
     This records the input value, the computed mask and the resulting base_algo.
     The log path is /tmp/logs/4478.txt as requested. If the directory does not
     exist, fopen will fail silently and the function behaviour is unchanged. */
  {
    FILE *logf = fopen("/tmp/logs/4478.txt", "a");
    if (logf != NULL)
    {
      unsigned int in_val = (unsigned int) algo3;
      unsigned int mask = ~((unsigned int)(MHD_DIGEST_AUTH_ALGO3_NON_SESSION | MHD_DIGEST_AUTH_ALGO3_NON_SESSION));
      fprintf(logf, "get_base_digest_algo: entered\n");
      fprintf(logf, "  input algo3 = %u (0x%X)\n", in_val, in_val);
      fprintf(logf, "  mask = ~(MHD_DIGEST_AUTH_ALGO3_NON_SESSION|MHD_DIGEST_AUTH_ALGO3_NON_SESSION) = 0x%X\n", mask);
      fprintf(logf, "  base_algo (result) = %u (0x%X)\n", base_algo, base_algo);
      fclose(logf);
    }
  }

  return (enum MHD_DigestBaseAlgo) base_algo;
}
