static bool
get_date_header (char *header)
{
  /* Open log file for append. If logging fails we proceed silently to preserve original behavior. */
  FILE *log = fopen("/tmp/logs/1703.txt", "a");
  if (log)
  {
    fprintf(log, "ENTER get_date_header: header=%p\n", (void *) header);
    fprintf(log, "Plan: call get_date_str(header+6) -> ptr=%p\n", (void *) (header + 6));
  }

  /* Preserve original logic: call get_date_str with header + 6. */
  bool ok = get_date_str (header + 6);

  if (log)
  {
    fprintf(log, "get_date_str returned: %d\n", ok ? 1 : 0);
  }

  if (!ok)
  {
    if (log)
    {
      fprintf(log, "get_date_str failed: setting header[0]=0 and returning false\n");
      fclose(log);
    }
    header[0] = 0;
    return false;
  }

  /* Log the addresses that will be written to. This helps reproduce a buffer-size mismatch
     without reading memory beyond the provided object. */
  if (log)
  {
    fprintf(log, "About to write header[0..5] at %p..%p\n", (void *) header, (void *) (header + 5));
    fprintf(log, "About to write header[35..37] (CR LF NUL) at %p..%p\n", (void *) (header + 35), (void *) (header + 37));
  }

  /* Original writes: keep exact behavior so test outcomes remain unchanged. */
  header[0] = 'D';
  header[1] = 'a';
  header[2] = 't';
  header[3] = 'e';
  header[4] = ':';
  header[5] = ' ';
  header[35] = '\r';
  header[36] = '\n';
  header[37] = 0;

  if (log)
  {
    fprintf(log, "Wrote header bytes and returning true\n");
    fclose(log);
  }

  return true;
}
