static unsigned int
get_no_space_err_status_code (struct MHD_Connection *c,
                              enum MHD_ProcRecvDataStage stage,
                              const char *add_element,
                              size_t add_element_size)
{
  size_t method_size;
  size_t uri_size;
  size_t opt_headers_size;
  size_t host_field_line_size;
  FILE *log = fopen("/tmp/logs/2223.txt", "a");

  if (log)
  {
    fprintf(log, "ENTER get_no_space_err_status_code: c=%p state=%d stage=%d add_element=%p add_element_size=%zu\n",
            (void *)c,
            (int)c->state,
            (int)stage,
            (const void *)add_element,
            add_element_size);
    fflush(log);
  }

  mhd_assert (MHD_CONNECTION_REQ_LINE_RECEIVED < c->state);
  mhd_assert (MHD_PROC_RECV_HEADERS <= stage);
  mhd_assert ((0 == add_element_size) || (NULL != add_element));

  if (MHD_CONNECTION_HEADERS_RECEIVED > c->state)
  {
    mhd_assert (NULL != c->rq.field_lines.start);
    opt_headers_size =
      (size_t) ((c->read_buffer + c->read_buffer_offset)
                - c->rq.field_lines.start);
  }
  else
    opt_headers_size = c->rq.field_lines.size;

  if (log)
  {
    fprintf(log, "after headers: read_buffer=%p read_buffer_offset=%d field_lines.start=%p field_lines.size=%zu opt_headers_size=%zu\n",
            (void *)c->read_buffer,
            (int)c->read_buffer_offset,
            (void *)c->rq.field_lines.start,
            c->rq.field_lines.size,
            opt_headers_size);
    fflush(log);
  }

  /* The read buffer is fully used by the request line, the field lines
     (headers) and internal information.
     The return status code works as a suggestion for the client to reduce
     one of the request elements. */

  if ((MHD_PROC_RECV_BODY_CHUNKED == stage) &&
      (MHD_MIN_REASONABLE_REQ_CHUNK_LINE_LENGTH_ < add_element_size))
  {
    if (log)
    {
      fprintf(log, "decision: chunked + add_element_size > threshold -> CONTENT_TOO_LARGE\n");
      fclose(log);
    }
    return MHD_HTTP_CONTENT_TOO_LARGE;
  }

  host_field_line_size = 0;
  if ((MHD_PROC_RECV_HEADERS == stage)
      && (0 != add_element_size))
  {
    static const size_t header_host_key_len =
      MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_HOST);
    const bool is_host_header =
      (header_host_key_len + 1 <= add_element_size)
      && ( (0 == add_element[header_host_key_len])
           || (':' == add_element[header_host_key_len]) )
      && MHD_str_equal_caseless_bin_n_ (MHD_HTTP_HEADER_HOST,
                                        add_element,
                                        header_host_key_len);
    if (is_host_header)
    {
      const bool is_parsed = ! (
        (MHD_CONNECTION_HEADERS_RECEIVED > c->state) &&
        (add_element_size == c->read_buffer_offset) &&
        (c->read_buffer == add_element) );
      size_t actual_element_size;

      mhd_assert (! is_parsed || (0 == add_element[header_host_key_len]));
      if (is_parsed)
        actual_element_size = add_element_size + 1;  /* "1" for LF */
      else
        actual_element_size = add_element_size;

      host_field_line_size = actual_element_size;
      mhd_assert (opt_headers_size >= actual_element_size);
      opt_headers_size -= actual_element_size;

      if (log)
      {
        fprintf(log, "host header found: header_host_key_len=%zu is_parsed=%d actual_element_size=%zu host_field_line_size=%zu opt_headers_size=%zu\n",
                header_host_key_len,
                (int)is_parsed,
                actual_element_size,
                host_field_line_size,
                opt_headers_size);
        fflush(log);
      }
    }
  }
  if (0 == host_field_line_size)
  {
    static const size_t host_field_name_len =
      MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_HOST);
    size_t host_field_name_value_len;
    if (MHD_NO != MHD_lookup_connection_value_n (c,
                                                 MHD_HEADER_KIND,
                                                 MHD_HTTP_HEADER_HOST,
                                                 host_field_name_len,
                                                 NULL,
                                                 &host_field_name_value_len))
    {
      host_field_line_size =
        host_field_name_len + host_field_name_value_len + 2; /* "2" for ':' and LF */

      if (opt_headers_size >= host_field_line_size)
      {
        opt_headers_size -= host_field_line_size;
        if (opt_headers_size >= 2)
          opt_headers_size -= 2;
      }
      else
        host_field_line_size = 0; /* No "Host:" field line set by the client */

      if (log)
      {
        fprintf(log, "lookup_host: host_field_name_value_len=%zu host_field_line_size=%zu opt_headers_size=%zu\n",
                host_field_name_value_len,
                host_field_line_size,
                opt_headers_size);
        fflush(log);
      }
    }
  }

  uri_size = c->rq.req_target_len;
  if (MHD_HTTP_MTHD_OTHER != c->rq.http_mthd)
    method_size = 0; /* Do not recommend shorter request method */
  else
  {
    mhd_assert (NULL != c->rq.method);

    if (log)
    {
      fprintf(log, "about to compute method length: rq.method=%p rq.http_mthd=%d\n",
              (const void *)c->rq.method,
              (int)c->rq.http_mthd);
      fflush(log);
    }

    method_size = strlen (c->rq.method);

    if (log)
    {
      /* Log the computed method_size and a short hexdump of up to 32 bytes
         starting at c->rq.method. This is best-effort and may read memory
         beyond intended method if non-terminated; the primary evidence is
         the pre-strlen log above. We limit to 32 bytes to reduce noise. */
      size_t i, dump_len = (method_size < 32) ? method_size : 32;
      fprintf(log, "method_size=%zu dump_len=%zu method_ptr=%p\n",
              method_size,
              dump_len,
              (const void *)c->rq.method);
      for (i = 0; i < dump_len; ++i)
      {
        unsigned char ch = ((const unsigned char *)c->rq.method)[i];
        fprintf(log, "%02x", ch);
        if ((i + 1) % 32 == 0)
          fprintf(log, "\n");
      }
      fprintf(log, "\n");
      fflush(log);
    }
  }

  if ((size_t) MHD_MAX_REASONABLE_HEADERS_SIZE_ < opt_headers_size)
  {
    if (log)
    {
      fprintf(log, "branch: opt_headers_size > MHD_MAX_REASONABLE_HEADERS_SIZE_ (%zu)\n", opt_headers_size);
      fflush(log);
    }
    if (opt_headers_size > (uri_size / 8))
    {
      if ((opt_headers_size / 2) > method_size)
      {
        if (log)
        {
          fprintf(log, "return: REQUEST_HEADER_FIELDS_TOO_LARGE opt_headers_size=%zu method_size=%zu uri_size=%zu\n",
                  opt_headers_size, method_size, uri_size);
          fclose(log);
        }
        return MHD_HTTP_REQUEST_HEADER_FIELDS_TOO_LARGE;
      }
      else
      {
        if (log)
        {
          fprintf(log, "return: NOT_IMPLEMENTED (method too large) opt_headers_size=%zu method_size=%zu\n",
                  opt_headers_size, method_size);
          fclose(log);
        }
        return MHD_HTTP_NOT_IMPLEMENTED; /* The length of the HTTP request method is unreasonably large */
      }
    }
    else
    { /* Request target is MUCH larger than headers */
      if ((uri_size / 16) > method_size)
      {
        if (log)
        {
          fprintf(log, "return: URI_TOO_LONG (headers big branch) uri_size=%zu method_size=%zu\n", uri_size, method_size);
          fclose(log);
        }
        return MHD_HTTP_URI_TOO_LONG;
      }
      else
      {
        if (log)
        {
          fprintf(log, "return: NOT_IMPLEMENTED (method too large) uri_size=%zu method_size=%zu\n", uri_size, method_size);
          fclose(log);
        }
        return MHD_HTTP_NOT_IMPLEMENTED; /* The length of the HTTP request method is unreasonably large */
      }
    }
  }
  if ((size_t) MHD_MAX_REASONABLE_REQ_TARGET_SIZE_ < uri_size)
  {
    if (log)
    {
      fprintf(log, "branch: uri_size > MHD_MAX_REASONABLE_REQ_TARGET_SIZE_ (%zu)\n", uri_size);
      fflush(log);
    }
    if ((uri_size / 16) > method_size)
    {
      if (log)
      {
        fprintf(log, "return: URI_TOO_LONG uri_size=%zu method_size=%zu\n", uri_size, method_size);
        fclose(log);
      }
      return MHD_HTTP_URI_TOO_LONG;     /* Request target is MUCH larger than headers */
    }
    else
    {
      if (log)
      {
        fprintf(log, "return: NOT_IMPLEMENTED (method too large) uri_size=%zu method_size=%zu\n", uri_size, method_size);
        fclose(log);
      }
      return MHD_HTTP_NOT_IMPLEMENTED;  /* The length of the HTTP request method is unreasonably large */
    }
  }

  /* The read buffer is too small to handle reasonably large requests */

  if ((size_t) MHD_MIN_REASONABLE_HEADERS_SIZE_ < opt_headers_size)
  {
    if (log)
    {
      fprintf(log, "branch: opt_headers_size > MHD_MIN_REASONABLE_HEADERS_SIZE_ (%zu)\n", opt_headers_size);
      fflush(log);
    }
    if ((opt_headers_size * 4) > uri_size)
    {
      if (opt_headers_size > method_size)
      {
        if (log)
        {
          fprintf(log, "return: REQUEST_HEADER_FIELDS_TOO_LARGE (min headers branch) opt_headers_size=%zu method_size=%zu\n",
                  opt_headers_size, method_size);
          fclose(log);
        }
        return MHD_HTTP_REQUEST_HEADER_FIELDS_TOO_LARGE;
      }
      else
      {
        if (log)
        {
          fprintf(log, "return: NOT_IMPLEMENTED (method too large) opt_headers_size=%zu method_size=%zu\n",
                  opt_headers_size, method_size);
          fclose(log);
        }
        return MHD_HTTP_NOT_IMPLEMENTED; /* The length of the HTTP request method is unreasonably large */
      }
    }
    else
    { /* Request target is significantly larger than headers */
      if (uri_size > method_size * 4)
      {
        if (log)
        {
          fprintf(log, "return: URI_TOO_LONG (significantly larger) uri_size=%zu method_size=%zu\n", uri_size, method_size);
          fclose(log);
        }
        return MHD_HTTP_URI_TOO_LONG;
      }
      else
      {
        if (log)
        {
          fprintf(log, "return: NOT_IMPLEMENTED (method too large) uri_size=%zu method_size=%zu\n", uri_size, method_size);
          fclose(log);
        }
        return MHD_HTTP_NOT_IMPLEMENTED; /* The length of the HTTP request method is unreasonably large */
      }
    }
  }
  if ((size_t) MHD_MIN_REASONABLE_REQ_TARGET_SIZE_ < uri_size)
  {
    if (log)
    {
      fprintf(log, "branch: uri_size > MHD_MIN_REASONABLE_REQ_TARGET_SIZE_ (%zu)\n", uri_size);
      fflush(log);
    }
    if (uri_size > method_size * 4)
    {
      if (log)
      {
        fprintf(log, "return: URI_TOO_LONG uri_size=%zu method_size=%zu\n", uri_size, method_size);
        fclose(log);
      }
      return MHD_HTTP_URI_TOO_LONG;
    }
    else
    {
      if (log)
      {
        fprintf(log, "return: NOT_IMPLEMENTED (method too large) uri_size=%zu method_size=%zu\n", uri_size, method_size);
        fclose(log);
      }
      return MHD_HTTP_NOT_IMPLEMENTED; /* The length of the HTTP request method is unreasonably large */
    }
  }

  if ((size_t) MHD_MIN_REASONABLE_REQ_METHOD_SIZE_ < method_size)
  {
    if (log)
    {
      fprintf(log, "return: NOT_IMPLEMENTED (method size > MHD_MIN_REASONABLE_REQ_METHOD_SIZE_) method_size=%zu\n", method_size);
      fclose(log);
    }
    return MHD_HTTP_NOT_IMPLEMENTED;
  }

  if ((1 < opt_headers_size) || (1 < uri_size))
  {
    if (opt_headers_size >= uri_size)
    {
      if (log)
      {
        fprintf(log, "return: REQUEST_HEADER_FIELDS_TOO_LARGE (final) opt_headers_size=%zu uri_size=%zu\n", opt_headers_size, uri_size);
        fclose(log);
      }
      return MHD_HTTP_REQUEST_HEADER_FIELDS_TOO_LARGE;
    }
    else
    {
      if (log)
      {
        fprintf(log, "return: URI_TOO_LONG (final) opt_headers_size=%zu uri_size=%zu\n", opt_headers_size, uri_size);
        fclose(log);
      }
      return MHD_HTTP_URI_TOO_LONG;
    }
  }

  if (0 != host_field_line_size)
  {
    if (log)
    {
      fprintf(log, "return: REQUEST_HEADER_FIELDS_TOO_LARGE (no reducible parts left, host present) host_field_line_size=%zu\n", host_field_line_size);
      fclose(log);
    }
    return MHD_HTTP_REQUEST_HEADER_FIELDS_TOO_LARGE;
  }

  if (log)
  {
    fprintf(log, "return: URI_TOO_LONG (default)\n");
    fclose(log);
  }

  return MHD_HTTP_URI_TOO_LONG;
}
