static unsigned int
get_no_space_err_status_code (struct MHD_Connection *c,
                              enum MHD_ProcRecvDataStage stage,
                              const char *add_element,
                              size_t add_element_size)
{
  size_t method_size;
  size_t uri_size;
  size_t opt_headers_size;
  size_t host_field_line_size;

  /* Minimal entry probe */
  {
    FILE *log = fopen("/tmp/logs/2225.txt", "a");
    if (NULL != log)
    {
      fprintf(log, "ENTER get_no_space_err_status_code: c=%p state=%d stage=%d add_element=%p add_element_size=%zu\n",
              (void *)c,
              (NULL != c) ? (int)c->state : -1,
              (int)stage,
              (const void *)add_element,
              add_element_size);
      if ((NULL != c) && (NULL != c->rq.method))
        fprintf(log, "  rq.method=%p req_target_len=%zu http_mthd=%d read_buffer=%p read_buffer_offset=%zu field_lines.start=%p field_lines.size=%zu\n",
                (const void *)c->rq.method,
                c->rq.req_target_len,
                (int)c->rq.http_mthd,
                (const void *)c->read_buffer,
                c->read_buffer_offset,
                (const void *)c->rq.field_lines.start,
                c->rq.field_lines.size);
      fclose(log);
    }
  }

  mhd_assert (MHD_CONNECTION_REQ_LINE_RECEIVED < c->state);
  mhd_assert (MHD_PROC_RECV_HEADERS <= stage);
  mhd_assert ((0 == add_element_size) || (NULL != add_element));

  if (MHD_CONNECTION_HEADERS_RECEIVED > c->state)
  {
    mhd_assert (NULL != c->rq.field_lines.start);
    opt_headers_size =
      (size_t) ((c->read_buffer + c->read_buffer_offset)
                - c->rq.field_lines.start);
  }
  else
    opt_headers_size = c->rq.field_lines.size;

  /* The read buffer is fully used by the request line, the field lines
     (headers) and internal information.
     The return status code works as a suggestion for the client to reduce
     one of the request elements. */

  if ((MHD_PROC_RECV_BODY_CHUNKED == stage) &&
      (MHD_MIN_REASONABLE_REQ_CHUNK_LINE_LENGTH_ < add_element_size))
  {
    FILE *log = fopen("/tmp/logs/2225.txt", "a");
    if (NULL != log)
    {
      fprintf(log, "CHUNKED stage and add_element_size=%zu > min_chunk_len -> returning %u (CONTENT_TOO_LARGE)\n",
              add_element_size,
              (unsigned int)MHD_HTTP_CONTENT_TOO_LARGE);
      fclose(log);
    }
    /* Request could be re-tried easily with smaller chunk sizes */
    return MHD_HTTP_CONTENT_TOO_LARGE;
  }

  host_field_line_size = 0;
  /* The "Host:" field line is mandatory.
     The total size of the field lines (headers) cannot be smaller than
     the size of the "Host:" field line. */
  if ((MHD_PROC_RECV_HEADERS == stage)
      && (0 != add_element_size))
  {
    static const size_t header_host_key_len =
      MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_HOST);
    const bool is_host_header =
      (header_host_key_len + 1 <= add_element_size)
      && ( (0 == add_element[header_host_key_len])
           || (':' == add_element[header_host_key_len]) )
      && MHD_str_equal_caseless_bin_n_ (MHD_HTTP_HEADER_HOST,
                                        add_element,
                                        header_host_key_len);
    if (is_host_header)
    {
      const bool is_parsed = ! (
        (MHD_CONNECTION_HEADERS_RECEIVED > c->state) &&
        (add_element_size == c->read_buffer_offset) &&
        (c->read_buffer == add_element) );
      size_t actual_element_size;

      mhd_assert (! is_parsed || (0 == add_element[header_host_key_len]));
      /* The actual size should be larger due to CRLF or LF chars,
         however the exact termination sequence is not known here and
         as perfect precision is not required, to simplify the code
         assume the minimal length. */
      if (is_parsed)
        actual_element_size = add_element_size + 1;  /* "1" for LF */
      else
        actual_element_size = add_element_size;

      host_field_line_size = actual_element_size;
      mhd_assert (opt_headers_size >= actual_element_size);
      opt_headers_size -= actual_element_size;

      {
        FILE *log = fopen("/tmp/logs/2225.txt", "a");
        if (NULL != log)
        {
          fprintf(log, "Detected Host header in add_element: is_parsed=%d actual_element_size=%zu host_field_line_size=%zu opt_headers_size=%zu\n",
                  (int)is_parsed, actual_element_size, host_field_line_size, opt_headers_size);
          fclose(log);
        }
      }
    }
  }
  if (0 == host_field_line_size)
  {
    static const size_t host_field_name_len =
      MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_HOST);
    size_t host_field_name_value_len;
    if (MHD_NO != MHD_lookup_connection_value_n (c,
                                                 MHD_HEADER_KIND,
                                                 MHD_HTTP_HEADER_HOST,
                                                 host_field_name_len,
                                                 NULL,
                                                 &host_field_name_value_len))
    {
      /* Calculate the minimal size of the field line: no space between
         colon and the field value, line terminated by LR */
      host_field_line_size =
        host_field_name_len + host_field_name_value_len + 2; /* "2" for ':' and LF */

      /* The "Host:" field could be added by application */
      if (opt_headers_size >= host_field_line_size)
      {
        opt_headers_size -= host_field_line_size;
        /* Take into account typical space after colon and CR at the end of the line */
        if (opt_headers_size >= 2)
          opt_headers_size -= 2;
      }
      else
        host_field_line_size = 0; /* No "Host:" field line set by the client */

      {
        FILE *log = fopen("/tmp/logs/2225.txt", "a");
        if (NULL != log)
        {
          fprintf(log, "Host lookup: host_field_name_value_len=%zu host_field_line_size=%zu opt_headers_size=%zu\n",
                  host_field_name_value_len, host_field_line_size, opt_headers_size);
          fclose(log);
        }
      }
    }
  }

  uri_size = c->rq.req_target_len;
  if (MHD_HTTP_MTHD_OTHER != c->rq.http_mthd)
    method_size = 0; /* Do not recommend shorter request method */
  else
  {
    mhd_assert (NULL != c->rq.method);
    method_size = strlen (c->rq.method);

    {
      FILE *log = fopen("/tmp/logs/2225.txt", "a");
      if (NULL != log)
      {
        /* Log pointer to method and computed length. This is key for
           validating the MISRA 8.13 scenario where method should be
           const-qualified since it's only read. */
        fprintf(log, "Computed method_size=%zu for rq.method=%p (method first bytes): ",
                method_size, (const void *)c->rq.method);
        /* print up to 64 chars or until NUL */
        if (NULL != c->rq.method)
        {
          size_t i;
          for (i = 0; (i < 64) && (c->rq.method[i] != '\0'); ++i)
            fputc((int)c->rq.method[i], log);
          if (i == 64)
            fprintf(log, "...");
        }
        fprintf(log, "\n");
        fclose(log);
      }
    }
  }

  if ((size_t) MHD_MAX_REASONABLE_HEADERS_SIZE_ < opt_headers_size)
  {
    /* Typically the easiest way to reduce request header size is
       a removal of some optional headers. */
    if (opt_headers_size > (uri_size / 8))
    {
      if ((opt_headers_size / 2) > method_size)
      {
        FILE *log = fopen("/tmp/logs/2225.txt", "a");
        if (NULL != log)
        {
          fprintf(log, "Decision: opt_headers_size(%zu) large; (opt_headers_size/2)=%zu > method_size(%zu) -> returning %u (REQUEST_HEADER_FIELDS_TOO_LARGE)\n",
                  opt_headers_size, (opt_headers_size / 2), method_size,
                  (unsigned int)MHD_HTTP_REQUEST_HEADER_FIELDS_TOO_LARGE);
          fclose(log);
        }
        return MHD_HTTP_REQUEST_HEADER_FIELDS_TOO_LARGE;
      }
      else
      {
        FILE *log = fopen("/tmp/logs/2225.txt", "a");
        if (NULL != log)
        {
          fprintf(log, "Decision: opt_headers_size(%zu) large; method_size(%zu) >= (opt_headers_size/2) -> returning %u (NOT_IMPLEMENTED)\n",
                  opt_headers_size, method_size, (unsigned int)MHD_HTTP_NOT_IMPLEMENTED);
          fclose(log);
        }
        return MHD_HTTP_NOT_IMPLEMENTED; /* The length of the HTTP request method is unreasonably large */
      }
    }
    else
    { /* Request target is MUCH larger than headers */
      if ((uri_size / 16) > method_size)
      {
        FILE *log = fopen("/tmp/logs/2225.txt", "a");
        if (NULL != log)
        {
          fprintf(log, "Decision: uri much larger than headers -> returning %u (URI_TOO_LONG)\n",
                  (unsigned int)MHD_HTTP_URI_TOO_LONG);
          fclose(log);
        }
        return MHD_HTTP_URI_TOO_LONG;
      }
      else
      {
        FILE *log = fopen("/tmp/logs/2225.txt", "a");
        if (NULL != log)
        {
          fprintf(log, "Decision: uri vs method -> returning %u (NOT_IMPLEMENTED)\n",
                  (unsigned int)MHD_HTTP_NOT_IMPLEMENTED);
          fclose(log);
        }
        return MHD_HTTP_NOT_IMPLEMENTED; /* The length of the HTTP request method is unreasonably large */
      }
    }
  }
  if ((size_t) MHD_MAX_REASONABLE_REQ_TARGET_SIZE_ < uri_size)
  {
    /* If request target size if larger than maximum reasonable size
       recommend client to reduce the request target size (length). */
    if ((uri_size / 16) > method_size)
    {
      FILE *log = fopen("/tmp/logs/2225.txt", "a");
      if (NULL != log)
      {
        fprintf(log, "Decision: uri_size(%zu) > max_reasonable -> returning %u (URI_TOO_LONG)\n",
                uri_size, (unsigned int)MHD_HTTP_URI_TOO_LONG);
        fclose(log);
      }
      return MHD_HTTP_URI_TOO_LONG;     /* Request target is MUCH larger than headers */
    }
    else
    {
      FILE *log = fopen("/tmp/logs/2225.txt", "a");
      if (NULL != log)
      {
        fprintf(log, "Decision: uri_size(%zu) > max_reasonable but method_balance -> returning %u (NOT_IMPLEMENTED)\n",
                uri_size, (unsigned int)MHD_HTTP_NOT_IMPLEMENTED);
        fclose(log);
      }
      return MHD_HTTP_NOT_IMPLEMENTED;  /* The length of the HTTP request method is unreasonably large */
    }
  }

  /* The read buffer is too small to handle reasonably large requests */

  if ((size_t) MHD_MIN_REASONABLE_HEADERS_SIZE_ < opt_headers_size)
  {
    /* Recommend application to retry with minimal headers */
    if ((opt_headers_size * 4) > uri_size)
    {
      if (opt_headers_size > method_size)
      {
        FILE *log = fopen("/tmp/logs/2225.txt", "a");
        if (NULL != log)
        {
          fprintf(log, "Decision: opt_headers_size(%zu)*4 > uri_size(%zu) and opt_headers_size > method_size(%zu) -> returning %u (REQUEST_HEADER_FIELDS_TOO_LARGE)\n",
                  opt_headers_size, uri_size, method_size, (unsigned int)MHD_HTTP_REQUEST_HEADER_FIELDS_TOO_LARGE);
          fclose(log);
        }
        return MHD_HTTP_REQUEST_HEADER_FIELDS_TOO_LARGE;
      }
      else
      {
        FILE *log = fopen("/tmp/logs/2225.txt", "a");
        if (NULL != log)
        {
          fprintf(log, "Decision: opt_headers_size*4 > uri_size but method_threshold -> returning %u (NOT_IMPLEMENTED)\n",
                  (unsigned int)MHD_HTTP_NOT_IMPLEMENTED);
          fclose(log);
        }
        return MHD_HTTP_NOT_IMPLEMENTED; /* The length of the HTTP request method is unreasonably large */
      }
    }
    else
    { /* Request target is significantly larger than headers */
      if (uri_size > method_size * 4)
      {
        FILE *log = fopen("/tmp/logs/2225.txt", "a");
        if (NULL != log)
        {
          fprintf(log, "Decision: uri_size(%zu) > method_size*4(%zu) -> returning %u (URI_TOO_LONG)\n",
                  uri_size, method_size * 4, (unsigned int)MHD_HTTP_URI_TOO_LONG);
          fclose(log);
        }
        return MHD_HTTP_URI_TOO_LONG;
      }
      else
      {
        FILE *log = fopen("/tmp/logs/2225.txt", "a");
        if (NULL != log)
        {
          fprintf(log, "Decision: final method fallback -> returning %u (NOT_IMPLEMENTED)\n",
                  (unsigned int)MHD_HTTP_NOT_IMPLEMENTED);
          fclose(log);
        }
        return MHD_HTTP_NOT_IMPLEMENTED; /* The length of the HTTP request method is unreasonably large */
      }
    }
  }
  if ((size_t) MHD_MIN_REASONABLE_REQ_TARGET_SIZE_ < uri_size)
  {
    /* Recommend application to retry with a shorter request target */
    if (uri_size > method_size * 4)
    {
      FILE *log = fopen("/tmp/logs/2225.txt", "a");
      if (NULL != log)
      {
        fprintf(log, "Decision: uri_size(%zu) > min_reasonable_req_target_size and uri_size > method_size*4 -> returning %u (URI_TOO_LONG)\n",
                uri_size, (unsigned int)MHD_HTTP_URI_TOO_LONG);
        fclose(log);
      }
      return MHD_HTTP_URI_TOO_LONG;
    }
    else
    {
      FILE *log = fopen("/tmp/logs/2225.txt", "a");
      if (NULL != log)
      {
        fprintf(log, "Decision: uri_size > min_reasonable but method balance -> returning %u (NOT_IMPLEMENTED)\n",
                (unsigned int)MHD_HTTP_NOT_IMPLEMENTED);
        fclose(log);
      }
      return MHD_HTTP_NOT_IMPLEMENTED; /* The length of the HTTP request method is unreasonably large */
    }
  }

  if ((size_t) MHD_MIN_REASONABLE_REQ_METHOD_SIZE_ < method_size)
  {
    /* The request target (URI) and headers are (reasonably) very small.
       Some non-standard long request method is used. */
    /* The last resort response as it means "the method is not supported
       by the server for any URI". */
    FILE *log = fopen("/tmp/logs/2225.txt", "a");
    if (NULL != log)
    {
      fprintf(log, "Decision: method_size(%zu) > MIN_REASONABLE_REQ_METHOD_SIZE -> returning %u (NOT_IMPLEMENTED)\n",
              method_size, (unsigned int)MHD_HTTP_NOT_IMPLEMENTED);
      fclose(log);
    }
    return MHD_HTTP_NOT_IMPLEMENTED;
  }

  /* The almost impossible situation: all elements are small, but cannot
     fit the buffer. The application set the buffer size to
     critically low value? */

  if ((1 < opt_headers_size) || (1 < uri_size))
  {
    if (opt_headers_size >= uri_size)
    {
      FILE *log = fopen("/tmp/logs/2225.txt", "a");
      if (NULL != log)
      {
        fprintf(log, "Decision: small remaining but opt_headers_size(%zu) >= uri_size(%zu) -> returning %u (REQUEST_HEADER_FIELDS_TOO_LARGE)\n",
                opt_headers_size, uri_size, (unsigned int)MHD_HTTP_REQUEST_HEADER_FIELDS_TOO_LARGE);
        fclose(log);
      }
      return MHD_HTTP_REQUEST_HEADER_FIELDS_TOO_LARGE;
    }
    else
    {
      FILE *log = fopen("/tmp/logs/2225.txt", "a");
      if (NULL != log)
      {
        fprintf(log, "Decision: small remaining but uri_size(%zu) > opt_headers_size(%zu) -> returning %u (URI_TOO_LONG)\n",
                uri_size, opt_headers_size, (unsigned int)MHD_HTTP_URI_TOO_LONG);
        fclose(log);
      }
      return MHD_HTTP_URI_TOO_LONG;
    }
  }

  /* Nothing to reduce in the request.
     Reply with some status. */
  if (0 != host_field_line_size)
  {
    FILE *log = fopen("/tmp/logs/2225.txt", "a");
    if (NULL != log)
    {
      fprintf(log, "Nothing reducible but Host present -> returning %u (REQUEST_HEADER_FIELDS_TOO_LARGE)\n",
              (unsigned int)MHD_HTTP_REQUEST_HEADER_FIELDS_TOO_LARGE);
      fclose(log);
    }
    return MHD_HTTP_REQUEST_HEADER_FIELDS_TOO_LARGE;
  }

  {
    FILE *log = fopen("/tmp/logs/2225.txt", "a");
    if (NULL != log)
    {
      fprintf(log, "Nothing reducible and no Host -> returning %u (URI_TOO_LONG)\n",
              (unsigned int)MHD_HTTP_URI_TOO_LONG);
      fclose(log);
    }
  }

  return MHD_HTTP_URI_TOO_LONG;
}
