static bool
get_nonce_timestamp (const char *const nonce,
                     size_t noncelen,
                     uint64_t *const ptimestamp)
{
  FILE *log = fopen("/tmp/logs/1889.txt", "a");
  if (log)
    fprintf(log, "enter get_nonce_timestamp: nonce=%p noncelen=%zu\n", (void *)nonce, noncelen);

  if (0 == noncelen)
    {
      if (nonce)
        {
          noncelen = strlen (nonce);
          if (log)
            fprintf(log, "noncelen was 0, computed strlen(nonce)=%zu\n", noncelen);
        }
      else
        {
          if (log)
            fprintf(log, "noncelen was 0 but nonce==NULL\n");
        }
      if (log)
        fflush(log);
    }

#ifdef MHD_MD5_SUPPORT
  if (log)
    fprintf(log, "MHD_MD5_SUPPORT defined\n");
#else
  if (log)
    fprintf(log, "MHD_MD5_SUPPORT NOT defined\n");
#endif

#if defined(MHD_SHA256_SUPPORT) || defined(MHD_SHA512_256_SUPPORT)
  if (log)
    fprintf(log, "MHD_SHA256_SUPPORT or MHD_SHA512_256_SUPPORT defined\n");
#else
  if (log)
    fprintf(log, "MHD_SHA256_SUPPORT and MHD_SHA512_256_SUPPORT NOT defined\n");
#endif

  if (log)
    fflush(log);

  if (true
#ifdef MHD_MD5_SUPPORT
      && (NONCE_STD_LEN (MD5_DIGEST_SIZE) != noncelen)
#endif /* MHD_MD5_SUPPORT */
#if defined(MHD_SHA256_SUPPORT) || defined(MHD_SHA512_256_SUPPORT)
      && (NONCE_STD_LEN (SHA256_SHA512_256_DIGEST_SIZE) != noncelen)
#endif /* MHD_SHA256_SUPPORT */
      )
    {
      if (log)
        fprintf(log, "length check controlling expression evaluated TRUE -> returning false (noncelen=%zu)\n", noncelen);
      if (log)
        fclose(log);
      return false;
    }

  if (log)
    fprintf(log, "passed length check, attempting to parse timestamp from last %d chars\n", (int) TIMESTAMP_CHARS_LEN);
  if (log)
    fflush(log);

  if (TIMESTAMP_CHARS_LEN !=
      MHD_strx_to_uint64_n_ (nonce + noncelen - TIMESTAMP_CHARS_LEN,
                             TIMESTAMP_CHARS_LEN,
                             ptimestamp))
    {
      if (log)
        fprintf(log, "timestamp parse failed\n");
      if (log)
        fclose(log);
      return false;
    }

  if (log)
    fprintf(log, "timestamp parse succeeded: %llu\n", (unsigned long long) *ptimestamp);
  if (log)
    fclose(log);
  return true;
}
