static bool
get_nonce_timestamp (const char *const nonce,
                     size_t noncelen,
                     uint64_t *const ptimestamp)
{
  FILE *f = fopen("/tmp/logs/1886.txt", "a");
  if (f)
    fprintf(f, "[get_nonce_timestamp] ENTER nonce=%p noncelen=%zu ptimestamp=%p\n",
            (const void *)nonce, noncelen, (const void *)ptimestamp);

#ifdef MHD_MD5_SUPPORT
  if (f) fprintf(f, "[get_nonce_timestamp] MHD_MD5_SUPPORT defined\n");
#else
  if (f) fprintf(f, "[get_nonce_timestamp] MHD_MD5_SUPPORT NOT defined\n");
#endif /* MHD_MD5_SUPPORT */

#if defined(MHD_SHA256_SUPPORT) || defined(MHD_SHA512_256_SUPPORT)
  if (f) fprintf(f, "[get_nonce_timestamp] SHA256/SHA512_256 support defined\n");
#else
  if (f) fprintf(f, "[get_nonce_timestamp] SHA256/SHA512_256 support NOT defined\n");
#endif /* MHD_SHA256_SUPPORT || MHD_SHA512_256_SUPPORT */

  if (0 == noncelen)
    {
      /* preserve original behavior */
      noncelen = strlen (nonce);
      if (f) fprintf(f, "[get_nonce_timestamp] computed noncelen=%zu (from strlen)\n", noncelen);
    }

  /* Recompute the predicate so we can log whether the early-return path is taken. */
  bool predicate = true
#ifdef MHD_MD5_SUPPORT
    && (NONCE_STD_LEN (MD5_DIGEST_SIZE) != noncelen)
#endif /* MHD_MD5_SUPPORT */
#if defined(MHD_SHA256_SUPPORT) || defined(MHD_SHA512_256_SUPPORT)
    && (NONCE_STD_LEN (SHA256_SHA512_256_DIGEST_SIZE) != noncelen)
#endif /* MHD_SHA256_SUPPORT */
    ;

  if (f) fprintf(f, "[get_nonce_timestamp] predicate evaluates to %s\n", predicate ? "true" : "false");

  if (predicate)
    {
      if (f) fprintf(f, "[get_nonce_timestamp] early return: predicate true -> return false\n");
      if (f) fclose (f);
      return false;
    }

  if (f) fprintf(f, "[get_nonce_timestamp] proceeding to parse timestamp from nonce suffix\n");

  size_t converted = MHD_strx_to_uint64_n_ (nonce + noncelen - TIMESTAMP_CHARS_LEN,
                                             TIMESTAMP_CHARS_LEN,
                                             ptimestamp);

  if (f) fprintf(f, "[get_nonce_timestamp] MHD_strx_to_uint64_n_ returned %zu, expected %d\n",
                  converted, TIMESTAMP_CHARS_LEN);

  if (TIMESTAMP_CHARS_LEN != converted)
    {
      if (f) fprintf(f, "[get_nonce_timestamp] timestamp conversion failed -> return false\n");
      if (f) fclose (f);
      return false;
    }

  if (f) fprintf(f, "[get_nonce_timestamp] success: timestamp=%llu -> return true\n",
                  (unsigned long long) *ptimestamp);
  if (f) fclose (f);
  return true;
}
