static enum MHD_DigestAuthAlgo3
get_rq_dauth_algo (const struct MHD_RqDAuthParam *const algo_param)
{
  FILE *f = fopen("/tmp/logs/1173.txt", "a");
  if (f)
  {
    fprintf(f, "ENTER get_rq_dauth_algo: algo_param=%p\n", (void *)algo_param);
    if (algo_param)
      fprintf(f, "  value.str=%p value.len=%zu quoted=%d\n",
              (void *)algo_param->value.str,
              (size_t)algo_param->value.len,
              (int)algo_param->quoted);
    fflush(f);
  }

  if (NULL == algo_param->value.str)
  {
    if (f)
      fprintf(f, "EXIT get_rq_dauth_algo: value.str is NULL -> MD5 (default)\n");
    if (f)
      fclose(f);
    return MHD_DIGEST_AUTH_ALGO3_MD5; /* Assume MD5 by default */
  }

  if (algo_param->quoted)
  {
    if (f)
      fprintf(f, "quoted: comparing against _MHD_MD5_TOKEN\n");
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_MD5_TOKEN))
    {
      if (f)
        fprintf(f, "MATCH: _MHD_MD5_TOKEN -> MD5\n");
      if (f)
        fclose(f);
      return MHD_DIGEST_AUTH_ALGO3_MD5;
    }

    if (f)
      fprintf(f, "quoted: comparing against _MHD_SHA256_TOKEN\n");
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_SHA256_TOKEN))
    {
      if (f)
        fprintf(f, "MATCH: _MHD_SHA256_TOKEN -> SHA256\n");
      if (f)
        fclose(f);
      return MHD_DIGEST_AUTH_ALGO3_SHA256;
    }

    if (f)
      fprintf(f, "quoted: comparing against _MHD_MD5_TOKEN _MHD_SESS_TOKEN -> SHA512_256 expected?\n");
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_MD5_TOKEN _MHD_SESS_TOKEN))
    {
      if (f)
        fprintf(f, "MATCH: _MHD_MD5_TOKEN _MHD_SESS_TOKEN -> SHA512_256\n");
      if (f)
        fclose(f);
      return MHD_DIGEST_AUTH_ALGO3_SHA512_256;
    }

    if (f)
      fprintf(f, "quoted: comparing against _MHD_SHA512_256_TOKEN _MHD_SESS_TOKEN\n");
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_SHA512_256_TOKEN
                                               _MHD_SESS_TOKEN))

      /* Algorithms below are not supported by MHD for authentication */

    {
      if (f)
        fprintf(f, "MATCH: _MHD_SHA512_256_TOKEN _MHD_SESS_TOKEN -> MD5_SESSION (reported)\n");
      if (f)
        fclose(f);
      return MHD_DIGEST_AUTH_ALGO3_MD5_SESSION;
    }

    if (f)
      fprintf(f, "quoted: comparing against _MHD_SHA256_TOKEN _MHD_SESS_TOKEN\n");
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_SHA256_TOKEN
                                               _MHD_SESS_TOKEN))
    {
      if (f)
        fprintf(f, "MATCH: _MHD_SHA256_TOKEN _MHD_SESS_TOKEN -> SHA256_SESSION\n");
      if (f)
        fclose(f);
      return MHD_DIGEST_AUTH_ALGO3_SHA256_SESSION;
    }

    if (f)
      fprintf(f, "quoted: comparing against _MHD_SHA512_256_TOKEN (session?)\n");
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_SHA512_256_TOKEN))
    {
      if (f)
        fprintf(f, "MATCH: _MHD_SHA512_256_TOKEN -> SHA512_256_SESSION\n");
      if (f)
        fclose(f);
      return MHD_DIGEST_AUTH_ALGO3_SHA512_256_SESSION;
    }

    /* No known algorithm has been detected */
    if (f)
      fprintf(f, "EXIT get_rq_dauth_algo: quoted path -> INVALID\n");
    if (f)
      fclose(f);
    return MHD_DIGEST_AUTH_ALGO3_INVALID;
  }

  /* The algorithm value is not quoted */
  if (f)
    fprintf(f, "not quoted: comparing _MHD_MD5_TOKEN (non-quoted)\n");
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_MD5_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len))
  {
    if (f)
      fprintf(f, "MATCH: non-quoted _MHD_MD5_TOKEN -> MD5\n");
    if (f)
      fclose(f);
    return MHD_DIGEST_AUTH_ALGO3_MD5;
  }

  if (f)
    fprintf(f, "not quoted: comparing _MHD_SHA256_TOKEN (non-quoted)\n");
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA256_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len))
  {
    if (f)
      fprintf(f, "MATCH: non-quoted _MHD_SHA256_TOKEN -> SHA256\n");
    if (f)
      fclose(f);
    return MHD_DIGEST_AUTH_ALGO3_SHA256;
  }

  if (f)
    fprintf(f, "not quoted: comparing _MHD_SHA512_256_TOKEN (non-quoted)\n");
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA512_256_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len))
  {
    if (f)
      fprintf(f, "MATCH: non-quoted _MHD_SHA512_256_TOKEN -> SHA512_256\n");
    if (f)
      fclose(f);
    return MHD_DIGEST_AUTH_ALGO3_SHA512_256;
  }

  /* Algorithms below are not supported by MHD for authentication */
  if (f)
    fprintf(f, "not quoted: comparing _MHD_MD5_TOKEN _MHD_SESS_TOKEN\n");
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_MD5_TOKEN _MHD_SESS_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len))
  {
    if (f)
      fprintf(f, "MATCH: non-quoted _MHD_MD5_TOKEN _MHD_SESS_TOKEN -> MD5_SESSION\n");
    if (f)
      fclose(f);
    return MHD_DIGEST_AUTH_ALGO3_MD5_SESSION;
  }

  if (f)
    fprintf(f, "not quoted: comparing _MHD_SHA256_TOKEN _MHD_SESS_TOKEN\n");
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA256_TOKEN _MHD_SESS_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len))
  {
    if (f)
      fprintf(f, "MATCH: non-quoted _MHD_SHA256_TOKEN _MHD_SESS_TOKEN -> SHA256_SESSION\n");
    if (f)
      fclose(f);
    return MHD_DIGEST_AUTH_ALGO3_SHA256_SESSION;
  }

  if (f)
    fprintf(f, "not quoted: comparing _MHD_SHA512_256_TOKEN _MHD_SESS_TOKEN\n");
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA512_256_TOKEN _MHD_SESS_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len))
  {
    if (f)
      fprintf(f, "MATCH: non-quoted _MHD_SHA512_256_TOKEN _MHD_SESS_TOKEN -> SHA512_256_SESSION\n");
    if (f)
      fclose(f);
    return MHD_DIGEST_AUTH_ALGO3_SHA512_256_SESSION;
  }

  /* No known algorithm has been detected */
  if (f)
    fprintf(f, "EXIT get_rq_dauth_algo: non-quoted path -> INVALID\n");
  if (f)
    fclose(f);
  return MHD_DIGEST_AUTH_ALGO3_INVALID;
}
