static enum MHD_DigestAuthAlgo3
get_rq_dauth_algo (const struct MHD_RqDAuthParam *const algo_param)
{
  FILE *log = fopen("/tmp/logs/1173.txt", "a");
  if (log)
  {
    fprintf(log, "get_rq_dauth_algo entered: algo_param=%p\n", (void *)algo_param);
    if (algo_param)
    {
      fprintf(log, "  value.str=%p, value.len=%zu, quoted=%d\n",
              (void *)algo_param->value.str,
              (size_t)algo_param->value.len,
              (int)algo_param->quoted);
      /* Hex dump up to first 64 bytes (bounded by reported length) to capture
         potential non-NUL-terminated content without reading beyond len. */
      if (algo_param->value.str != NULL && algo_param->value.len > 0)
      {
        size_t i, lim = (algo_param->value.len > 64) ? 64U : (size_t)algo_param->value.len;
        fprintf(log, "  first %zu bytes (hex):", lim);
        for (i = 0; i < lim; ++i)
          fprintf(log, " %02x", (unsigned int)(unsigned char)algo_param->value.str[i]);
        if (algo_param->value.len > lim)
          fprintf(log, " ... (len=%zu)", (size_t)algo_param->value.len);
        fputc('\n', log);
      }
    }
    fflush(log);
  }

  if (NULL == algo_param->value.str)
  {
    if (log)
    {
      fprintf(log, "  value.str is NULL -> defaulting to MD5\n");
      fclose(log);
    }
    return MHD_DIGEST_AUTH_ALGO3_MD5; /* Assume MD5 by default */
  }

  if (algo_param->quoted)
  {
    int matched;

    matched = MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                                      algo_param->value.len,
                                                      _MHD_MD5_TOKEN);
    if (log) { fprintf(log, "  quoted: compare _MHD_MD5_TOKEN -> %d\n", matched); fflush(log); }
    if (matched)
    {
      if (log) { fprintf(log, "  result: MD5 (quoted)\n"); fclose(log); }
      return MHD_DIGEST_AUTH_ALGO3_MD5;
    }

    matched = MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                                      algo_param->value.len,
                                                      _MHD_SHA256_TOKEN);
    if (log) { fprintf(log, "  quoted: compare _MHD_SHA256_TOKEN -> %d\n", matched); fflush(log); }
    if (matched)
    {
      if (log) { fprintf(log, "  result: SHA256 (quoted)\n"); fclose(log); }
      return MHD_DIGEST_AUTH_ALGO3_SHA256;
    }

    matched = MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                                      algo_param->value.len,
                                                      _MHD_MD5_TOKEN _MHD_SESS_TOKEN);
    if (log) { fprintf(log, "  quoted: compare _MHD_MD5_TOKEN _MHD_SESS_TOKEN -> %d\n", matched); fflush(log); }
    if (matched)
    {
      if (log) { fprintf(log, "  result: SHA512_256 (quoted MD5+sess)\n"); fclose(log); }
      return MHD_DIGEST_AUTH_ALGO3_SHA512_256;
    }

    matched = MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                                      algo_param->value.len,
                                                      _MHD_SHA512_256_TOKEN _MHD_SESS_TOKEN);
    if (log) { fprintf(log, "  quoted: compare _MHD_SHA512_256_TOKEN _MHD_SESS_TOKEN -> %d\n", matched); fflush(log); }

    /* Algorithms below are not supported by MHD for authentication */

    if (matched)
    {
      if (log) { fprintf(log, "  result: MD5_SESSION (quoted SHA512_256+sess)\n"); fclose(log); }
      return MHD_DIGEST_AUTH_ALGO3_MD5_SESSION;
    }

    matched = MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                                      algo_param->value.len,
                                                      _MHD_SHA256_TOKEN _MHD_SESS_TOKEN);
    if (log) { fprintf(log, "  quoted: compare _MHD_SHA256_TOKEN _MHD_SESS_TOKEN -> %d\n", matched); fflush(log); }
    if (matched)
    {
      if (log) { fprintf(log, "  result: SHA256_SESSION (quoted)\n"); fclose(log); }
      return MHD_DIGEST_AUTH_ALGO3_SHA256_SESSION;
    }

    matched = MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                                      algo_param->value.len,
                                                      _MHD_SHA512_256_TOKEN);
    if (log) { fprintf(log, "  quoted: compare _MHD_SHA512_256_TOKEN -> %d\n", matched); fflush(log); }
    if (matched)
    {
      if (log) { fprintf(log, "  result: SHA512_256_SESSION (quoted)\n"); fclose(log); }
      return MHD_DIGEST_AUTH_ALGO3_SHA512_256_SESSION;
    }

    /* No known algorithm has been detected */
    if (log) { fprintf(log, "  quoted: no known algorithm -> INVALID\n"); fclose(log); }
    return MHD_DIGEST_AUTH_ALGO3_INVALID;
  }

  /* The algorithm value is not quoted */
  {
    int matched;

    matched = MHD_str_equal_caseless_s_bin_n_ (_MHD_MD5_TOKEN,
                                               algo_param->value.str,
                                               algo_param->value.len);
    if (log) { fprintf(log, "  unquoted: compare _MHD_MD5_TOKEN -> %d\n", matched); fflush(log); }
    if (matched)
    {
      if (log) { fprintf(log, "  result: MD5 (unquoted)\n"); fclose(log); }
      return MHD_DIGEST_AUTH_ALGO3_MD5;
    }

    matched = MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA256_TOKEN,
                                               algo_param->value.str,
                                               algo_param->value.len);
    if (log) { fprintf(log, "  unquoted: compare _MHD_SHA256_TOKEN -> %d\n", matched); fflush(log); }
    if (matched)
    {
      if (log) { fprintf(log, "  result: SHA256 (unquoted)\n"); fclose(log); }
      return MHD_DIGEST_AUTH_ALGO3_SHA256;
    }

    matched = MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA512_256_TOKEN,
                                               algo_param->value.str,
                                               algo_param->value.len);
    if (log) { fprintf(log, "  unquoted: compare _MHD_SHA512_256_TOKEN -> %d\n", matched); fflush(log); }
    if (matched)
    {
      if (log) { fprintf(log, "  result: SHA512_256 (unquoted)\n"); fclose(log); }
      return MHD_DIGEST_AUTH_ALGO3_SHA512_256;
    }

    /* Algorithms below are not supported by MHD for authentication */

    matched = MHD_str_equal_caseless_s_bin_n_ (_MHD_MD5_TOKEN _MHD_SESS_TOKEN,
                                               algo_param->value.str,
                                               algo_param->value.len);
    if (log) { fprintf(log, "  unquoted: compare _MHD_MD5_TOKEN _MHD_SESS_TOKEN -> %d\n", matched); fflush(log); }
    if (matched)
    {
      if (log) { fprintf(log, "  result: MD5_SESSION (unquoted)\n"); fclose(log); }
      return MHD_DIGEST_AUTH_ALGO3_MD5_SESSION;
    }

    matched = MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA256_TOKEN _MHD_SESS_TOKEN,
                                               algo_param->value.str,
                                               algo_param->value.len);
    if (log) { fprintf(log, "  unquoted: compare _MHD_SHA256_TOKEN _MHD_SESS_TOKEN -> %d\n", matched); fflush(log); }
    if (matched)
    {
      if (log) { fprintf(log, "  result: SHA256_SESSION (unquoted)\n"); fclose(log); }
      return MHD_DIGEST_AUTH_ALGO3_SHA256_SESSION;
    }

    matched = MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA512_256_TOKEN _MHD_SESS_TOKEN,
                                               algo_param->value.str,
                                               algo_param->value.len);
    if (log) { fprintf(log, "  unquoted: compare _MHD_SHA512_256_TOKEN _MHD_SESS_TOKEN -> %d\n", matched); fflush(log); }
    if (matched)
    {
      if (log) { fprintf(log, "  result: SHA512_256_SESSION (unquoted)\n"); fclose(log); }
      return MHD_DIGEST_AUTH_ALGO3_SHA512_256_SESSION;
    }
  }

  /* No known algorithm has been detected */
  if (log) { fprintf(log, "  unquoted: no known algorithm -> INVALID\n"); fclose(log); }
  return MHD_DIGEST_AUTH_ALGO3_INVALID;
}
