static enum MHD_DigestAuthAlgo3
get_rq_dauth_algo (const struct MHD_RqDAuthParam *const algo_param)
{
  FILE *logf = fopen("/tmp/logs/1175.txt", "a");
  if (logf)
  {
    fprintf(logf, "Entered get_rq_dauth_algo: algo_param=%p, quoted=%d, value.str=%p, value.len=%zu\n",
            (const void *)algo_param, (int)algo_param->quoted,
            (const void *)algo_param->value.str, (size_t)algo_param->value.len);
    fflush(logf);
  }

  if (NULL == algo_param->value.str)
  {
    if (logf)
    {
      fprintf(logf, "Decision: value.str is NULL -> returning MHD_DIGEST_AUTH_ALGO3_MD5 (default)\n");
      fclose(logf);
    }
    return MHD_DIGEST_AUTH_ALGO3_MD5; /* Assume MD5 by default */
  }

  if (algo_param->quoted)
  {
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_MD5_TOKEN))
    {
      if (logf)
      {
        fprintf(logf, "Matched quoted token: %s -> MHD_DIGEST_AUTH_ALGO3_MD5\n", _MHD_MD5_TOKEN);
        fclose(logf);
      }
      return MHD_DIGEST_AUTH_ALGO3_MD5;
    }
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_SHA256_TOKEN))
    {
      if (logf)
      {
        fprintf(logf, "Matched quoted token: %s -> MHD_DIGEST_AUTH_ALGO3_SHA256\n", _MHD_SHA256_TOKEN);
        fclose(logf);
      }
      return MHD_DIGEST_AUTH_ALGO3_SHA256;
    }
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_MD5_TOKEN _MHD_SESS_TOKEN))
    {
      if (logf)
      {
        fprintf(logf, "Matched quoted token: %s%s -> MHD_DIGEST_AUTH_ALGO3_SHA512_256\n", _MHD_MD5_TOKEN, _MHD_SESS_TOKEN);
        fclose(logf);
      }
      return MHD_DIGEST_AUTH_ALGO3_SHA512_256;
    }
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_SHA512_256_TOKEN _MHD_SESS_TOKEN))
    {
      /* Algorithms below are not supported by MHD for authentication */
      if (logf)
      {
        fprintf(logf, "Matched quoted token: %s%s -> MHD_DIGEST_AUTH_ALGO3_MD5_SESSION (unsupported by MHD)\n",
                _MHD_SHA512_256_TOKEN, _MHD_SESS_TOKEN);
        fclose(logf);
      }
      return MHD_DIGEST_AUTH_ALGO3_MD5_SESSION;
    }
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_SHA256_TOKEN _MHD_SESS_TOKEN))
    {
      if (logf)
      {
        fprintf(logf, "Matched quoted token: %s%s -> MHD_DIGEST_AUTH_ALGO3_SHA256_SESSION (unsupported by MHD)\n",
                _MHD_SHA256_TOKEN, _MHD_SESS_TOKEN);
        fclose(logf);
      }
      return MHD_DIGEST_AUTH_ALGO3_SHA256_SESSION;
    }
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_SHA512_256_TOKEN))
    {
      if (logf)
      {
        fprintf(logf, "Matched quoted token: %s -> MHD_DIGEST_AUTH_ALGO3_SHA512_256_SESSION (unsupported by MHD)\n",
                _MHD_SHA512_256_TOKEN);
        fclose(logf);
      }
      return MHD_DIGEST_AUTH_ALGO3_SHA512_256_SESSION;
    }

    /* No known algorithm has been detected */
    if (logf)
    {
      fprintf(logf, "No known quoted algorithm detected -> returning MHD_DIGEST_AUTH_ALGO3_INVALID\n");
      fclose(logf);
    }
    return MHD_DIGEST_AUTH_ALGO3_INVALID;
  }

  /* The algorithm value is not quoted */
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_MD5_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len))
  {
    if (logf)
    {
      fprintf(logf, "Matched token (unquoted): %s -> MHD_DIGEST_AUTH_ALGO3_MD5\n", _MHD_MD5_TOKEN);
      fclose(logf);
    }
    return MHD_DIGEST_AUTH_ALGO3_MD5;
  }
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA256_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len))
  {
    if (logf)
    {
      fprintf(logf, "Matched token (unquoted): %s -> MHD_DIGEST_AUTH_ALGO3_SHA256\n", _MHD_SHA256_TOKEN);
      fclose(logf);
    }
    return MHD_DIGEST_AUTH_ALGO3_SHA256;
  }
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA512_256_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len))
  {
    if (logf)
    {
      fprintf(logf, "Matched token (unquoted): %s -> MHD_DIGEST_AUTH_ALGO3_SHA512_256\n", _MHD_SHA512_256_TOKEN);
      fclose(logf);
    }
    return MHD_DIGEST_AUTH_ALGO3_SHA512_256;
  }

  /* Algorithms below are not supported by MHD for authentication */
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_MD5_TOKEN _MHD_SESS_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len))
  {
    if (logf)
    {
      fprintf(logf, "Matched token (unquoted): %s%s -> MHD_DIGEST_AUTH_ALGO3_MD5_SESSION (unsupported by MHD)\n",
              _MHD_MD5_TOKEN, _MHD_SESS_TOKEN);
      fclose(logf);
    }
    return MHD_DIGEST_AUTH_ALGO3_MD5_SESSION;
  }
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA256_TOKEN _MHD_SESS_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len))
  {
    if (logf)
    {
      fprintf(logf, "Matched token (unquoted): %s%s -> MHD_DIGEST_AUTH_ALGO3_SHA256_SESSION (unsupported by MHD)\n",
              _MHD_SHA256_TOKEN, _MHD_SESS_TOKEN);
      fclose(logf);
    }
    return MHD_DIGEST_AUTH_ALGO3_SHA256_SESSION;
  }
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA512_256_TOKEN _MHD_SESS_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len))
  {
    if (logf)
    {
      fprintf(logf, "Matched token (unquoted): %s%s -> MHD_DIGEST_AUTH_ALGO3_SHA512_256_SESSION (unsupported by MHD)\n",
              _MHD_SHA512_256_TOKEN, _MHD_SESS_TOKEN);
      fclose(logf);
    }
    return MHD_DIGEST_AUTH_ALGO3_SHA512_256_SESSION;
  }

  /* No known algorithm has been detected */
  if (logf)
  {
    fprintf(logf, "No known unquoted algorithm detected -> returning MHD_DIGEST_AUTH_ALGO3_INVALID\n");
    fclose(logf);
  }
  return MHD_DIGEST_AUTH_ALGO3_INVALID;
}
