static enum MHD_DigestAuthAlgo3
get_rq_dauth_algo (const struct MHD_RqDAuthParam *const algo_param)
{
  /* Open log file for appending control-flow and state information. */
  FILE *log = fopen("/tmp/logs/1175.txt", "a");
  if (log)
  {
    fprintf(log, "get_rq_dauth_algo ENTER: algo_param=%p\n", (const void *)algo_param);
    fflush(log);
  }

  /* Capture local copies of the members we will inspect to avoid
     repeatedly dereferencing the parameter in the logging code.  This
     preserves original evaluation while allowing us to record state. */
  const char *valstr = NULL;
  size_t val_len = 0u;
  int quoted = 0;

  if (algo_param)
  {
    valstr = algo_param->value.str;
    val_len = algo_param->value.len;
    quoted = (algo_param->quoted) ? 1 : 0;
    if (log)
    {
      fprintf(log, "state: value.str=%p, value.len=%zu, quoted=%d\n",
              (const void *)valstr, val_len, quoted);
      fflush(log);
    }
  }

  if (NULL == valstr)
  {
    if (log)
    {
      fprintf(log, "branch: value.str == NULL -> return MHD_DIGEST_AUTH_ALGO3_MD5 (default)\n");
      fclose(log);
    }
    return MHD_DIGEST_AUTH_ALGO3_MD5; /* Assume MD5 by default */
  }

  if (quoted)
  {
    if (log)
      fprintf(log, "branch: quoted == true\n");

    if (MHD_str_equal_caseless_quoted_s_bin_n (valstr, val_len, _MHD_MD5_TOKEN))
    {
      if (log)
      {
        fprintf(log, "match: _MHD_MD5_TOKEN -> MHD_DIGEST_AUTH_ALGO3_MD5\n");
        fclose(log);
      }
      return MHD_DIGEST_AUTH_ALGO3_MD5;
    }
    if (MHD_str_equal_caseless_quoted_s_bin_n (valstr, val_len, _MHD_SHA256_TOKEN))
    {
      if (log)
      {
        fprintf(log, "match: _MHD_SHA256_TOKEN -> MHD_DIGEST_AUTH_ALGO3_SHA256\n");
        fclose(log);
      }
      return MHD_DIGEST_AUTH_ALGO3_SHA256;
    }
    if (MHD_str_equal_caseless_quoted_s_bin_n (valstr, val_len, _MHD_MD5_TOKEN _MHD_SESS_TOKEN))
    {
      if (log)
      {
        fprintf(log, "match: _MHD_MD5_TOKEN+_MHD_SESS_TOKEN -> MHD_DIGEST_AUTH_ALGO3_SHA512_256\n");
        fclose(log);
      }
      return MHD_DIGEST_AUTH_ALGO3_SHA512_256;
    }
    if (MHD_str_equal_caseless_quoted_s_bin_n (valstr, val_len, _MHD_SHA512_256_TOKEN _MHD_SESS_TOKEN))
    {
      /* Algorithms below are not supported by MHD for authentication */
      if (log)
      {
        fprintf(log, "match: _MHD_SHA512_256_TOKEN+_MHD_SESS_TOKEN -> MHD_DIGEST_AUTH_ALGO3_MD5_SESSION\n");
        fclose(log);
      }

      return MHD_DIGEST_AUTH_ALGO3_MD5_SESSION;
    }
    if (MHD_str_equal_caseless_quoted_s_bin_n (valstr, val_len, _MHD_SHA256_TOKEN _MHD_SESS_TOKEN))
    {
      if (log)
      {
        fprintf(log, "match: _MHD_SHA256_TOKEN+_MHD_SESS_TOKEN -> MHD_DIGEST_AUTH_ALGO3_SHA256_SESSION\n");
        fclose(log);
      }
      return MHD_DIGEST_AUTH_ALGO3_SHA256_SESSION;
    }
    if (MHD_str_equal_caseless_quoted_s_bin_n (valstr, val_len, _MHD_SHA512_256_TOKEN))
    {
      if (log)
      {
        fprintf(log, "match: _MHD_SHA512_256_TOKEN -> MHD_DIGEST_AUTH_ALGO3_SHA512_256_SESSION\n");
        fclose(log);
      }
      return MHD_DIGEST_AUTH_ALGO3_SHA512_256_SESSION;
    }

    /* No known algorithm has been detected */
    if (log)
    {
      fprintf(log, "no match in quoted -> return MHD_DIGEST_AUTH_ALGO3_INVALID\n");
      fclose(log);
    }
    return MHD_DIGEST_AUTH_ALGO3_INVALID;
  }

  /* The algorithm value is not quoted */
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_MD5_TOKEN, valstr, val_len))
  {
    if (log)
    {
      fprintf(log, "match (non-quoted): _MHD_MD5_TOKEN -> MHD_DIGEST_AUTH_ALGO3_MD5\n");
      fclose(log);
    }
    return MHD_DIGEST_AUTH_ALGO3_MD5;
  }
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA256_TOKEN, valstr, val_len))
  {
    if (log)
    {
      fprintf(log, "match (non-quoted): _MHD_SHA256_TOKEN -> MHD_DIGEST_AUTH_ALGO3_SHA256\n");
      fclose(log);
    }
    return MHD_DIGEST_AUTH_ALGO3_SHA256;
  }
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA512_256_TOKEN, valstr, val_len))
  {
    if (log)
    {
      fprintf(log, "match (non-quoted): _MHD_SHA512_256_TOKEN -> MHD_DIGEST_AUTH_ALGO3_SHA512_256\n");
      fclose(log);
    }
    return MHD_DIGEST_AUTH_ALGO3_SHA512_256;
  }

  /* Algorithms below are not supported by MHD for authentication */
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_MD5_TOKEN _MHD_SESS_TOKEN, valstr, val_len))
  {
    if (log)
    {
      fprintf(log, "match (non-quoted): _MHD_MD5_TOKEN+_MHD_SESS_TOKEN -> MHD_DIGEST_AUTH_ALGO3_MD5_SESSION\n");
      fclose(log);
    }
    return MHD_DIGEST_AUTH_ALGO3_MD5_SESSION;
  }
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA256_TOKEN _MHD_SESS_TOKEN, valstr, val_len))
  {
    if (log)
    {
      fprintf(log, "match (non-quoted): _MHD_SHA256_TOKEN+_MHD_SESS_TOKEN -> MHD_DIGEST_AUTH_ALGO3_SHA256_SESSION\n");
      fclose(log);
    }
    return MHD_DIGEST_AUTH_ALGO3_SHA256_SESSION;
  }
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA512_256_TOKEN _MHD_SESS_TOKEN, valstr, val_len))
  {
    if (log)
    {
      fprintf(log, "match (non-quoted): _MHD_SHA512_256_TOKEN+_MHD_SESS_TOKEN -> MHD_DIGEST_AUTH_ALGO3_SHA512_256_SESSION\n");
      fclose(log);
    }
    return MHD_DIGEST_AUTH_ALGO3_SHA512_256_SESSION;
  }

  /* No known algorithm has been detected */
  if (log)
  {
    fprintf(log, "no match (non-quoted) -> return MHD_DIGEST_AUTH_ALGO3_INVALID\n");
    fclose(log);
  }
  return MHD_DIGEST_AUTH_ALGO3_INVALID;
}
