static enum MHD_DigestAuthAlgo3
get_rq_dauth_algo (const struct MHD_RqDAuthParam *const algo_param)
{
  FILE *f = fopen("/tmp/logs/1175.txt", "a");
  /* Log entry and basic state */
  if (f)
  {
    fprintf(f, "ENTER get_rq_dauth_algo: algo_param=%p\n", (const void *)algo_param);
    if (algo_param == NULL)
    {
      fprintf(f, "  algo_param is NULL\n");
      fclose(f);
    }
    else
    {
      fprintf(f, "  quoted=%d, value.len=%u, value.str=%p\n",
              (int)algo_param->quoted,
              (unsigned int)algo_param->value.len,
              (const void *)algo_param->value.str);
      if (algo_param->value.str != NULL)
      {
        /* Print first up to 64 bytes as string (using length) and hex for evidence */
        unsigned int prlen = (unsigned int)algo_param->value.len;
        if (prlen > 64u) prlen = 64u;
        fprintf(f, "  value.str (up to %u bytes as text): '%.*s'\n", prlen, (int)prlen, algo_param->value.str);
        fprintf(f, "  value.str (up to %u bytes in hex):", prlen);
        for (unsigned int i = 0u; i < prlen; ++i)
          fprintf(f, " %02x", (unsigned char)algo_param->value.str[i]);
        fprintf(f, "\n");
      }
    }
  }

  if (NULL == algo_param->value.str)
  {
    if (f) { fprintf(f, "BRANCH: value.str == NULL -> return MD5 (default)\n"); fclose(f); }
    return MHD_DIGEST_AUTH_ALGO3_MD5; /* Assume MD5 by default */
  }

  if (algo_param->quoted)
  {
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_MD5_TOKEN))
    {
      if (f) { fprintf(f, "BRANCH: quoted && matches _MHD_MD5_TOKEN -> MD5\n"); fclose(f); }
      return MHD_DIGEST_AUTH_ALGO3_MD5;
    }
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_SHA256_TOKEN))
    {
      if (f) { fprintf(f, "BRANCH: quoted && matches _MHD_SHA256_TOKEN -> SHA256\n"); fclose(f); }
      return MHD_DIGEST_AUTH_ALGO3_SHA256;
    }
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_MD5_TOKEN _MHD_SESS_TOKEN))
    {
      if (f) { fprintf(f, "BRANCH: quoted && matches MD5_SESS token -> SHA512_256\n"); fclose(f); }
      return MHD_DIGEST_AUTH_ALGO3_SHA512_256;
    }
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_SHA512_256_TOKEN
                                               _MHD_SESS_TOKEN))

      /* Algorithms below are not supported by MHD for authentication */

      if (f) { fprintf(f, "BRANCH: quoted && matches SHA512_256 + SESS -> MD5_SESSION (not supported)\n"); fclose(f); }
      return MHD_DIGEST_AUTH_ALGO3_MD5_SESSION;
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_SHA256_TOKEN
                                               _MHD_SESS_TOKEN))
    {
      if (f) { fprintf(f, "BRANCH: quoted && matches SHA256 + SESS -> SHA256_SESSION (not supported)\n"); fclose(f); }
      return MHD_DIGEST_AUTH_ALGO3_SHA256_SESSION;
    }
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_SHA512_256_TOKEN))
    {
      if (f) { fprintf(f, "BRANCH: quoted && matches SHA512_256_TOKEN -> SHA512_256_SESSION (not supported)\n"); fclose(f); }
      return MHD_DIGEST_AUTH_ALGO3_SHA512_256_SESSION;
    }

    /* No known algorithm has been detected */
    if (f) { fprintf(f, "BRANCH: quoted && no match -> INVALID\n"); fclose(f); }
    return MHD_DIGEST_AUTH_ALGO3_INVALID;
  }
  /* The algorithm value is not quoted */
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_MD5_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len))
  {
    if (f) { fprintf(f, "BRANCH: unquoted && matches _MHD_MD5_TOKEN -> MD5\n"); fclose(f); }
    return MHD_DIGEST_AUTH_ALGO3_MD5;
  }
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA256_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len))
  {
    if (f) { fprintf(f, "BRANCH: unquoted && matches _MHD_SHA256_TOKEN -> SHA256\n"); fclose(f); }
    return MHD_DIGEST_AUTH_ALGO3_SHA256;
  }
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA512_256_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len))
  {
    if (f) { fprintf(f, "BRANCH: unquoted && matches _MHD_SHA512_256_TOKEN -> SHA512_256\n"); fclose(f); }
    return MHD_DIGEST_AUTH_ALGO3_SHA512_256;
  }

  /* Algorithms below are not supported by MHD for authentication */

  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_MD5_TOKEN _MHD_SESS_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len))
  {
    if (f) { fprintf(f, "BRANCH: unquoted && matches MD5+SESS -> MD5_SESSION (not supported)\n"); fclose(f); }
    return MHD_DIGEST_AUTH_ALGO3_MD5_SESSION;
  }
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA256_TOKEN _MHD_SESS_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len))
  {
    if (f) { fprintf(f, "BRANCH: unquoted && matches SHA256+SESS -> SHA256_SESSION (not supported)\n"); fclose(f); }
    return MHD_DIGEST_AUTH_ALGO3_SHA256_SESSION;
  }
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA512_256_TOKEN _MHD_SESS_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len))
  {
    if (f) { fprintf(f, "BRANCH: unquoted && matches SHA512_256+SESS -> SHA512_256_SESSION (not supported)\n"); fclose(f); }
    return MHD_DIGEST_AUTH_ALGO3_SHA512_256_SESSION;
  }

  /* No known algorithm has been detected */
  if (f) { fprintf(f, "BRANCH: unquoted && no match -> INVALID\n"); fclose(f); }
  return MHD_DIGEST_AUTH_ALGO3_INVALID;
}
