static enum MHD_DigestAuthAlgo3
get_rq_dauth_algo (const struct MHD_RqDAuthParam *const algo_param)
{
  /* Logging probe: record entry and parameter state */
  {
    FILE *log = fopen("/tmp/logs/1175.txt", "a");
    if (log)
    {
      fprintf(log, "ENTER get_rq_dauth_algo: algo_param=%p\n", (const void *)algo_param);
      if (algo_param)
      {
        fprintf(log, "  value.str=%p, value.len=%zu, quoted=%d\n",
                (const void *)algo_param->value.str,
                (size_t)algo_param->value.len,
                (int)algo_param->quoted);
        if (NULL != algo_param->value.str)
        {
          /* Print a short preview of the string (not assuming NUL-termination) */
          size_t preview_len = algo_param->value.len > 32 ? 32 : algo_param->value.len;
          if (preview_len > 0)
            fprintf(log, "  value.str (preview upto %zu): '%.*s'\n",
                    preview_len, (int)preview_len, algo_param->value.str);
        }
      }
      fclose(log);
    }
  }

  if (NULL == algo_param->value.str)
  {
    FILE *log = fopen("/tmp/logs/1175.txt", "a");
    if (log)
    {
      fprintf(log, "BRANCH: value.str == NULL -> return MHD_DIGEST_AUTH_ALGO3_MD5 (default)\n");
      fclose(log);
    }
    return MHD_DIGEST_AUTH_ALGO3_MD5; /* Assume MD5 by default */
  }

  if (algo_param->quoted)
  {
    int cmp;

    cmp = MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                                 algo_param->value.len,
                                                 _MHD_MD5_TOKEN);
    {
      FILE *log = fopen("/tmp/logs/1175.txt", "a");
      if (log)
      {
        fprintf(log, "QUOTE branch: compare to _MHD_MD5_TOKEN -> %d\n", cmp);
        fclose(log);
      }
    }
    if (cmp)
      return MHD_DIGEST_AUTH_ALGO3_MD5;

    cmp = MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                                 algo_param->value.len,
                                                 _MHD_SHA256_TOKEN);
    {
      FILE *log = fopen("/tmp/logs/1175.txt", "a");
      if (log)
      {
        fprintf(log, "QUOTE branch: compare to _MHD_SHA256_TOKEN -> %d\n", cmp);
        fclose(log);
      }
    }
    if (cmp)
      return MHD_DIGEST_AUTH_ALGO3_SHA256;

    cmp = MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                                 algo_param->value.len,
                                                 _MHD_MD5_TOKEN _MHD_SESS_TOKEN);
    {
      FILE *log = fopen("/tmp/logs/1175.txt", "a");
      if (log)
      {
        fprintf(log, "QUOTE branch: compare to _MHD_MD5_TOKEN _MHD_SESS_TOKEN -> %d\n", cmp);
        fclose(log);
      }
    }
    if (cmp)
      return MHD_DIGEST_AUTH_ALGO3_SHA512_256;

    cmp = MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                                 algo_param->value.len,
                                                 _MHD_SHA512_256_TOKEN
                                                 _MHD_SESS_TOKEN);
    {
      FILE *log = fopen("/tmp/logs/1175.txt", "a");
      if (log)
      {
        fprintf(log, "QUOTE branch: compare to _MHD_SHA512_256_TOKEN _MHD_SESS_TOKEN -> %d\n", cmp);
        fclose(log);
      }
    }

    /* Algorithms below are not supported by MHD for authentication */

    if (cmp)
      return MHD_DIGEST_AUTH_ALGO3_MD5_SESSION;

    cmp = MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                                 algo_param->value.len,
                                                 _MHD_SHA256_TOKEN
                                                 _MHD_SESS_TOKEN);
    {
      FILE *log = fopen("/tmp/logs/1175.txt", "a");
      if (log)
      {
        fprintf(log, "QUOTE branch: compare to _MHD_SHA256_TOKEN _MHD_SESS_TOKEN -> %d\n", cmp);
        fclose(log);
      }
    }
    if (cmp)
      return MHD_DIGEST_AUTH_ALGO3_SHA256_SESSION;

    cmp = MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                                 algo_param->value.len,
                                                 _MHD_SHA512_256_TOKEN);
    {
      FILE *log = fopen("/tmp/logs/1175.txt", "a");
      if (log)
      {
        fprintf(log, "QUOTE branch: compare to _MHD_SHA512_256_TOKEN -> %d\n", cmp);
        fclose(log);
      }
    }
    if (cmp)
      return MHD_DIGEST_AUTH_ALGO3_SHA512_256_SESSION;

    /* No known algorithm has been detected */
    {
      FILE *log = fopen("/tmp/logs/1175.txt", "a");
      if (log)
      {
        fprintf(log, "QUOTE branch: no known algorithm -> return MHD_DIGEST_AUTH_ALGO3_INVALID\n");
        fclose(log);
      }
    }
    return MHD_DIGEST_AUTH_ALGO3_INVALID;
  }

  /* The algorithm value is not quoted */
  {
    int cmp;

    cmp = MHD_str_equal_caseless_s_bin_n_ (_MHD_MD5_TOKEN,
                                           algo_param->value.str,
                                           algo_param->value.len);
    {
      FILE *log = fopen("/tmp/logs/1175.txt", "a");
      if (log)
      {
        fprintf(log, "NON-QUOTE branch: compare to _MHD_MD5_TOKEN -> %d\n", cmp);
        fclose(log);
      }
    }
    if (cmp)
      return MHD_DIGEST_AUTH_ALGO3_MD5;

    cmp = MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA256_TOKEN,
                                           algo_param->value.str,
                                           algo_param->value.len);
    {
      FILE *log = fopen("/tmp/logs/1175.txt", "a");
      if (log)
      {
        fprintf(log, "NON-QUOTE branch: compare to _MHD_SHA256_TOKEN -> %d\n", cmp);
        fclose(log);
      }
    }
    if (cmp)
      return MHD_DIGEST_AUTH_ALGO3_SHA256;

    cmp = MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA512_256_TOKEN,
                                           algo_param->value.str,
                                           algo_param->value.len);
    {
      FILE *log = fopen("/tmp/logs/1175.txt", "a");
      if (log)
      {
        fprintf(log, "NON-QUOTE branch: compare to _MHD_SHA512_256_TOKEN -> %d\n", cmp);
        fclose(log);
      }
    }
    if (cmp)
      return MHD_DIGEST_AUTH_ALGO3_SHA512_256;

    /* Algorithms below are not supported by MHD for authentication */

    cmp = MHD_str_equal_caseless_s_bin_n_ (_MHD_MD5_TOKEN _MHD_SESS_TOKEN,
                                           algo_param->value.str,
                                           algo_param->value.len);
    {
      FILE *log = fopen("/tmp/logs/1175.txt", "a");
      if (log)
      {
        fprintf(log, "NON-QUOTE branch: compare to _MHD_MD5_TOKEN _MHD_SESS_TOKEN -> %d\n", cmp);
        fclose(log);
      }
    }
    if (cmp)
      return MHD_DIGEST_AUTH_ALGO3_MD5_SESSION;

    cmp = MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA256_TOKEN _MHD_SESS_TOKEN,
                                           algo_param->value.str,
                                           algo_param->value.len);
    {
      FILE *log = fopen("/tmp/logs/1175.txt", "a");
      if (log)
      {
        fprintf(log, "NON-QUOTE branch: compare to _MHD_SHA256_TOKEN _MHD_SESS_TOKEN -> %d\n", cmp);
        fclose(log);
      }
    }
    if (cmp)
      return MHD_DIGEST_AUTH_ALGO3_SHA256_SESSION;

    cmp = MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA512_256_TOKEN _MHD_SESS_TOKEN,
                                           algo_param->value.str,
                                           algo_param->value.len);
    {
      FILE *log = fopen("/tmp/logs/1175.txt", "a");
      if (log)
      {
        fprintf(log, "NON-QUOTE branch: compare to _MHD_SHA512_256_TOKEN _MHD_SESS_TOKEN -> %d\n", cmp);
        fclose(log);
      }
    }
    if (cmp)
      return MHD_DIGEST_AUTH_ALGO3_SHA512_256_SESSION;

    /* No known algorithm has been detected */
    {
      FILE *log = fopen("/tmp/logs/1175.txt", "a");
      if (log)
      {
        fprintf(log, "NON-QUOTE branch: no known algorithm -> return MHD_DIGEST_AUTH_ALGO3_INVALID\n");
        fclose(log);
      }
    }
    return MHD_DIGEST_AUTH_ALGO3_INVALID;
  }
}
