static enum MHD_DigestAuthAlgo3
get_rq_dauth_algo (const struct MHD_RqDAuthParam *const algo_param)
{
  enum MHD_DigestAuthAlgo3 ret = MHD_DIGEST_AUTH_ALGO3_INVALID;
  FILE *log = fopen("/tmp/logs/1175.txt", "a");
  if (log)
  {
    fprintf(log, "ENTER get_rq_dauth_algo: algo_param=%p\n", (void *)algo_param);
    if (algo_param)
    {
      fprintf(log, "  quoted=%d, value.str=%p, value.len=%lu\n",
              (int)algo_param->quoted,
              (void *)algo_param->value.str,
              (unsigned long)algo_param->value.len);
      if (algo_param->value.str)
      {
        /* Log up to 64 bytes of the provided string value for diagnostics */
        int dump_len = (int)((algo_param->value.len > 64) ? 64 : (unsigned long)algo_param->value.len);
        fprintf(log, "  value.preview=\"");
        if (dump_len > 0)
          fwrite(algo_param->value.str, 1, dump_len, log);
        if (algo_param->value.len > (unsigned long)dump_len)
          fprintf(log, "... (len=%lu)", (unsigned long)algo_param->value.len);
        fprintf(log, "\"\n");
      }
    }
    fflush(log);
  }

  /* Preserve original logic but collect boolean results in temporaries so we can log them */
  if (NULL == algo_param->value.str)
  {
    if (log) fprintf(log, "BRANCH: value.str is NULL -> assume MD5\n"), fflush(log);
    ret = MHD_DIGEST_AUTH_ALGO3_MD5;
    goto done;
  }

  if (algo_param->quoted)
  {
    int match_md5 = MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str, algo_param->value.len, _MHD_MD5_TOKEN);
    if (log) fprintf(log, "CHECK quoted MD5 -> %d\n", match_md5), fflush(log);
    if (match_md5)
    {
      ret = MHD_DIGEST_AUTH_ALGO3_MD5;
      goto done;
    }

    int match_sha256 = MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str, algo_param->value.len, _MHD_SHA256_TOKEN);
    if (log) fprintf(log, "CHECK quoted SHA256 -> %d\n", match_sha256), fflush(log);
    if (match_sha256)
    {
      ret = MHD_DIGEST_AUTH_ALGO3_SHA256;
      goto done;
    }

    int match_sha512_256 = MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str, algo_param->value.len, _MHD_MD5_TOKEN _MHD_SESS_TOKEN);
    if (log) fprintf(log, "CHECK quoted SHA512_256 (MD5+SESS token) -> %d\n", match_sha512_256), fflush(log);
    if (match_sha512_256)
    {
      ret = MHD_DIGEST_AUTH_ALGO3_SHA512_256;
      goto done;
    }

    int match_sha512_256_sess = MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str, algo_param->value.len, _MHD_SHA512_256_TOKEN _MHD_SESS_TOKEN);
    if (log) fprintf(log, "CHECK quoted SHA512_256+SESS -> %d\n", match_sha512_256_sess), fflush(log);
    /* Algorithms below are not supported by MHD for authentication */
    if (match_sha512_256_sess)
    {
      ret = MHD_DIGEST_AUTH_ALGO3_MD5_SESSION;
      goto done;
    }

    int match_sha256_sess = MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str, algo_param->value.len, _MHD_SHA256_TOKEN _MHD_SESS_TOKEN);
    if (log) fprintf(log, "CHECK quoted SHA256+SESS -> %d\n", match_sha256_sess), fflush(log);
    if (match_sha256_sess)
    {
      ret = MHD_DIGEST_AUTH_ALGO3_SHA256_SESSION;
      goto done;
    }

    int match_sha512_256_session = MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str, algo_param->value.len, _MHD_SHA512_256_TOKEN);
    if (log) fprintf(log, "CHECK quoted SHA512_256_SESSION -> %d\n", match_sha512_256_session), fflush(log);
    if (match_sha512_256_session)
    {
      ret = MHD_DIGEST_AUTH_ALGO3_SHA512_256_SESSION;
      goto done;
    }

    /* No known algorithm has been detected */
    if (log) fprintf(log, "BRANCH: quoted but no known algorithm -> INVALID\n"), fflush(log);
    ret = MHD_DIGEST_AUTH_ALGO3_INVALID;
    goto done;
  }

  /* The algorithm value is not quoted */
  {
    int match_md5 = MHD_str_equal_caseless_s_bin_n_ (_MHD_MD5_TOKEN, algo_param->value.str, algo_param->value.len);
    if (log) fprintf(log, "CHECK unquoted MD5 -> %d\n", match_md5), fflush(log);
    if (match_md5)
    {
      ret = MHD_DIGEST_AUTH_ALGO3_MD5;
      goto done;
    }

    int match_sha256 = MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA256_TOKEN, algo_param->value.str, algo_param->value.len);
    if (log) fprintf(log, "CHECK unquoted SHA256 -> %d\n", match_sha256), fflush(log);
    if (match_sha256)
    {
      ret = MHD_DIGEST_AUTH_ALGO3_SHA256;
      goto done;
    }

    int match_sha512_256 = MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA512_256_TOKEN, algo_param->value.str, algo_param->value.len);
    if (log) fprintf(log, "CHECK unquoted SHA512_256 -> %d\n", match_sha512_256), fflush(log);
    if (match_sha512_256)
    {
      ret = MHD_DIGEST_AUTH_ALGO3_SHA512_256;
      goto done;
    }

    /* Algorithms below are not supported by MHD for authentication */
    int match_md5_sess = MHD_str_equal_caseless_s_bin_n_ (_MHD_MD5_TOKEN _MHD_SESS_TOKEN, algo_param->value.str, algo_param->value.len);
    if (log) fprintf(log, "CHECK unquoted MD5+SESS -> %d\n", match_md5_sess), fflush(log);
    if (match_md5_sess)
    {
      ret = MHD_DIGEST_AUTH_ALGO3_MD5_SESSION;
      goto done;
    }

    int match_sha256_sess = MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA256_TOKEN _MHD_SESS_TOKEN, algo_param->value.str, algo_param->value.len);
    if (log) fprintf(log, "CHECK unquoted SHA256+SESS -> %d\n", match_sha256_sess), fflush(log);
    if (match_sha256_sess)
    {
      ret = MHD_DIGEST_AUTH_ALGO3_SHA256_SESSION;
      goto done;
    }

    int match_sha512_256_sess = MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA512_256_TOKEN _MHD_SESS_TOKEN, algo_param->value.str, algo_param->value.len);
    if (log) fprintf(log, "CHECK unquoted SHA512_256+SESS -> %d\n", match_sha512_256_sess), fflush(log);
    if (match_sha512_256_sess)
    {
      ret = MHD_DIGEST_AUTH_ALGO3_SHA512_256_SESSION;
      goto done;
    }
  }

  /* No known algorithm has been detected */
  if (log) fprintf(log, "BRANCH: unquoted but no known algorithm -> INVALID\n"), fflush(log);
  ret = MHD_DIGEST_AUTH_ALGO3_INVALID;

done:
  if (log)
  {
    fprintf(log, "EXIT get_rq_dauth_algo: returning %d\n", (int)ret);
    fclose(log);
  }
  return ret;
}
