static enum MHD_DigestAuthAlgo3
get_rq_dauth_algo (const struct MHD_RqDAuthParam *const algo_param)
{
  FILE *log = fopen("/tmp/logs/1175.txt", "a");
  if (log)
  {
    fprintf(log, "ENTER get_rq_dauth_algo: algo_param=%p\n", (void *) algo_param);
    if (NULL == algo_param)
    {
      fprintf(log, "algo_param is NULL\n");
      fclose(log);
      /* Preserve original behavior: but original never expected NULL algoparam; return invalid */
      return MHD_DIGEST_AUTH_ALGO3_INVALID;
    }
    fprintf(log, "algo_param->value.str=%p algo_param->value.len=%zu quoted=%d\n",
            (void *) algo_param->value.str,
            (size_t) algo_param->value.len,
            algo_param->quoted ? 1 : 0);

    if (NULL != algo_param->value.str)
    {
      /* Dump up to 256 bytes of the string (printable chars, escape others) */
      size_t len = (size_t) algo_param->value.len;
      size_t dump_len = len > 256 ? 256 : len;
      fprintf(log, "value.len=%zu dump_len=%zu value.dump=\"", len, dump_len);
      for (size_t i = 0; i < dump_len; ++i)
      {
        unsigned char c = (unsigned char) algo_param->value.str[i];
        if (c >= 32 && c < 127)
          fputc(c, log);
        else
          fprintf(log, "\\x%02x", c);
      }
      if (len > dump_len)
        fprintf(log, "...");
      fprintf(log, "\"\n");
    }
    fflush(log);
  }

  if (NULL == algo_param->value.str)
  {
    if (log) fprintf(log, "decision: value.str is NULL -> return MD5 (default)\n"), fclose(log);
    return MHD_DIGEST_AUTH_ALGO3_MD5; /* Assume MD5 by default */
  }

  if (algo_param->quoted)
  {
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_MD5_TOKEN))
    {
      if (log) fprintf(log, "decision: quoted and matched _MHD_MD5_TOKEN -> MD5\n"), fclose(log);
      return MHD_DIGEST_AUTH_ALGO3_MD5;
    }
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_SHA256_TOKEN))
    {
      if (log) fprintf(log, "decision: quoted and matched _MHD_SHA256_TOKEN -> SHA256\n"), fclose(log);
      return MHD_DIGEST_AUTH_ALGO3_SHA256;
    }
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_MD5_TOKEN _MHD_SESS_TOKEN))
    {
      if (log) fprintf(log, "decision: quoted and matched _MHD_MD5_TOKEN + _MHD_SESS_TOKEN -> SHA512_256\n"), fclose(log);
      return MHD_DIGEST_AUTH_ALGO3_SHA512_256;
    }
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_SHA512_256_TOKEN
                                               _MHD_SESS_TOKEN))
    {
      /* Algorithms below are not supported by MHD for authentication */
      if (log) fprintf(log, "decision: quoted and matched _MHD_SHA512_256_TOKEN + _MHD_SESS_TOKEN -> MD5_SESSION (not supported)\n"), fclose(log);
      return MHD_DIGEST_AUTH_ALGO3_MD5_SESSION;
    }
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_SHA256_TOKEN
                                               _MHD_SESS_TOKEN))
    {
      if (log) fprintf(log, "decision: quoted and matched _MHD_SHA256_TOKEN + _MHD_SESS_TOKEN -> SHA256_SESSION\n"), fclose(log);
      return MHD_DIGEST_AUTH_ALGO3_SHA256_SESSION;
    }
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_SHA512_256_TOKEN))
    {
      if (log) fprintf(log, "decision: quoted and matched _MHD_SHA512_256_TOKEN -> SHA512_256_SESSION\n"), fclose(log);
      return MHD_DIGEST_AUTH_ALGO3_SHA512_256_SESSION;
    }

    /* No known algorithm has been detected */
    if (log) fprintf(log, "decision: quoted but no known algorithm -> INVALID\n"), fclose(log);
    return MHD_DIGEST_AUTH_ALGO3_INVALID;
  }

  /* The algorithm value is not quoted */
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_MD5_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len))
  {
    if (log) fprintf(log, "decision: unquoted matched _MHD_MD5_TOKEN -> MD5\n"), fclose(log);
    return MHD_DIGEST_AUTH_ALGO3_MD5;
  }
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA256_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len))
  {
    if (log) fprintf(log, "decision: unquoted matched _MHD_SHA256_TOKEN -> SHA256\n"), fclose(log);
    return MHD_DIGEST_AUTH_ALGO3_SHA256;
  }
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA512_256_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len))
  {
    if (log) fprintf(log, "decision: unquoted matched _MHD_SHA512_256_TOKEN -> SHA512_256\n"), fclose(log);
    return MHD_DIGEST_AUTH_ALGO3_SHA512_256;
  }

  /* Algorithms below are not supported by MHD for authentication */

  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_MD5_TOKEN _MHD_SESS_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len))
  {
    if (log) fprintf(log, "decision: unquoted matched MD5 + sess -> MD5_SESSION\n"), fclose(log);
    return MHD_DIGEST_AUTH_ALGO3_MD5_SESSION;
  }
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA256_TOKEN _MHD_SESS_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len))
  {
    if (log) fprintf(log, "decision: unquoted matched SHA256 + sess -> SHA256_SESSION\n"), fclose(log);
    return MHD_DIGEST_AUTH_ALGO3_SHA256_SESSION;
  }
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA512_256_TOKEN _MHD_SESS_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len))
  {
    if (log) fprintf(log, "decision: unquoted matched SHA512_256 + sess -> SHA512_256_SESSION\n"), fclose(log);
    return MHD_DIGEST_AUTH_ALGO3_SHA512_256_SESSION;
  }

  /* No known algorithm has been detected */
  if (log) fprintf(log, "decision: unquoted but no known algorithm -> INVALID\n"), fclose(log);
  return MHD_DIGEST_AUTH_ALGO3_INVALID;
}
