static enum MHD_DigestAuthAlgo3
get_rq_dauth_algo (const struct MHD_RqDAuthParam *const algo_param)
{
  /* Log entry and parameter state for debugging the Rule 8.13 violation scenario. */
  {
    FILE *f = fopen("/tmp/logs/1175.txt", "a");
    if (f)
    {
      fprintf(f, "get_rq_dauth_algo: entered, algo_param=%p\n", (void *)algo_param);
      if (algo_param != NULL)
      {
        fprintf(f, "  quoted=%d, value.str=%p, value.len=%zu\n",
                (int)algo_param->quoted,
                (void *)algo_param->value.str,
                (size_t)algo_param->value.len);
        if (algo_param->value.str != NULL)
        {
          /* Print up to 128 bytes of the value (printable characters or hex escapes) */
          size_t i, n = algo_param->value.len;
          if (n > 128U) n = 128U;
          fprintf(f, "  value (len=%zu): '", (size_t)algo_param->value.len);
          for (i = 0U; i < n; ++i)
          {
            unsigned char c = (unsigned char)algo_param->value.str[i];
            if (c >= 0x20U && c < 0x7FU && c != '\' && c != '\'')
            {
              fputc((int)c, f);
            }
            else
            {
              fprintf(f, "\\x%02x", (unsigned)c);
            }
          }
          if (algo_param->value.len > n)
            fprintf(f, "...(truncated)");
          fprintf(f, "'\n");
        }
        else
        {
          fprintf(f, "  value.str is NULL\n");
        }
      }
      fclose(f);
    }
  }

  if (NULL == algo_param->value.str)
  {
    FILE *f = fopen("/tmp/logs/1175.txt", "a");
    if (f)
    {
      fprintf(f, "  branch: value.str == NULL -> returning MHD_DIGEST_AUTH_ALGO3_MD5 (default)\n");
      fclose(f);
    }
    return MHD_DIGEST_AUTH_ALGO3_MD5; /* Assume MD5 by default */
  }

  if (algo_param->quoted)
  {
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_MD5_TOKEN))
    {
      FILE *f = fopen("/tmp/logs/1175.txt", "a");
      if (f) { fprintf(f, "  matched quoted token: MD5\n"); fclose(f); }
      return MHD_DIGEST_AUTH_ALGO3_MD5;
    }
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_SHA256_TOKEN))
    {
      FILE *f = fopen("/tmp/logs/1175.txt", "a");
      if (f) { fprintf(f, "  matched quoted token: SHA256\n"); fclose(f); }
      return MHD_DIGEST_AUTH_ALGO3_SHA256;
    }
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_MD5_TOKEN _MHD_SESS_TOKEN))
    {
      FILE *f = fopen("/tmp/logs/1175.txt", "a");
      if (f) { fprintf(f, "  matched quoted token: MD5-SESS (treated as SHA512_256 in original code block)\n"); fclose(f); }
      return MHD_DIGEST_AUTH_ALGO3_SHA512_256;
    }
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_SHA512_256_TOKEN
                                               _MHD_SESS_TOKEN))
    {
      /* Algorithms below are not supported by MHD for authentication */
      FILE *f = fopen("/tmp/logs/1175.txt", "a");
      if (f) { fprintf(f, "  matched quoted token: SHA512_256-SESSION -> returning MD5_SESSION (unsupported comment in original)\n"); fclose(f); }

      return MHD_DIGEST_AUTH_ALGO3_MD5_SESSION;
    }
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_SHA256_TOKEN
                                               _MHD_SESS_TOKEN))
    {
      FILE *f = fopen("/tmp/logs/1175.txt", "a");
      if (f) { fprintf(f, "  matched quoted token: SHA256-SESSION\n"); fclose(f); }
      return MHD_DIGEST_AUTH_ALGO3_SHA256_SESSION;
    }
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_SHA512_256_TOKEN))
    {
      FILE *f = fopen("/tmp/logs/1175.txt", "a");
      if (f) { fprintf(f, "  matched quoted token: SHA512_256-SESSION? (no SESS token)\n"); fclose(f); }
      return MHD_DIGEST_AUTH_ALGO3_SHA512_256_SESSION;
    }

    /* No known algorithm has been detected */
    {
      FILE *f = fopen("/tmp/logs/1175.txt", "a");
      if (f) { fprintf(f, "  quoted branch: no known algorithm -> returning INVALID\n"); fclose(f); }
    }
    return MHD_DIGEST_AUTH_ALGO3_INVALID;
  }

  /* The algorithm value is not quoted */
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_MD5_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len))
  {
    FILE *f = fopen("/tmp/logs/1175.txt", "a");
    if (f) { fprintf(f, "  matched non-quoted token: MD5\n"); fclose(f); }
    return MHD_DIGEST_AUTH_ALGO3_MD5;
  }
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA256_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len))
  {
    FILE *f = fopen("/tmp/logs/1175.txt", "a");
    if (f) { fprintf(f, "  matched non-quoted token: SHA256\n"); fclose(f); }
    return MHD_DIGEST_AUTH_ALGO3_SHA256;
  }
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA512_256_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len))
  {
    FILE *f = fopen("/tmp/logs/1175.txt", "a");
    if (f) { fprintf(f, "  matched non-quoted token: SHA512_256\n"); fclose(f); }
    return MHD_DIGEST_AUTH_ALGO3_SHA512_256;
  }

  /* Algorithms below are not supported by MHD for authentication */

  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_MD5_TOKEN _MHD_SESS_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len))
  {
    FILE *f = fopen("/tmp/logs/1175.txt", "a");
    if (f) { fprintf(f, "  matched non-quoted token: MD5-SESSION\n"); fclose(f); }
    return MHD_DIGEST_AUTH_ALGO3_MD5_SESSION;
  }
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA256_TOKEN _MHD_SESS_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len))
  {
    FILE *f = fopen("/tmp/logs/1175.txt", "a");
    if (f) { fprintf(f, "  matched non-quoted token: SHA256-SESSION\n"); fclose(f); }
    return MHD_DIGEST_AUTH_ALGO3_SHA256_SESSION;
  }
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA512_256_TOKEN _MHD_SESS_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len))
  {
    FILE *f = fopen("/tmp/logs/1175.txt", "a");
    if (f) { fprintf(f, "  matched non-quoted token: SHA512_256-SESSION\n"); fclose(f); }
    return MHD_DIGEST_AUTH_ALGO3_SHA512_256_SESSION;
  }

  /* No known algorithm has been detected */
  {
    FILE *f = fopen("/tmp/logs/1175.txt", "a");
    if (f) { fprintf(f, "  non-quoted branch: no known algorithm -> returning INVALID\n"); fclose(f); }
  }
  return MHD_DIGEST_AUTH_ALGO3_INVALID;
}
