static enum MHD_DigestAuthAlgo3
get_rq_dauth_algo (const struct MHD_RqDAuthParam *const algo_param)
{
  FILE *log = fopen("/tmp/logs/1175.txt", "a");
  if (log)
  {
    fprintf(log, "get_rq_dauth_algo: entry algo_param=%p\n", (void *)algo_param);
    if (algo_param)
    {
      fprintf(log, " get_rq_dauth_algo: quoted=%d, value.str=%p, value.len=%zu\n",
              (int)algo_param->quoted,
              (void *)algo_param->value.str,
              (size_t)algo_param->value.len);
      if (algo_param->value.str && algo_param->value.len > 0)
      {
        size_t dump_len = (size_t)algo_param->value.len;
        if (dump_len > 128U) dump_len = 128U;
        fprintf(log, " value.str (first %zu bytes): '", dump_len);
        for (size_t i = 0U; i < dump_len; ++i)
        {
          unsigned char c = (unsigned char)algo_param->value.str[i];
          if (c >= 32U && c < 127U)
            fputc((int)c, log);
          else
            fprintf(log, "\\x%02x", c);
        }
        fprintf(log, "'\n");
      }
    }
  }

  if (NULL == algo_param->value.str)
  {
    if (log)
    {
      fprintf(log, "get_rq_dauth_algo: value.str is NULL -> returning MHD_DIGEST_AUTH_ALGO3_MD5 (default)\n");
      fclose(log);
    }
    return MHD_DIGEST_AUTH_ALGO3_MD5; /* Assume MD5 by default */
  }

  if (algo_param->quoted)
  {
    int r;

    r = MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_MD5_TOKEN);
    if (log) fprintf(log, "quoted compare with _MHD_MD5_TOKEN -> %d\n", r);
    if (r)
    {
      if (log) { fprintf(log, "get_rq_dauth_algo: match -> MHD_DIGEST_AUTH_ALGO3_MD5\n"); fclose(log); }
      return MHD_DIGEST_AUTH_ALGO3_MD5;
    }

    r = MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_SHA256_TOKEN);
    if (log) fprintf(log, "quoted compare with _MHD_SHA256_TOKEN -> %d\n", r);
    if (r)
    {
      if (log) { fprintf(log, "get_rq_dauth_algo: match -> MHD_DIGEST_AUTH_ALGO3_SHA256\n"); fclose(log); }
      return MHD_DIGEST_AUTH_ALGO3_SHA256;
    }

    r = MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_MD5_TOKEN _MHD_SESS_TOKEN);
    if (log) fprintf(log, "quoted compare with _MHD_MD5_TOKEN _MHD_SESS_TOKEN -> %d\n", r);
    if (r)
    {
      if (log) { fprintf(log, "get_rq_dauth_algo: match -> MHD_DIGEST_AUTH_ALGO3_SHA512_256 (note: original mapping preserved)\n"); fclose(log); }
      return MHD_DIGEST_AUTH_ALGO3_SHA512_256;
    }

    r = MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_SHA512_256_TOKEN
                                               _MHD_SESS_TOKEN);
    if (log) fprintf(log, "quoted compare with _MHD_SHA512_256_TOKEN _MHD_SESS_TOKEN -> %d\n", r);

    /* Algorithms below are not supported by MHD for authentication */

    if (r)
    {
      if (log) { fprintf(log, "get_rq_dauth_algo: match -> MHD_DIGEST_AUTH_ALGO3_MD5_SESSION\n"); fclose(log); }
      return MHD_DIGEST_AUTH_ALGO3_MD5_SESSION;
    }

    r = MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_SHA256_TOKEN
                                               _MHD_SESS_TOKEN);
    if (log) fprintf(log, "quoted compare with _MHD_SHA256_TOKEN _MHD_SESS_TOKEN -> %d\n", r);
    if (r)
    {
      if (log) { fprintf(log, "get_rq_dauth_algo: match -> MHD_DIGEST_AUTH_ALGO3_SHA256_SESSION\n"); fclose(log); }
      return MHD_DIGEST_AUTH_ALGO3_SHA256_SESSION;
    }

    r = MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_SHA512_256_TOKEN);
    if (log) fprintf(log, "quoted compare with _MHD_SHA512_256_TOKEN -> %d\n", r);
    if (r)
    {
      if (log) { fprintf(log, "get_rq_dauth_algo: match -> MHD_DIGEST_AUTH_ALGO3_SHA512_256_SESSION\n"); fclose(log); }
      return MHD_DIGEST_AUTH_ALGO3_SHA512_256_SESSION;
    }

    /* No known algorithm has been detected */
    if (log) { fprintf(log, "get_rq_dauth_algo: quoted path -> no known algorithm -> INVALID\n"); fclose(log); }
    return MHD_DIGEST_AUTH_ALGO3_INVALID;
  }

  /* The algorithm value is not quoted */
  {
    int r;

    r = MHD_str_equal_caseless_s_bin_n_ (_MHD_MD5_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len);
    if (log) fprintf(log, "non-quoted compare with _MHD_MD5_TOKEN -> %d\n", r);
    if (r)
    {
      if (log) { fprintf(log, "get_rq_dauth_algo: match -> MHD_DIGEST_AUTH_ALGO3_MD5\n"); fclose(log); }
      return MHD_DIGEST_AUTH_ALGO3_MD5;
    }

    r = MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA256_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len);
    if (log) fprintf(log, "non-quoted compare with _MHD_SHA256_TOKEN -> %d\n", r);
    if (r)
    {
      if (log) { fprintf(log, "get_rq_dauth_algo: match -> MHD_DIGEST_AUTH_ALGO3_SHA256\n"); fclose(log); }
      return MHD_DIGEST_AUTH_ALGO3_SHA256;
    }

    r = MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA512_256_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len);
    if (log) fprintf(log, "non-quoted compare with _MHD_SHA512_256_TOKEN -> %d\n", r);
    if (r)
    {
      if (log) { fprintf(log, "get_rq_dauth_algo: match -> MHD_DIGEST_AUTH_ALGO3_SHA512_256\n"); fclose(log); }
      return MHD_DIGEST_AUTH_ALGO3_SHA512_256;
    }

    /* Algorithms below are not supported by MHD for authentication */

    r = MHD_str_equal_caseless_s_bin_n_ (_MHD_MD5_TOKEN _MHD_SESS_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len);
    if (log) fprintf(log, "non-quoted compare with _MHD_MD5_TOKEN _MHD_SESS_TOKEN -> %d\n", r);
    if (r)
    {
      if (log) { fprintf(log, "get_rq_dauth_algo: match -> MHD_DIGEST_AUTH_ALGO3_MD5_SESSION\n"); fclose(log); }
      return MHD_DIGEST_AUTH_ALGO3_MD5_SESSION;
    }

    r = MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA256_TOKEN _MHD_SESS_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len);
    if (log) fprintf(log, "non-quoted compare with _MHD_SHA256_TOKEN _MHD_SESS_TOKEN -> %d\n", r);
    if (r)
    {
      if (log) { fprintf(log, "get_rq_dauth_algo: match -> MHD_DIGEST_AUTH_ALGO3_SHA256_SESSION\n"); fclose(log); }
      return MHD_DIGEST_AUTH_ALGO3_SHA256_SESSION;
    }

    r = MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA512_256_TOKEN _MHD_SESS_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len);
    if (log) fprintf(log, "non-quoted compare with _MHD_SHA512_256_TOKEN _MHD_SESS_TOKEN -> %d\n", r);
    if (r)
    {
      if (log) { fprintf(log, "get_rq_dauth_algo: match -> MHD_DIGEST_AUTH_ALGO3_SHA512_256_SESSION\n"); fclose(log); }
      return MHD_DIGEST_AUTH_ALGO3_SHA512_256_SESSION;
    }

    /* No known algorithm has been detected */
    if (log) { fprintf(log, "get_rq_dauth_algo: non-quoted path -> no known algorithm -> INVALID\n"); fclose(log); }
    return MHD_DIGEST_AUTH_ALGO3_INVALID;
  }
}
