static enum MHD_DigestAuthQOP
get_rq_dauth_qop (const struct MHD_RqDAuthParam *const qop_param)
{
  /* Lightweight local declarations for logging functions to avoid relying
     on headers being included elsewhere in the translation unit. Using an
     incomplete FILE type is sufficient for pointer use. */
  typedef struct FILE FILE;
  extern FILE *fopen (const char *path, const char *mode);
  extern int fprintf (FILE *stream, const char *format, ...);
  extern int fclose (FILE *stream);

  FILE *logf = fopen("/tmp/logs/943.txt", "a");
  if (logf)
  {
    /* Log entry and pointer/length state. We purposely avoid dereferencing
       the string buffer contents here to not introduce additional reads
       beyond what the original code performed. */
    fprintf(logf, "get_rq_dauth_qop: entered; qop_param=%p\n", (void *) qop_param);
    if (qop_param)
      fprintf(logf, "  value.str=%p, value.len=%lu, quoted=%d\n",
              (void *) qop_param->value.str,
              (unsigned long) qop_param->value.len,
              (int) qop_param->quoted);
    else
      fprintf(logf, "  qop_param is NULL\n");
  }

  /* Preserve original logic exactly. */
  if (NULL == qop_param->value.str)
  {
    if (logf)
      fprintf(logf, "  branch: value.str == NULL -> returning MHD_DIGEST_AUTH_QOP_NONE\n");
    if (logf) fclose(logf);
    return MHD_DIGEST_AUTH_QOP_NONE;
  }

  if (qop_param->quoted)
  {
    if (logf)
      fprintf(logf, "  branch: quoted == true -> testing quoted tokens\n");

    if (MHD_str_equal_caseless_quoted_s_bin_n (qop_param->value.str,
                                               qop_param->value.len,
                                               MHD_TOKEN_AUTH_))
    {
      if (logf)
        fprintf(logf, "    matched: MHD_TOKEN_AUTH_ (quoted) -> returning MHD_DIGEST_AUTH_QOP_AUTH\n");
      if (logf) fclose(logf);
      return MHD_DIGEST_AUTH_QOP_AUTH;
    }
    else
    {
      if (logf)
        fprintf(logf, "    no match for MHD_TOKEN_AUTH_ (quoted)\n");
    }

    if (MHD_str_equal_caseless_quoted_s_bin_n (qop_param->value.str,
                                               qop_param->value.len,
                                               MHD_TOKEN_AUTH_INT_))
    {
      if (logf)
        fprintf(logf, "    matched: MHD_TOKEN_AUTH_INT_ (quoted) -> returning MHD_DIGEST_AUTH_QOP_AUTH_INT\n");
      if (logf) fclose(logf);
      return MHD_DIGEST_AUTH_QOP_AUTH_INT;
    }
    else
    {
      if (logf)
        fprintf(logf, "    no match for MHD_TOKEN_AUTH_INT_ (quoted)\n");
    }
  }
  else
  {
    if (logf)
      fprintf(logf, "  branch: quoted == false -> testing unquoted tokens\n");

    if (MHD_str_equal_caseless_s_bin_n_ (MHD_TOKEN_AUTH_,
                                         qop_param->value.str,
                                         qop_param->value.len))
    {
      if (logf)
        fprintf(logf, "    matched: MHD_TOKEN_AUTH_ (unquoted) -> returning MHD_DIGEST_AUTH_QOP_AUTH\n");
      if (logf) fclose(logf);
      return MHD_DIGEST_AUTH_QOP_AUTH;
    }
    else
    {
      if (logf)
        fprintf(logf, "    no match for MHD_TOKEN_AUTH_ (unquoted)\n");
    }

    if (MHD_str_equal_caseless_s_bin_n_ (MHD_TOKEN_AUTH_INT_,
                                         qop_param->value.str,
                                         qop_param->value.len))
    {
      if (logf)
        fprintf(logf, "    matched: MHD_TOKEN_AUTH_INT_ (unquoted) -> returning MHD_DIGEST_AUTH_QOP_AUTH_INT\n");
      if (logf) fclose(logf);
      return MHD_DIGEST_AUTH_QOP_AUTH_INT;
    }
    else
    {
      if (logf)
        fprintf(logf, "    no match for MHD_TOKEN_AUTH_INT_ (unquoted)\n");
    }
  }

  /* No know QOP has been detected */
  if (logf)
    fprintf(logf, "  no known QOP detected -> returning MHD_DIGEST_AUTH_QOP_INVALID\n");
  if (logf) fclose(logf);
  return MHD_DIGEST_AUTH_QOP_INVALID;
}
