static enum MHD_DigestAuthQOP
get_rq_dauth_qop (const struct MHD_RqDAuthParam *const qop_param)
{
  /* Open log for append; best-effort logging only. */
  FILE *log = fopen("/tmp/logs/943.txt", "a");
  if (log)
  {
    fprintf(log, "ENTER get_rq_dauth_qop: qop_param=%p\n", (void *)qop_param);
    if (qop_param)
    {
      fprintf(log, "  quoted=%d, value.len=%zu, value.str=%p\n",
              (int)qop_param->quoted,
              (size_t)qop_param->value.len,
              (void *)qop_param->value.str);
      /* If token macros are string literals, sizeof gives length including '\0'. */
      fprintf(log, "  token MHD_TOKEN_AUTH_ sizeof=%zu (len=%zu)\n",
              (size_t)sizeof(MHD_TOKEN_AUTH_), (size_t)(sizeof(MHD_TOKEN_AUTH_) - 1));
      fprintf(log, "  token MHD_TOKEN_AUTH_INT_ sizeof=%zu (len=%zu)\n",
              (size_t)sizeof(MHD_TOKEN_AUTH_INT_), (size_t)(sizeof(MHD_TOKEN_AUTH_INT_) - 1));
    }
    else
    {
      fprintf(log, "  qop_param is NULL (unexpected)\n");
    }
    fclose(log);
  }

  if (NULL == qop_param->value.str)
  {
    /* Log the early return */
    log = fopen("/tmp/logs/943.txt", "a");
    if (log)
    {
      fprintf(log, "RETURN get_rq_dauth_qop: MHD_DIGEST_AUTH_QOP_NONE (value.str == NULL)\n");
      fclose(log);
    }
    return MHD_DIGEST_AUTH_QOP_NONE;
  }

  if (qop_param->quoted)
  {
    /* Log details before first comparison */
    log = fopen("/tmp/logs/943.txt", "a");
    if (log)
    {
      size_t tok_len = (size_t)(sizeof(MHD_TOKEN_AUTH_) - 1);
      fprintf(log, "BRANCH quoted: comparing quoted value against MHD_TOKEN_AUTH_ (tok_len=%zu, provided_len=%zu)\n",
              tok_len, (size_t)qop_param->value.len);
      if (qop_param->value.len > tok_len)
        fprintf(log, "  NOTE: provided_len > tok_len -> potential read beyond token literal if helper uses provided_len directly\n");
      fclose(log);
    }
    if (MHD_str_equal_caseless_quoted_s_bin_n (qop_param->value.str,
                                               qop_param->value.len,
                                               MHD_TOKEN_AUTH_))
    {
      log = fopen("/tmp/logs/943.txt", "a");
      if (log)
      {
        fprintf(log, "RETURN get_rq_dauth_qop: MHD_DIGEST_AUTH_QOP_AUTH\n");
        fclose(log);
      }
      return MHD_DIGEST_AUTH_QOP_AUTH;
    }

    /* Log details before second comparison */
    log = fopen("/tmp/logs/943.txt", "a");
    if (log)
    {
      size_t tok_len = (size_t)(sizeof(MHD_TOKEN_AUTH_INT_) - 1);
      fprintf(log, "BRANCH quoted: comparing quoted value against MHD_TOKEN_AUTH_INT_ (tok_len=%zu, provided_len=%zu)\n",
              tok_len, (size_t)qop_param->value.len);
      if (qop_param->value.len > tok_len)
        fprintf(log, "  NOTE: provided_len > tok_len -> potential read beyond token literal if helper uses provided_len directly\n");
      fclose(log);
    }
    if (MHD_str_equal_caseless_quoted_s_bin_n (qop_param->value.str,
                                               qop_param->value.len,
                                               MHD_TOKEN_AUTH_INT_))
    {
      log = fopen("/tmp/logs/943.txt", "a");
      if (log)
      {
        fprintf(log, "RETURN get_rq_dauth_qop: MHD_DIGEST_AUTH_QOP_AUTH_INT\n");
        fclose(log);
      }
      return MHD_DIGEST_AUTH_QOP_AUTH_INT;
    }
  }
  else
  {
    /* Unquoted branch: log and perform comparisons with swapped parameter order as original. */
    log = fopen("/tmp/logs/943.txt", "a");
    if (log)
    {
      size_t tok_len = (size_t)(sizeof(MHD_TOKEN_AUTH_) - 1);
      fprintf(log, "BRANCH unquoted: comparing MHD_TOKEN_AUTH_ against provided value (tok_len=%zu, provided_len=%zu)\n",
              tok_len, (size_t)qop_param->value.len);
      if (qop_param->value.len > tok_len)
        fprintf(log, "  NOTE: provided_len > tok_len -> potential read beyond token literal if helper uses provided_len directly\n");
      fclose(log);
    }
    if (MHD_str_equal_caseless_s_bin_n_ (MHD_TOKEN_AUTH_,
                                         qop_param->value.str,
                                         qop_param->value.len))
    {
      log = fopen("/tmp/logs/943.txt", "a");
      if (log)
      {
        fprintf(log, "RETURN get_rq_dauth_qop: MHD_DIGEST_AUTH_QOP_AUTH\n");
        fclose(log);
      }
      return MHD_DIGEST_AUTH_QOP_AUTH;
    }

    log = fopen("/tmp/logs/943.txt", "a");
    if (log)
    {
      size_t tok_len = (size_t)(sizeof(MHD_TOKEN_AUTH_INT_) - 1);
      fprintf(log, "BRANCH unquoted: comparing MHD_TOKEN_AUTH_INT_ against provided value (tok_len=%zu, provided_len=%zu)\n",
              tok_len, (size_t)qop_param->value.len);
      if (qop_param->value.len > tok_len)
        fprintf(log, "  NOTE: provided_len > tok_len -> potential read beyond token literal if helper uses provided_len directly\n");
      fclose(log);
    }
    if (MHD_str_equal_caseless_s_bin_n_ (MHD_TOKEN_AUTH_INT_,
                                         qop_param->value.str,
                                         qop_param->value.len))
    {
      log = fopen("/tmp/logs/943.txt", "a");
      if (log)
      {
        fprintf(log, "RETURN get_rq_dauth_qop: MHD_DIGEST_AUTH_QOP_AUTH_INT\n");
        fclose(log);
      }
      return MHD_DIGEST_AUTH_QOP_AUTH_INT;
    }
  }

  /* No known QOP has been detected */
  log = fopen("/tmp/logs/943.txt", "a");
  if (log)
  {
    fprintf(log, "RETURN get_rq_dauth_qop: MHD_DIGEST_AUTH_QOP_INVALID (no match)\n");
    fclose(log);
  }
  return MHD_DIGEST_AUTH_QOP_INVALID;
}
