static enum MHD_DigestAuthQOP
get_rq_dauth_qop (const struct MHD_RqDAuthParam *const qop_param)
{
  FILE *logf = fopen("/tmp/logs/945.txt", "a");
  if (logf == NULL)
  {
    /* Fallback if directory does not exist or cannot be opened. */
    logf = fopen("/tmp/945.txt", "a");
  }

  if (logf != NULL)
  {
    fprintf(logf, "ENTER get_rq_dauth_qop: qop_param=%p\n", (void *)qop_param);
    if (qop_param == NULL)
    {
      fprintf(logf, " qop_param is NULL -> behavior unchanged (likely crash in original).\n");
      fflush(logf);
      fclose(logf);
      /* Preserve original behavior: dereferencing NULL would have occurred in original code. */
      return MHD_DIGEST_AUTH_QOP_INVALID;
    }

    fprintf(logf, " qop_param->value.str=%p, quoted=%d, len=%zu\n",
            (void *)qop_param->value.str,
            (int)qop_param->quoted,
            (size_t)qop_param->value.len);

    /* If pointer is non-NULL, log up to the first 64 bytes (as hex) to avoid
       assuming a NUL-terminated string and to reduce risk of overruns. */
    if (qop_param->value.str != NULL && qop_param->value.len > 0)
    {
      size_t i, lim = qop_param->value.len;
      if (lim > 64u)
        lim = 64u;
      fprintf(logf, " value.str (hex, up to %zu bytes):", lim);
      for (i = 0; i < lim; ++i)
      {
        unsigned char c = (unsigned char)qop_param->value.str[i];
        fprintf(logf, " %02x", c);
      }
      if (qop_param->value.len > lim)
        fprintf(logf, " ... (total len=%zu)", (size_t)qop_param->value.len);
      fprintf(logf, "\n");
    }
    fflush(logf);
    fclose(logf);
  }

  /* Preserve original logic exactly, but capture the result of each
     string comparison to log the control decisions. */
  if (NULL == qop_param->value.str)
  {
    FILE *f = fopen("/tmp/logs/945.txt", "a");
    if (f == NULL)
      f = fopen("/tmp/945.txt", "a");
    if (f)
    {
      fprintf(f, "DECISION: value.str == NULL -> return MHD_DIGEST_AUTH_QOP_NONE\n");
      fflush(f);
      fclose(f);
    }
    return MHD_DIGEST_AUTH_QOP_NONE;
  }

  if (qop_param->quoted)
  {
    int eq;

    eq = MHD_str_equal_caseless_quoted_s_bin_n (qop_param->value.str, 
                                                qop_param->value.len, 
                                                MHD_TOKEN_AUTH_);
    {
      FILE *f = fopen("/tmp/logs/945.txt", "a");
      if (f == NULL)
        f = fopen("/tmp/945.txt", "a");
      if (f)
      {
        fprintf(f, "COMPARISON quoted with MHD_TOKEN_AUTH_: result=%d\n", eq);
        fflush(f);
        fclose(f);
      }
    }
    if (eq)
    {
      FILE *f = fopen("/tmp/logs/945.txt", "a");
      if (f == NULL)
        f = fopen("/tmp/945.txt", "a");
      if (f)
      {
        fprintf(f, "DECISION: quoted && eq(MHD_TOKEN_AUTH_) -> return MHD_DIGEST_AUTH_QOP_AUTH\n");
        fflush(f);
        fclose(f);
      }
      return MHD_DIGEST_AUTH_QOP_AUTH;
    }

    eq = MHD_str_equal_caseless_quoted_s_bin_n (qop_param->value.str, 
                                                qop_param->value.len, 
                                                MHD_TOKEN_AUTH_INT_);
    {
      FILE *f = fopen("/tmp/logs/945.txt", "a");
      if (f == NULL)
        f = fopen("/tmp/945.txt", "a");
      if (f)
      {
        fprintf(f, "COMPARISON quoted with MHD_TOKEN_AUTH_INT_: result=%d\n", eq);
        fflush(f);
        fclose(f);
      }
    }
    if (eq)
    {
      FILE *f = fopen("/tmp/logs/945.txt", "a");
      if (f == NULL)
        f = fopen("/tmp/945.txt", "a");
      if (f)
      {
        fprintf(f, "DECISION: quoted && eq(MHD_TOKEN_AUTH_INT_) -> return MHD_DIGEST_AUTH_QOP_AUTH_INT\n");
        fflush(f);
        fclose(f);
      }
      return MHD_DIGEST_AUTH_QOP_AUTH_INT;
    }
  }
  else
  {
    int eq;

    eq = MHD_str_equal_caseless_s_bin_n_ (MHD_TOKEN_AUTH_, 
                                          qop_param->value.str, 
                                          qop_param->value.len);
    {
      FILE *f = fopen("/tmp/logs/945.txt", "a");
      if (f == NULL)
        f = fopen("/tmp/945.txt", "a");
      if (f)
      {
        fprintf(f, "COMPARISON unquoted with MHD_TOKEN_AUTH_: result=%d\n", eq);
        fflush(f);
        fclose(f);
      }
    }
    if (eq)
    {
      FILE *f = fopen("/tmp/logs/945.txt", "a");
      if (f == NULL)
        f = fopen("/tmp/945.txt", "a");
      if (f)
      {
        fprintf(f, "DECISION: !quoted && eq(MHD_TOKEN_AUTH_) -> return MHD_DIGEST_AUTH_QOP_AUTH\n");
        fflush(f);
        fclose(f);
      }
      return MHD_DIGEST_AUTH_QOP_AUTH;
    }

    eq = MHD_str_equal_caseless_s_bin_n_ (MHD_TOKEN_AUTH_INT_, 
                                          qop_param->value.str, 
                                          qop_param->value.len);
    {
      FILE *f = fopen("/tmp/logs/945.txt", "a");
      if (f == NULL)
        f = fopen("/tmp/945.txt", "a");
      if (f)
      {
        fprintf(f, "COMPARISON unquoted with MHD_TOKEN_AUTH_INT_: result=%d\n", eq);
        fflush(f);
        fclose(f);
      }
    }
    if (eq)
    {
      FILE *f = fopen("/tmp/logs/945.txt", "a");
      if (f == NULL)
        f = fopen("/tmp/945.txt", "a");
      if (f)
      {
        fprintf(f, "DECISION: !quoted && eq(MHD_TOKEN_AUTH_INT_) -> return MHD_DIGEST_AUTH_QOP_AUTH_INT\n");
        fflush(f);
        fclose(f);
      }
      return MHD_DIGEST_AUTH_QOP_AUTH_INT;
    }
  }

  /* No known QOP has been detected */
  {
    FILE *f = fopen("/tmp/logs/945.txt", "a");
    if (f == NULL)
      f = fopen("/tmp/945.txt", "a");
    if (f)
    {
      fprintf(f, "DECISION: No known QOP detected -> return MHD_DIGEST_AUTH_QOP_INVALID\n");
      fflush(f);
      fclose(f);
    }
  }

  return MHD_DIGEST_AUTH_QOP_INVALID;
}
