static size_t
get_rq_param_unquoted_copy_z (const struct MHD_RqDAuthParam *param, char *buf)
{
  size_t len;
  FILE *log = fopen("/tmp/logs/4238.txt", "a");

  /* Minimal entry probe so callers and tests prove this function was reached */
  if (log)
  {
    fprintf(log, "ENTER get_rq_param_unquoted_copy_z: param=%p\n", (void *)param);
    if (param)
      fprintf(log, "  quoted=%d value.str=%p value.len=%zu\n",
              (int)param->quoted, (void *)param->value.str, (size_t)param->value.len);
    else
      fprintf(log, "  param is NULL\n");
  }

  /* Evaluate assertion expressions for logging (does not replace mhd_assert calls). */
  int assert1_eval = (param != NULL && param->value.str != NULL);
  if (log)
    fprintf(log, "  eval assert1 (NULL != param->value.str) -> %d\n", assert1_eval);

  /* Original assertion must remain to preserve behavior in builds where it has effect. */
  mhd_assert (NULL != param->value.str);

  if (! param->quoted)
  {
    if (log)
      fprintf(log, "  branch: not quoted; memcpy %zu bytes\n", param->value.len);

    memcpy (buf, param->value.str, param->value.len);
    buf [param->value.len] = 0;

    if (log)
    {
      fprintf(log, "  result: copied unquoted (not quoted) len=%zu\n", param->value.len);
      fclose(log);
    }
    return param->value.len;
  }

  len = MHD_str_unquote (param->value.str, param->value.len, buf);
  if (log)
    fprintf(log, "  MHD_str_unquote returned len=%zu\n", len);

  int assert2_eval = (0 != len);
  int assert3_eval = (len < param->value.len);
  if (log)
    fprintf(log, "  eval assert2 (0 != len) -> %d; eval assert3 (len < param->value.len) -> %d\n",
            assert2_eval, assert3_eval);

  /* Preserve original assertions (they may be no-ops in some builds). */
  mhd_assert (0 != len);
  mhd_assert (len < param->value.len);

  buf[len] = 0;

  if (log)
  {
    fprintf(log, "  result: copied unquoted (quoted) len=%zu\n", len);
    fclose(log);
  }

  return len;
}
