static size_t
get_rq_param_unquoted_copy_z (const struct MHD_RqDAuthParam *param, char *buf)
{
  size_t len;
  /* Minimal entry log so callers/tests can see the function was reached and
     capture key state needed to reproduce the violation scenario. Logs are
     appended to /tmp/logs/4233.txt. */
  {
    FILE *f = fopen("/tmp/logs/4233.txt", "a");
    if (f)
    {
      fprintf(f, "get_rq_param_unquoted_copy_z ENTER: func=%p param=%p buf=%p\n",
              (void *) &get_rq_param_unquoted_copy_z, (void *) param, (void *) buf);
      if (param)
      {
        /* Print basic param state without dereferencing value.str contents beyond bounds. */
        fprintf(f, "  quoted=%d value.len=%zu value.str=%p\n",
                (int) param->quoted, param->value.len, (void *) param->value.str);
        /* Print a short preview (up to 64 bytes) of value.str in printable form */
        if (param->value.str && param->value.len > 0)
        {
          size_t preview = param->value.len;
          if (preview > 64) preview = 64;
          fprintf(f, "  value.str[0..%zu]: ", preview);
          for (size_t i = 0; i < preview; ++i)
          {
            unsigned char c = (unsigned char) param->value.str[i];
            if (c >= 0x20 && c < 0x7f)
              fputc(c, f);
            else
              fprintf(f, "\\x%02x", c);
          }
          if (param->value.len > preview) fprintf(f, "...");
          fprintf(f, "\n");
        }
      }
      fclose(f);
    }
  }

  mhd_assert (NULL != param->value.str);
  if (! param->quoted)
  {
    /* Log intent to copy and to write the terminating NUL at index param->value.len */
    {
      FILE *f = fopen("/tmp/logs/4233.txt", "a");
      if (f)
      {
        fprintf(f, "UNQUOTED PATH: about to memcpy(buf=%p, value.str=%p, %zu) and write NUL at buf[%zu]\n",
                (void *) buf, (void *) param->value.str, param->value.len, param->value.len);
        fclose(f);
      }
    }

    memcpy (buf, param->value.str, param->value.len);
    buf [param->value.len] = 0;

    {
      FILE *f = fopen("/tmp/logs/4233.txt", "a");
      if (f)
      {
        fprintf(f, "UNQUOTED PATH AFTER: copied %zu bytes and wrote terminating NUL at buf[%zu]\n",
                param->value.len, param->value.len);
        fclose(f);
      }
    }
    return param->value.len;
  }

  len = MHD_str_unquote (param->value.str, param->value.len, buf);
  mhd_assert (0 != len);
  mhd_assert (len < param->value.len);

  {
    FILE *f = fopen("/tmp/logs/4233.txt", "a");
    if (f)
    {
      fprintf(f, "QUOTED PATH: unquoted_len=%zu original_len=%zu; will write NUL at buf[%zu]\n",
              len, param->value.len, len);
      fclose(f);
    }
  }

  buf[len] = 0;
  return len;
}
