static size_t
get_rq_param_unquoted_copy_z (const struct MHD_RqDAuthParam *param, char *buf)
{
  size_t len;
  mhd_assert (NULL != param->value.str);

  /* Open log for appending; tolerate failure to open. */
  FILE *log = fopen("/tmp/logs/4233.txt", "a");
  if (log)
  {
    fprintf(log, "ENTER get_rq_param_unquoted_copy_z: param=%p, value.str=%p, value.len=%zu, quoted=%d, buf=%p\n",
            (void *)param, (void *)param->value.str, param->value.len, (int)(param->quoted), (void *)buf);
    fflush(log);
  }

  if (! param->quoted)
  {
    if (log)
    {
      fprintf(log, "BRANCH unquoted: about to memcpy %zu bytes from %p to %p\n",
              param->value.len, (void *)param->value.str, (void *)buf);
      fflush(log);
    }

    memcpy (buf, param->value.str, param->value.len);

    if (log)
    {
      fprintf(log, "AFTER memcpy: setting buf[%zu]=0\n", param->value.len);
      fflush(log);
    }

    buf [param->value.len] = 0;

    if (log)
    {
      fprintf(log, "RETURN (unquoted) %zu\n", param->value.len);
      fclose(log);
    }

    return param->value.len;
  }

  /* quoted */
  len = MHD_str_unquote (param->value.str, param->value.len, buf);

  if (log)
  {
    fprintf(log, "BRANCH quoted: MHD_str_unquote returned len=%zu (orig len=%zu)\n", len, param->value.len);
    fflush(log);
  }

  mhd_assert (0 != len);
  mhd_assert (len < param->value.len);
  buf[len] = 0;

  if (log)
  {
    fprintf(log, "AFTER unquote: set buf[%zu]=0; RETURN %zu\n", len, len);
    fclose(log);
  }

  return len;
}
