static size_t
get_rq_param_unquoted_copy_z (const struct MHD_RqDAuthParam *param, char *buf)
{
  size_t len;
  /* Open log file for appending probe information. If opening fails, continue silently
     to preserve original behavior. */
  FILE *log = fopen("/tmp/logs/4235.txt", "a");

  if (log)
  {
    fprintf(log, "ENTER: get_rq_param_unquoted_copy_z: param=%p, buf=%p\n", (void *)param, (void *)buf);
    fflush(log);
  }

  /* Preserve original assertion (will abort if violated). Log just before asserting
     the condition so that reproducer can see the state that led to the assertion. */
  if (log)
  {
    /* We avoid dereferencing param->value.str here if param itself is NULL; print param first. */
    fprintf(log, "STATE BEFORE ASSERT: param=%p\n", (void *)param);
    fflush(log);
  }

  mhd_assert (NULL != param->value.str);

  if (log)
  {
    fprintf(log, "STATE: param->value.str=%p, param->value.len=%zu, param->quoted=%d\n",
            (void *)param->value.str, (size_t)param->value.len, (int)param->quoted);
    fflush(log);
  }

  if (! param->quoted)
  {
    if (log)
    {
      fprintf(log, "BRANCH: not quoted - performing memcpy of %zu bytes\n", (size_t)param->value.len);
      fflush(log);
    }

    memcpy (buf, param->value.str, param->value.len);
    buf [param->value.len] = 0;

    if (log)
    {
      fprintf(log, "EXIT: copied unquoted (not originally quoted). return=%zu\n", (size_t)param->value.len);
      fclose(log);
    }

    return param->value.len;
  }

  if (log)
  {
    fprintf(log, "BRANCH: quoted - calling MHD_str_unquote on %p with len=%zu\n",
            (void *)param->value.str, (size_t)param->value.len);
    fflush(log);
  }

  len = MHD_str_unquote (param->value.str, param->value.len, buf);

  if (log)
  {
    fprintf(log, "AFTER_UNQUOTE: returned len=%zu\n", len);
    fflush(log);
  }

  mhd_assert (0 != len);
  mhd_assert (len < param->value.len);
  buf[len] = 0;

  if (log)
  {
    fprintf(log, "EXIT: quoted branch completed. return=%zu\n", len);
    fclose(log);
  }

  return len;
}
