static size_t
get_rq_param_unquoted_copy_z (const struct MHD_RqDAuthParam *param, char *buf)
{
  size_t len;
  /* Log entry and key state for reproduction and debugging. */
  {
    FILE *log = fopen("/tmp/logs/4235.txt", "a");
    if (log)
    {
      fprintf(log, "ENTER get_rq_param_unquoted_copy_z: param=%p\n", (void *) param);
      if (param != NULL)
      {
        fprintf(log, " param->quoted=%d, param->value.len=%zu, param->value.str=%p\n",
                param->quoted ? 1 : 0,
                param->value.len,
                (void *) param->value.str);
        if (param->value.str != NULL && param->value.len > 0)
        {
          size_t dump = param->value.len < (size_t)32 ? param->value.len : (size_t)32;
          fprintf(log, " param->value.str (first %zu bytes as hex): ", dump);
          for (size_t i = 0U; i < dump; ++i)
          {
            unsigned char c = ((const unsigned char *) param->value.str)[i];
            fprintf(log, "%02x", c);
          }
          fprintf(log, "\n");
        }
      }
      else
      {
        fprintf(log, " param is NULL\n");
      }
      fclose(log);
    }
  }

  mhd_assert (NULL != param->value.str);
  if (! param->quoted)
  {
    memcpy (buf, param->value.str, param->value.len);
    buf [param->value.len] = 0;

    {
      FILE *log = fopen("/tmp/logs/4235.txt", "a");
      if (log)
      {
        fprintf(log, "PATH: unquoted branch taken; copied %zu bytes from %p to buf=%p; returning %zu\n",
                param->value.len, (void *) param->value.str, (void *) buf, param->value.len);
        fclose(log);
      }
    }

    return param->value.len;
  }

  len = MHD_str_unquote (param->value.str, param->value.len, buf);
  mhd_assert (0 != len);
  mhd_assert (len < param->value.len);
  buf[len] = 0;

  {
    FILE *log = fopen("/tmp/logs/4235.txt", "a");
    if (log)
    {
      fprintf(log, "PATH: quoted branch taken; MHD_str_unquote returned len=%zu; original len=%zu; buf=%p\n",
              len, param->value.len, (void *) buf);
      fclose(log);
    }
  }

  return len;
}
