static size_t
get_rq_uname (const struct MHD_RqDAuth *params,
              enum MHD_DigestAuthUsernameType uname_type,
              struct MHD_DigestAuthUsernameInfo *uname_info,
              uint8_t *buf,
              size_t buf_size)
{
  size_t buf_used;
  FILE *logf = NULL;

  /* Open log file for append; best-effort, do not alter control flow on failure */
  logf = fopen("/tmp/logs/3683.txt", "a");
  if (logf)
  {
    fprintf(logf, "get_rq_uname ENTRY: params=%p uname_type=%d buf=%p buf_size=%zu\n",
            (const void *) params, (int) uname_type, (const void *) buf, buf_size);
  }

  buf_used = 0;
  mhd_assert (get_rq_uname_type (params) == uname_type);
  mhd_assert (MHD_DIGEST_AUTH_UNAME_TYPE_INVALID != uname_type);
  mhd_assert (MHD_DIGEST_AUTH_UNAME_TYPE_MISSING != uname_type);

  uname_info->username = NULL;
  uname_info->username_len = 0;
  uname_info->userhash_hex = NULL;
  uname_info->userhash_hex_len = 0;
  uname_info->userhash_bin = NULL;

  if (MHD_DIGEST_AUTH_UNAME_TYPE_STANDARD == uname_type)
  {
    size_t remaining = (buf_size > buf_used) ? buf_size - buf_used : 0;

    if (logf)
    {
      fprintf(logf, "BRANCH: STANDARD start buf_used=%zu remaining=%zu dest=%p params=%p\n",
              buf_used, remaining, (void *) (buf + buf_used), (const void *) &params->username);
    }

    uname_info->username = (char *) (buf + buf_used);
    uname_info->username_len =
      get_rq_param_unquoted_copy_z (&params->username,
                                    uname_info->username);

    if (logf)
    {
      size_t rem_after = (buf_size > buf_used) ? buf_size - buf_used : 0;
      fprintf(logf, "BRANCH: STANDARD after copy uname_len=%zu buf_used_before=%zu buf_size=%zu rem_before=%zu rem_after_est=%zu\n",
              uname_info->username_len, buf_used, buf_size, remaining, rem_after);
    }

    buf_used += uname_info->username_len + 1;
    uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_STANDARD;
  }
  else if (MHD_DIGEST_AUTH_UNAME_TYPE_USERHASH == uname_type)
  {
    size_t res;
    size_t remaining = (buf_size > buf_used) ? buf_size - buf_used : 0;

    if (logf)
    {
      fprintf(logf, "BRANCH: USERHASH start buf_used=%zu remaining=%zu dest_hex=%p params=%p\n",
              buf_used, remaining, (void *) (buf + buf_used), (const void *) &params->username);
    }

    uname_info->userhash_hex = (char *) (buf + buf_used);
    uname_info->userhash_hex_len =
      get_rq_param_unquoted_copy_z (&params->username,
                                    uname_info->userhash_hex);

    if (logf)
    {
      fprintf(logf, "BRANCH: USERHASH after hex copy hex_len=%zu buf_used_before=%zu buf_size=%zu\n",
              uname_info->userhash_hex_len, buf_used, buf_size);
    }

    buf_used += uname_info->userhash_hex_len + 1;
    uname_info->userhash_bin = (uint8_t *) (buf + buf_used);
    res = MHD_hex_to_bin (uname_info->userhash_hex,
                          uname_info->userhash_hex_len,
                          uname_info->userhash_bin);
    if (res != uname_info->userhash_hex_len / 2)
    {
      if (logf)
        fprintf(logf, "BRANCH: USERHASH invalid hex conversion res=%zu expected=%zu\n",
                res, uname_info->userhash_hex_len / 2);
      uname_info->userhash_bin = NULL;
      uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_INVALID;
    }
    else
    {
      /* Avoid pointers outside allocated region when the size is zero */
      if (0 == res)
        uname_info->userhash_bin = (uint8_t *) uname_info->username;
      uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_USERHASH;
      buf_used += res;
      if (logf)
        fprintf(logf, "BRANCH: USERHASH ok res=%zu new_buf_used=%zu\n", res, buf_used);
    }
  }
  else if (MHD_DIGEST_AUTH_UNAME_TYPE_EXTENDED == uname_type)
  {
    ssize_t res;
    size_t pass_size = (buf_size > buf_used) ? buf_size - buf_used : 0;

    if (logf)
    {
      fprintf(logf, "BRANCH: EXTENDED start buf_used=%zu pass_size=%zu src=%p len=%zu\n",
              buf_used, pass_size, (const void *) params->username_ext.value.str,
              (size_t) params->username_ext.value.len);
    }

    res = get_rq_extended_uname_copy_z (params->username_ext.value.str,
                                        params->username_ext.value.len,
                                        (char *) (buf + buf_used),
                                        buf_size - buf_used);
    if (0 > res)
    {
      if (logf)
        fprintf(logf, "BRANCH: EXTENDED invalid res=%zd\n", res);
      uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_INVALID;
    }
    else
    {
      uname_info->username = (char *) (buf + buf_used);
      uname_info->username_len = (size_t) res;
      uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_EXTENDED;
      buf_used += uname_info->username_len + 1;
      if (logf)
        fprintf(logf, "BRANCH: EXTENDED ok uname_len=%zu new_buf_used=%zu\n",
                uname_info->username_len, buf_used);
    }
  }
  else
  {
    mhd_assert (0);
    uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_INVALID;
    if (logf)
      fprintf(logf, "BRANCH: UNKNOWN uname_type=%d -> INVALID\n", (int) uname_type);
  }

  if (logf)
  {
    fprintf(logf, "EXIT: final buf_used=%zu buf_size=%zu uname_type=%d username=%p username_len=%zu userhash_hex=%p userhash_hex_len=%zu userhash_bin=%p\n",
            buf_used, buf_size, (int) uname_info->uname_type,
            (const void *) uname_info->username, uname_info->username_len,
            (const void *) uname_info->userhash_hex, uname_info->userhash_hex_len,
            (const void *) uname_info->userhash_bin);
    fclose(logf);
  }

  mhd_assert (buf_size >= buf_used);
  return buf_used;
}
