static size_t
get_rq_uname (const struct MHD_RqDAuth *params,
              enum MHD_DigestAuthUsernameType uname_type,
              struct MHD_DigestAuthUsernameInfo *uname_info,
              uint8_t *buf,
              size_t buf_size)
{
  size_t buf_used;
  FILE *logf = fopen("/tmp/logs/3683.txt", "a");

  if (logf)
  {
    fprintf(logf, "ENTER get_rq_uname: params=%p uname_type=%d buf=%p buf_size=%zu\n",
            (const void *) params, (int) uname_type, (const void *) buf, buf_size);
    fflush(logf);
  }

  buf_used = 0;
  mhd_assert (get_rq_uname_type (params) == uname_type);
  mhd_assert (MHD_DIGEST_AUTH_UNAME_TYPE_INVALID != uname_type);
  mhd_assert (MHD_DIGEST_AUTH_UNAME_TYPE_MISSING != uname_type);

  uname_info->username = NULL;
  uname_info->username_len = 0;
  uname_info->userhash_hex = NULL;
  uname_info->userhash_hex_len = 0;
  uname_info->userhash_bin = NULL;

  if (logf)
  {
    fprintf(logf, "initial buf_used=%zu\n", buf_used);
    fflush(logf);
  }

  if (MHD_DIGEST_AUTH_UNAME_TYPE_STANDARD == uname_type)
  {
    uname_info->username = (char *) (buf + buf_used);
    if (logf)
    {
      fprintf(logf, "BRANCH STANDARD: about to call get_rq_param_unquoted_copy_z at buf+%zu\n", buf_used);
      fflush(logf);
    }
    uname_info->username_len =
      get_rq_param_unquoted_copy_z (&params->username,
                                    uname_info->username);
    if (logf)
    {
      fprintf(logf, "STANDARD: got username_len=%zu (stored at offset=%zu)\n",
              uname_info->username_len, buf_used);
      fflush(logf);
    }
    buf_used += uname_info->username_len + 1;
    uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_STANDARD;
  }
  else if (MHD_DIGEST_AUTH_UNAME_TYPE_USERHASH == uname_type)
  {
    size_t res;

    uname_info->userhash_hex = (char *) (buf + buf_used);
    if (logf)
    {
      fprintf(logf, "BRANCH USERHASH: about to call get_rq_param_unquoted_copy_z at buf+%zu\n", buf_used);
      fflush(logf);
    }
    uname_info->userhash_hex_len =
      get_rq_param_unquoted_copy_z (&params->username,
                                    uname_info->userhash_hex);
    if (logf)
    {
      fprintf(logf, "USERHASH: userhash_hex_len=%zu (hex stored at offset=%zu)\n",
              uname_info->userhash_hex_len, buf_used);
      fflush(logf);
    }

    /* Log potential buffer requirements before converting hex to bin. Do not
       change program flow or prevent the original calls; just record state. */
    if (logf)
    {
      size_t needed_after_copy = buf_used + uname_info->userhash_hex_len + 1 + (uname_info->userhash_hex_len / 2);
      fprintf(logf, "USERHASH: buf_size=%zu buf_used_after_hex_copy=%zu needed_total_for_hex_plus_bin=%zu\n",
              buf_size, buf_used + uname_info->userhash_hex_len + 1, needed_after_copy);
      if (buf_size < needed_after_copy)
        fprintf(logf, "USERHASH WARNING: buffer likely too small: buf_size < needed_total (%zu < %zu)\n",
                buf_size, needed_after_copy);
      fflush(logf);
    }

    buf_used += uname_info->userhash_hex_len + 1;
    uname_info->userhash_bin = (uint8_t *) (buf + buf_used);
    if (logf)
    {
      fprintf(logf, "USERHASH: about to call MHD_hex_to_bin from offset=%zu into offset=%zu\n",
              (size_t)(uname_info->userhash_hex - (char *)buf), (size_t)(uname_info->userhash_bin - (uint8_t *)buf));
      fflush(logf);
    }
    res = MHD_hex_to_bin (uname_info->userhash_hex,
                          uname_info->userhash_hex_len,
                          uname_info->userhash_bin);
    if (logf)
    {
      fprintf(logf, "MHD_hex_to_bin returned res=%zu expected=%zu\n",
              res, uname_info->userhash_hex_len / 2);
      fflush(logf);
    }
    if (res != uname_info->userhash_hex_len / 2)
    {
      uname_info->userhash_bin = NULL;
      uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_INVALID;
      if (logf)
      {
        fprintf(logf, "USERHASH: hex->bin size mismatch, marking INVALID\n");
        fflush(logf);
      }
    }
    else
    {
      /* Avoid pointers outside allocated region when the size is zero */
      if (0 == res)
        uname_info->userhash_bin = (uint8_t *) uname_info->username;
      uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_USERHASH;
      buf_used += res;
      if (logf)
      {
        fprintf(logf, "USERHASH: successful conversion, buf_used now=%zu\n", buf_used);
        fflush(logf);
      }
    }
  }
  else if (MHD_DIGEST_AUTH_UNAME_TYPE_EXTENDED == uname_type)
  {
    ssize_t res;
    if (logf)
    {
      fprintf(logf, "BRANCH EXTENDED: about to call get_rq_extended_uname_copy_z at buf+%zu with remaining=%zu\n",
              buf_used, (buf_size >= buf_used) ? (buf_size - buf_used) : 0);
      fflush(logf);
    }
    res = get_rq_extended_uname_copy_z (params->username_ext.value.str,
                                        params->username_ext.value.len,
                                        (char *) (buf + buf_used),
                                        buf_size - buf_used);
    if (0 > res)
      uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_INVALID;
    else
    {
      uname_info->username = (char *) (buf + buf_used);
      uname_info->username_len = (size_t) res;
      uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_EXTENDED;
      buf_used += uname_info->username_len + 1;
      if (logf)
      {
        fprintf(logf, "EXTENDED: copied username_len=%zu buf_used=%zu\n",
                uname_info->username_len, buf_used);
        fflush(logf);
      }
    }
  }
  else
  {
    mhd_assert (0);
    uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_INVALID;
    if (logf)
    {
      fprintf(logf, "BRANCH UNKNOWN: marked INVALID\n");
      fflush(logf);
    }
  }

  if (logf)
  {
    fprintf(logf, "EXIT get_rq_uname: final buf_used=%zu buf_size=%zu uname_type=%d\n",
            buf_used, buf_size, (int) uname_info->uname_type);
    fflush(logf);
    fclose(logf);
  }

  mhd_assert (buf_size >= buf_used);
  return buf_used;
}
