_MHD_static_inline size_t
get_rq_unames_size (const struct MHD_RqDAuth *params,
                    enum MHD_DigestAuthUsernameType uname_type)
{
  size_t s;

  /* Preserve original assertion */
  mhd_assert (get_rq_uname_type (params) == uname_type);

  /* Open a persistent log for debugging/control-flow tracing. */
  {
    FILE *f = fopen("/tmp/logs/3783.txt", "a");
    if (f)
      {
        fprintf(f, "ENTER get_rq_unames_size: params=%p, uname_type=%d\n",
                (void *) params, (int) uname_type);
        fclose(f);
      }
  }

  s = 0;
  if ((MHD_DIGEST_AUTH_UNAME_TYPE_STANDARD == uname_type) ||
      (MHD_DIGEST_AUTH_UNAME_TYPE_USERHASH == uname_type) )
  {
    s += params->username.value.len + 1; /* Add one byte for zero-termination */
    {
      FILE *f = fopen("/tmp/logs/3783.txt", "a");
      if (f)
        {
          fprintf(f, "BRANCH standard/userhash: username.len=%zu, s(intermediate)=%zu\n",
                  (size_t) params->username.value.len, s);
          fclose(f);
        }
    }
    if (MHD_DIGEST_AUTH_UNAME_TYPE_USERHASH == uname_type)
      s += (params->username.value.len + 1) / 2;
  }
  else if (MHD_DIGEST_AUTH_UNAME_TYPE_EXTENDED == uname_type)
    s += params->username_ext.value.len
         - MHD_DAUTH_EXT_PARAM_MIN_LEN + 1; /* Add one byte for zero-termination */

  /* Log final computed values and potential underflow situation for extended type. */
  {
    FILE *f = fopen("/tmp/logs/3783.txt", "a");
    if (f)
      {
        fprintf(f, "EXIT get_rq_unames_size: params=%p, uname_type=%d, final_s=%zu\n",
                (void *) params, (int) uname_type, s);

        /* If extended, also log lengths and underline potential unsigned wrap-around. */
        if (MHD_DIGEST_AUTH_UNAME_TYPE_EXTENDED == uname_type)
          {
            size_t vlen = params->username_ext.value.len;
            size_t minlen = (size_t) MHD_DAUTH_EXT_PARAM_MIN_LEN;
            fprintf(f, "  extended details: username_ext.len=%zu, MHD_DAUTH_EXT_PARAM_MIN_LEN=%zu\n",
                    vlen, minlen);
            if (vlen < minlen)
              fprintf(f, "  WARNING: unsigned underflow possible: %zu - %zu + 1 -> %zu\n",
                      vlen, minlen, (size_t) (vlen - minlen + 1));
          }
        fclose(f);
      }
  }

  return s;
}
