static enum _MHD_GetUnqResult
get_unquoted_param (const struct MHD_RqDAuthParam *param,
                    char tmp1[_MHD_STATIC_UNQ_BUFFER_SIZE],
                    char **ptmp2,
                    size_t *ptmp2_size,
                    struct _MHD_str_w_len *unquoted)
{
  char *str;
  size_t len;
  FILE *log = fopen("/tmp/logs/3345.txt", "a");

  /* Log entry and input state. If logging fails, continue normally. */
  if (NULL != log)
  {
    fprintf(log, "get_unquoted_param ENTRY: this call\n");
    fprintf(log, "  param=%p\n", (void *)param);
    if (NULL != param)
    {
      fprintf(log, "  param->value.str=%p\n", (void *)param->value.str);
      fprintf(log, "  param->value.len=%zu\n", param->value.len);
      fprintf(log, "  param->quoted=%d\n", (int)param->quoted);
    }
    fprintf(log, "  tmp1=%p ptmp2=%p ptmp2_size=%p unquoted=%p\n",
            (void *)tmp1, (void *)ptmp2, (void *)ptmp2_size, (void *)unquoted);
    fflush(log);
  }

  mhd_assert (NULL != param->value.str);
  mhd_assert (0 != param->value.len);

  if (! param->quoted)
  {
    /* Direct assignment from param->value.str (no copy). */
    unquoted->str = param->value.str;
    unquoted->len = param->value.len;

    if (NULL != log)
    {
      fprintf(log, "BRANCH: not quoted -> assigned directly from param->value.str\n");
      fprintf(log, "  unquoted->str=%p unquoted->len=%zu\n",
              (void *)unquoted->str, unquoted->len);
      fprintf(log, "  NOTE: assignment records that unquoted->str points to the same address as param->value.str\n");
      fflush(log);
      fclose(log);
    }

    return _MHD_UNQ_OK;
  }

  /* The value is present and is quoted, needs to be copied and unquoted */
  str = get_buffer_for_size (tmp1, ptmp2, ptmp2_size, param->value.len);

  if (NULL != log)
  {
    fprintf(log, "BRANCH: quoted -> requested buffer for size %zu\n", param->value.len);
    fprintf(log, "  get_buffer_for_size returned %p\n", (void *)str);
    fflush(log);
  }

  if (NULL == str)
  {
    enum _MHD_GetUnqResult ret = (param->value.len > _MHD_AUTH_DIGEST_MAX_PARAM_SIZE) ?
           _MHD_UNQ_TOO_LARGE : _MHD_UNQ_OUT_OF_MEM;

    if (NULL != log)
    {
      fprintf(log, "ERROR: could not obtain buffer, returning %d\n", (int)ret);
      fflush(log);
      fclose(log);
    }

    return ret;
  }

  len = MHD_str_unquote (param->value.str, param->value.len, str);
  unquoted->str = str;
  unquoted->len = len;
  mhd_assert (0 != unquoted->len);
  mhd_assert (unquoted->len < param->value.len);

  if (NULL != log)
  {
    fprintf(log, "AFTER UNQUOTE: copied/unquoted into buffer\n");
    fprintf(log, "  buffer addr (str)=%p, unquoted->str=%p\n", (void *)str, (void *)unquoted->str);
    fprintf(log, "  unquoted->len=%zu (original param->value.len=%zu)\n", unquoted->len, param->value.len);
    fprintf(log, "  NOTE: unquoted->str now points to a mutable buffer (may differ from param->value.str)\n");
    fflush(log);
    fclose(log);
  }

  return _MHD_UNQ_OK;
}
