static enum _MHD_GetUnqResult
get_unquoted_param_copy (const struct MHD_RqDAuthParam *param,
                         char tmp1[_MHD_STATIC_UNQ_BUFFER_SIZE],
                         char **ptmp2,
                         size_t *ptmp2_size,
                         struct _MHD_mstr_w_len *unquoted)
{
  /* Probe logging: record entry and key state so other agents can reproduce
     and gather evidence for the reported dead-code scenario. Logs are
     appended to /tmp/logs/3558.txt. */
  {
    FILE *f = fopen("/tmp/logs/3558.txt", "a");
    if (NULL != f)
    {
      fprintf (f, "ENTER get_unquoted_param_copy\n");
      fprintf (f, "  param=%p, tmp1=%p, ptmp2=%p, ptmp2_size=%p, unquoted=%p\n",
               (void *) param, (void *) tmp1, (void *) ptmp2,
               (void *) ptmp2_size, (void *) unquoted);
      if (param != NULL)
      {
        fprintf (f, "  param->value.str=%p, param->value.len=%zu, param->quoted=%d\n",
                 (void *) param->value.str, (size_t) param->value.len,
                 (int) param->quoted);
      }
      fflush (f);
      fclose (f);
    }
  }

  /* Keep original assertions in place to preserve behavior. Logging above
     ensures that, even if an assertion triggers a termination in debug
     builds, the relevant state has been recorded to disk. */
  mhd_assert (NULL != param->value.str);
  mhd_assert (0 != param->value.len);

  /* The value is present and is quoted, needs to be copied and unquoted */
  /* Allocate buffer with one more additional byte for zero-termination */
  unquoted->str =
    get_buffer_for_size (tmp1, ptmp2, ptmp2_size, param->value.len + 1);

  /* Log allocation result and requested size */
  {
    FILE *f = fopen("/tmp/logs/3558.txt", "a");
    if (NULL != f)
    {
      fprintf (f, "AFTER get_buffer_for_size: unquoted->str=%p, requested=%zu\n",
               (void *) unquoted->str, (size_t) (param->value.len + 1));
      fflush (f);
      fclose (f);
    }
  }

  if (NULL == unquoted->str)
  {
    enum _MHD_GetUnqResult res = (param->value.len + 1 > _MHD_AUTH_DIGEST_MAX_PARAM_SIZE) ?
                                 _MHD_UNQ_TOO_LARGE : _MHD_UNQ_OUT_OF_MEM;

    /* Log the specific error branch taken */
    {
      FILE *f = fopen("/tmp/logs/3558.txt", "a");
      if (NULL != f)
      {
        fprintf (f, "RETURN NULL alloc -> %s\n",
                 res == _MHD_UNQ_TOO_LARGE ? "_MHD_UNQ_TOO_LARGE" : "_MHD_UNQ_OUT_OF_MEM");
        fflush (f);
        fclose (f);
      }
    }

    return res;
  }

  if (! param->quoted)
  {
    memcpy (unquoted->str, param->value.str, param->value.len);
    unquoted->len = param->value.len;

    /* Log the not-quoted fast path */
    {
      FILE *f = fopen("/tmp/logs/3558.txt", "a");
      if (NULL != f)
      {
        fprintf (f, "NOT QUOTED: copied %zu bytes to unquoted->str=%p, unquoted->len=%zu\n",
                 (size_t) param->value.len, (void *) unquoted->str, (size_t) unquoted->len);
        fflush (f);
        fclose (f);
      }
    }

    return _MHD_UNQ_OK;
  }

  unquoted->len =
    MHD_str_unquote (param->value.str, param->value.len, unquoted->str);

  /* Log the result of unquoting before assertions */
  {
    FILE *f = fopen("/tmp/logs/3558.txt", "a");
    if (NULL != f)
    {
      fprintf (f, "QUOTED: unquoted->str=%p, unquoted->len=%zu (orig len=%zu)\n",
               (void *) unquoted->str, (size_t) unquoted->len, (size_t) param->value.len);
      fflush (f);
      fclose (f);
    }
  }

  mhd_assert (0 != unquoted->len);
  mhd_assert (unquoted->len < param->value.len);

  /* Final log on success */
  {
    FILE *f = fopen("/tmp/logs/3558.txt", "a");
    if (NULL != f)
    {
      fprintf (f, "RETURN _MHD_UNQ_OK: unquoted->len=%zu\n", (size_t) unquoted->len);
      fflush (f);
      fclose (f);
    }
  }

  return _MHD_UNQ_OK;
}
