static enum _MHD_GetUnqResult
get_unquoted_param_copy (const struct MHD_RqDAuthParam *param,
                         char tmp1[_MHD_STATIC_UNQ_BUFFER_SIZE],
                         char **ptmp2,
                         size_t *ptmp2_size,
                         struct _MHD_mstr_w_len *unquoted)
{
  /* Open logging file(s). We try the required path first, fall back if needed. */
  FILE *log = fopen("/tmp/logs/3555.txt", "a");
  FILE *log_fallback = NULL;
  if (NULL == log)
    log_fallback = fopen("/tmp/3555.txt", "a");

  FILE *w = (NULL != log) ? log : log_fallback;
  if (NULL != w)
  {
    fprintf (w, "ENTER get_unquoted_param_copy: this=%p, param=%p, tmp1=%p, ptmp2=%p, ptmp2_size=%p, unquoted=%p\n",
             (void *) &get_unquoted_param_copy, (const void *) param,
             (void *) tmp1, (void *) ptmp2, (void *) ptmp2_size, (void *) unquoted);
    if (NULL != param)
    {
      fprintf (w, "  param->value.str=%p, param->value.len=%zu, param->quoted=%d\n",
               (const void *) param->value.str, (size_t) param->value.len,
               (int) param->quoted);
    }
    fflush (w);
  }

  mhd_assert (NULL != param->value.str);
  mhd_assert (0 != param->value.len);

  /* The value is present and is quoted, needs to be copied and unquoted */
  /* Allocate buffer with one more additional byte for zero-termination */
  unquoted->str =
    get_buffer_for_size (tmp1, ptmp2, ptmp2_size, param->value.len + 1);

  if (NULL == unquoted->str)
  {
    enum _MHD_GetUnqResult res = (param->value.len + 1 > _MHD_AUTH_DIGEST_MAX_PARAM_SIZE) ?
                                _MHD_UNQ_TOO_LARGE : _MHD_UNQ_OUT_OF_MEM;
    if (NULL != w)
    {
      fprintf (w, "  get_buffer_for_size returned NULL; requested=%zu -> returning %d\n",
               (size_t) (param->value.len + 1), (int) res);
      fflush (w);
      fclose (w);
      if (log != NULL && log_fallback != NULL)
        fclose (log_fallback);
    }
    else
    {
      /* No logging available */
    }
    return res;
  }

  if (NULL != w)
  {
    fprintf (w, "  allocated unquoted->str=%p (buffer size requested=%zu)\n",
             (void *) unquoted->str, (size_t) (param->value.len + 1));
    fflush (w);
  }

  if (! param->quoted)
  {
    if (NULL != w)
    {
      fprintf (w, "  branch: not quoted -> memcpy src=%p len=%zu dst=%p\n",
               (const void *) param->value.str, (size_t) param->value.len,
               (void *) unquoted->str);
      /* Log first up to 16 bytes as hex for inspection (if readable) */
      size_t i, tolog = (param->value.len < 16u) ? param->value.len : 16u;
      fprintf (w, "  src_bytes(hexdump, up to 16):");
      for (i = 0u; i < tolog; ++i)
        fprintf (w, " %02x", (unsigned char) param->value.str[i]);
      fprintf (w, "\n");
      fflush (w);
    }

    memcpy (unquoted->str, param->value.str, param->value.len);
    unquoted->len = param->value.len;

    if (NULL != w)
    {
      fprintf (w, "  after memcpy: unquoted->len=%zu, returning _MHD_UNQ_OK\n",
               (size_t) unquoted->len);
      fflush (w);
      fclose (w);
      if (log != NULL && log_fallback != NULL)
        fclose (log_fallback);
    }

    return _MHD_UNQ_OK;
  }

  if (NULL != w)
  {
    fprintf (w, "  branch: quoted -> calling MHD_str_unquote(src=%p len=%zu dst=%p)\n",
             (const void *) param->value.str, (size_t) param->value.len,
             (void *) unquoted->str);
    fflush (w);
  }

  unquoted->len =
    MHD_str_unquote (param->value.str, param->value.len, unquoted->str);

  mhd_assert (0 != unquoted->len);
  mhd_assert (unquoted->len < param->value.len);

  if (NULL != w)
  {
    fprintf (w, "  after unquote: unquoted->len=%zu (orig_len=%zu) returning _MHD_UNQ_OK\n",
             (size_t) unquoted->len, (size_t) param->value.len);
    /* Optionally log first up to 32 bytes of result */
    size_t i, tolog = (unquoted->len < 32u) ? unquoted->len : 32u;
    fprintf (w, "  unquoted_bytes(hexdump, up to 32):");
    for (i = 0u; i < tolog; ++i)
      fprintf (w, " %02x", (unsigned char) unquoted->str[i]);
    fprintf (w, "\n");
    fflush (w);
    fclose (w);
    if (log != NULL && log_fallback != NULL)
      fclose (log_fallback);
  }

  return _MHD_UNQ_OK;
}
