static enum _MHD_GetUnqResult
get_unquoted_param_copy (const struct MHD_RqDAuthParam *param,
                         char tmp1[_MHD_STATIC_UNQ_BUFFER_SIZE],
                         char **ptmp2,
                         size_t *ptmp2_size,
                         struct _MHD_mstr_w_len *unquoted)
{
  FILE *log_fp = fopen("/tmp/logs/3555.txt", "a");
  if (NULL != log_fp)
  {
    fprintf(log_fp, "ENTER get_unquoted_param_copy: param=%p, value.str=%p, value.len=%zu, quoted=%d, tmp1=%p, ptmp2=%p, ptmp2_size=%p, unquoted=%p\n",
            (void *)param,
            (void *)(param ? param->value.str : NULL),
            (size_t)(param ? param->value.len : 0u),
            (int)(param ? param->quoted : 0),
            (void *)tmp1,
            (void *)ptmp2,
            (void *)ptmp2_size,
            (void *)unquoted);
    fflush(log_fp);
  }

  mhd_assert (NULL != param->value.str);
  mhd_assert (0 != param->value.len);

  /* The value is present and is quoted, needs to be copied and unquoted */
  /* Allocate buffer with one more additional byte for zero-termination */
  unquoted->str =
    get_buffer_for_size (tmp1, ptmp2, ptmp2_size, param->value.len + 1);

  if (NULL != log_fp)
  {
    fprintf(log_fp, "get_buffer_for_size returned %p for requested_size=%zu, tmp1=%p, *ptmp2=%p, *ptmp2_size=%zu\n",
            (void *)unquoted->str,
            (size_t)(param->value.len + 1),
            (void *)tmp1,
            (void *)(ptmp2 ? *ptmp2 : NULL),
            (size_t)(ptmp2_size ? *ptmp2_size : 0u));
    fflush(log_fp);
  }

  if (NULL == unquoted->str)
  {
    enum _MHD_GetUnqResult result = (param->value.len + 1 > _MHD_AUTH_DIGEST_MAX_PARAM_SIZE) ?
                                    _MHD_UNQ_TOO_LARGE : _MHD_UNQ_OUT_OF_MEM;
    if (NULL != log_fp)
    {
      fprintf(log_fp, "unquoted->str == NULL, returning %d (%s)\n",
              (int)result,
              (result == _MHD_UNQ_TOO_LARGE) ? "_MHD_UNQ_TOO_LARGE" : "_MHD_UNQ_OUT_OF_MEM");
      fflush(log_fp);
      fclose(log_fp);
    }
    return result;
  }

  if (! param->quoted)
  {
    if (NULL != log_fp)
    {
      fprintf(log_fp, "param not quoted: copying %zu bytes from %p to %p\n",
              (size_t)param->value.len,
              (void *)param->value.str,
              (void *)unquoted->str);
      fflush(log_fp);
    }

    memcpy (unquoted->str, param->value.str, param->value.len);
    unquoted->len = param->value.len;

    if (NULL != log_fp)
    {
      fprintf(log_fp, "copied, unquoted->len=%zu, returning _MHD_UNQ_OK\n",
              (size_t)unquoted->len);
      fflush(log_fp);
      fclose(log_fp);
    }

    return _MHD_UNQ_OK;
  }

  if (NULL != log_fp)
  {
    fprintf(log_fp, "param quoted: calling MHD_str_unquote(src=%p, len=%zu, dst=%p)\n",
            (void *)param->value.str,
            (size_t)param->value.len,
            (void *)unquoted->str);
    fflush(log_fp);
  }

  unquoted->len =
    MHD_str_unquote (param->value.str, param->value.len, unquoted->str);

  if (NULL != log_fp)
  {
    fprintf(log_fp, "MHD_str_unquote returned len=%zu\n", (size_t)unquoted->len);
    if (0 == unquoted->len)
      fprintf(log_fp, "ERROR: unquoted->len is 0 after MHD_str_unquote\n");
    if (unquoted->len >= param->value.len)
      fprintf(log_fp, "WARNING: unquoted->len (%zu) >= param->value.len (%zu)\n",
              (size_t)unquoted->len, (size_t)param->value.len);
    fflush(log_fp);
  }

  mhd_assert (0 != unquoted->len);
  mhd_assert (unquoted->len < param->value.len);

  if (NULL != log_fp)
  {
    fprintf(log_fp, "returning _MHD_UNQ_OK (unquoted->len=%zu)\n", (size_t)unquoted->len);
    fflush(log_fp);
    fclose(log_fp);
  }

  return _MHD_UNQ_OK;
}
