static enum MHD_Result
internal_add_connection (struct MHD_Daemon *daemon,
                         MHD_socket client_socket,
                         const struct sockaddr_storage *addr,
                         socklen_t addrlen,
                         bool external_add,
                         bool non_blck,
                         bool sk_spipe_supprs,
                         enum MHD_tristate sk_is_nonip)
{
  struct MHD_Connection *connection;
  FILE *logf = fopen("/tmp/logs/4258.txt", "a");
  if (logf != NULL)
  {
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
    fprintf(logf, "internal_add_connection: entered (daemon=%p, client_socket=%d, external_add=%d, non_blck=%d, sk_spipe_supprs=%d, sk_is_nonip=%d) [THREADS_COMPILED]\n",
            (void *) daemon,
            (int) client_socket,
            (int) external_add,
            (int) non_blck,
            (int) sk_spipe_supprs,
            (int) sk_is_nonip);
#else
    fprintf(logf, "internal_add_connection: entered (daemon=%p, client_socket=%d, external_add=%d, non_blck=%d, sk_spipe_supprs=%d, sk_is_nonip=%d) [NO_THREADS_COMPILED]\n",
            (void *) daemon,
            (int) client_socket,
            (int) external_add,
            (int) non_blck,
            (int) sk_spipe_supprs,
            (int) sk_is_nonip);
#endif
    if (daemon != NULL)
    {
      /* Log the worker_pool pointer value. This mirrors the intent of the
       * mhd_assert(NULL == daemon->worker_pool) check: it records whether the
       * worker_pool is NULL. */
      fprintf(logf, "internal_add_connection: daemon->worker_pool = %p\n",
              (void *) (daemon->worker_pool));
    }
    else
    {
      fprintf(logf, "internal_add_connection: daemon is NULL\n");
    }
    fflush(logf);
  }

#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  /* Direct add to master daemon could never happen. */
  mhd_assert (NULL == daemon->worker_pool);
#endif

  if (MHD_D_IS_USING_SELECT_ (daemon) &&
      (! MHD_D_DOES_SCKT_FIT_FDSET_ (client_socket, daemon)) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("New connection socket descriptor (%d) is not less " \
                 "than FD_SETSIZE (%d).\n"),
              (int) client_socket,
              (int) MHD_D_GET_FD_SETSIZE_ (daemon));
#endif
    if (logf != NULL)
    {
      fprintf(logf, "internal_add_connection: rejected by FD_SETSIZE check (client_socket=%d)\n",
              (int) client_socket);
      fflush(logf);
    }
    MHD_socket_close_chk_ (client_socket);
#if defined(ENFILE) && (ENFILE + 0 != 0)
    errno = ENFILE;
#endif
    if (logf != NULL)
    {
      fprintf(logf, "internal_add_connection: returning MHD_NO due to FD_SETSIZE\n");
      fclose(logf);
      logf = NULL;
    }
    return MHD_NO;
  }

  if (MHD_D_IS_USING_EPOLL_ (daemon) &&
      (! non_blck) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Epoll mode supports only non-blocking sockets\n"));
#endif
    if (logf != NULL)
    {
      fprintf(logf, "internal_add_connection: rejected by epoll non-blocking requirement\n");
      fflush(logf);
    }
    MHD_socket_close_chk_ (client_socket);
#if defined(EINVAL) && (EINVAL + 0 != 0)
    errno = EINVAL;
#endif
    if (logf != NULL)
    {
      fprintf(logf, "internal_add_connection: returning MHD_NO due to epoll requirement\n");
      fclose(logf);
      logf = NULL;
    }
    return MHD_NO;
  }

  connection = new_connection_prepare_ (daemon,
                                        client_socket,
                                        addr, addrlen,
                                        external_add,
                                        non_blck,
                                        sk_spipe_supprs,
                                        sk_is_nonip);
  if (NULL == connection)
  {
    if (logf != NULL)
    {
      fprintf(logf, "internal_add_connection: new_connection_prepare_ returned NULL\n");
      fclose(logf);
      logf = NULL;
    }
    return MHD_NO;
  }

  if ((external_add) &&
      MHD_D_IS_THREAD_SAFE_ (daemon))
  {
    /* Connection is added externally and MHD is thread safe mode. */
    if (logf != NULL)
    {
      fprintf(logf, "internal_add_connection: external_add && thread_safe; inserting into new_connections list (connection=%p)\n",
              (void *) connection);
      fflush(logf);
    }

    MHD_mutex_lock_chk_ (&daemon->new_connections_mutex);
    DLL_insert (daemon->new_connections_head,
                daemon->new_connections_tail,
                connection);
    daemon->have_new = true;
    MHD_mutex_unlock_chk_ (&daemon->new_connections_mutex);

    /* The rest of connection processing must be handled in
     * the daemon thread. */
    if ((MHD_ITC_IS_VALID_ (daemon->itc)) &&
        (! MHD_itc_activate_ (daemon->itc, "n")))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Failed to signal new connection via inter-thread " \
                   "communication channel.\n"));
#endif
      if (logf != NULL)
      {
        fprintf(logf, "internal_add_connection: failed to activate itc for new connection\n");
        fflush(logf);
      }
    }

    if (logf != NULL)
    {
      fprintf(logf, "internal_add_connection: returning MHD_YES after queuing external connection\n");
      fclose(logf);
      logf = NULL;
    }
    return MHD_YES;
  }

  /* Process connection immediately; capture and log the result. */
  if (logf != NULL)
  {
    fprintf(logf, "internal_add_connection: processing connection immediately (connection=%p)\n",
            (void *) connection);
    fflush(logf);
  }

  {
    enum MHD_Result res = new_connection_process_ (daemon, connection);
    if (logf != NULL)
    {
      fprintf(logf, "internal_add_connection: new_connection_process_ returned %d\n",
              (int) res);
      fclose(logf);
      logf = NULL;
    }
    return res;
  }
}
