static enum MHD_Result
internal_get_fdset2 (struct MHD_Daemon *daemon,
                     fd_set *read_fd_set,
                     fd_set *write_fd_set,
                     fd_set *except_fd_set,
                     MHD_socket *max_fd,
                     int fd_setsize)
{
  struct MHD_Connection *pos;
  struct MHD_Connection *posn;
  enum MHD_Result result = MHD_YES;
  MHD_socket ls;
  bool itc_added;

  /* Logging probe: append mode so multiple runs accumulate evidence */
  {
    FILE *log = fopen("/tmp/logs/3961.txt", "a");
    if (NULL != log)
    {
      fprintf(log, "internal_get_fdset2 ENTER: daemon=%p, read_fd_set=%p, write_fd_set=%p, except_fd_set=%p, max_fd=%p, fd_setsize=%d\n",
              (void *) daemon, (void *) read_fd_set, (void *) write_fd_set, (void *) except_fd_set, (void *) max_fd, fd_setsize);
      fflush(log);
      fclose(log);
    }
  }

#ifndef HAS_FD_SETSIZE_OVERRIDABLE
  (void) fd_setsize;  /* Mute compiler warning */
  fd_setsize = (int) FD_SETSIZE; /* Help compiler to optimise */
#endif /* ! HAS_FD_SETSIZE_OVERRIDABLE */

  if (daemon->shutdown)
  {
    /* Log the early exit due to shutdown */
    FILE *log = fopen("/tmp/logs/3961.txt", "a");
    if (NULL != log)
    {
      fprintf(log, "internal_get_fdset2 EARLY-EXIT: daemon->shutdown is true, returning MHD_YES\n");
      fflush(log);
      fclose(log);
    }
    return MHD_YES;
  }

  /* The order of FDs added is important for W32 sockets as W32 fd_set has
     limits for number of added FDs instead of the limit for the higher
     FD value. */

  /* Add ITC FD first. The daemon must be able to respond on application
     commands issued in other threads. */
  itc_added = false;
  if (MHD_ITC_IS_VALID_ (daemon->itc))
  {
    itc_added = MHD_add_to_fd_set_ (MHD_itc_r_fd_ (daemon->itc),
                                    read_fd_set,
                                    max_fd,
                                    fd_setsize);
    if (! itc_added)
      result = MHD_NO;

    /* Log ITC add outcome */
    {
      FILE *log = fopen("/tmp/logs/3961.txt", "a");
      if (NULL != log)
      {
        fprintf(log, "ITC check: itc=%p, itc_added=%d, result=%d\n", (void *) daemon->itc, (int) itc_added, (int) result);
        fflush(log);
        fclose(log);
      }
    }
  }

  ls = daemon->was_quiesced ? MHD_INVALID_SOCKET : daemon->listen_fd;
  if (! itc_added &&
      (MHD_INVALID_SOCKET != ls))
  {
    /* Add listen FD if ITC was not added. Listen FD could be used to signal
       the daemon shutdown. */
    if (MHD_add_to_fd_set_ (ls,
                            read_fd_set,
                            max_fd,
                            fd_setsize))
      ls = MHD_INVALID_SOCKET;   /* Already added */
    else
      result = MHD_NO;

    {
      FILE *log = fopen("/tmp/logs/3961.txt", "a");
      if (NULL != log)
      {
        fprintf(log, "Listen FD handling: ls=%ld, itc_added=%d, result=%d\n", (long) ls, (int) itc_added, (int) result);
        fflush(log);
        fclose(log);
      }
    }
  }

  /* Add all sockets to 'except_fd_set' as well to watch for
   * out-of-band data. However, ignore errors if INFO_READ
   * or INFO_WRITE sockets will not fit 'except_fd_set'. */
  /* Start from oldest connections. Make sense for W32 FDSETs. */
  for (pos = daemon->connections_tail; NULL != pos; pos = posn)
  {
    /* Log before assigning posn to capture the loop counter 'pos' */
    {
      FILE *log = fopen("/tmp/logs/3961.txt", "a");
      if (NULL != log)
      {
        fprintf(log, "FOR-LOOP ITERATION START: pos=%p, pos->prev=%p\n", (void *) pos, (void *) pos->prev);
        fflush(log);
        fclose(log);
      }
    }

    posn = pos->prev;

    /* Log the assignment to posn (this is the core of the reported violation
       where the for-clause reads posn which is modified in the loop body). */
    {
      FILE *log = fopen("/tmp/logs/3961.txt", "a");
      if (NULL != log)
      {
        fprintf(log, "Assigned posn = pos->prev -> posn=%p (pos=%p)\n", (void *) posn, (void *) pos);
        fflush(log);
        fclose(log);
      }
    }

    switch (pos->event_loop_info)
    {
    case MHD_EVENT_LOOP_INFO_READ:
    case MHD_EVENT_LOOP_INFO_PROCESS_READ:
      if (! MHD_add_to_fd_set_ (pos->socket_fd,
                                read_fd_set,
                                max_fd,
                                fd_setsize))
        result = MHD_NO;
#ifdef MHD_POSIX_SOCKETS
      if (NULL != except_fd_set)
        (void) MHD_add_to_fd_set_ (pos->socket_fd,
                                   except_fd_set,
                                   max_fd,
                                   fd_setsize);
#endif /* MHD_POSIX_SOCKETS */
      break;
    case MHD_EVENT_LOOP_INFO_WRITE:
      if (! MHD_add_to_fd_set_ (pos->socket_fd,
                                write_fd_set,
                                max_fd,
                                fd_setsize))
        result = MHD_NO;
#ifdef MHD_POSIX_SOCKETS
      if (NULL != except_fd_set)
        (void) MHD_add_to_fd_set_ (pos->socket_fd,
                                   except_fd_set,
                                   max_fd,
                                   fd_setsize);
#endif /* MHD_POSIX_SOCKETS */
      break;
    case MHD_EVENT_LOOP_INFO_PROCESS:
      if ( (NULL == except_fd_set) ||
           ! MHD_add_to_fd_set_ (pos->socket_fd,
                                 except_fd_set,
                                 max_fd,
                                 fd_setsize))
        result = MHD_NO;
      break;
    case MHD_EVENT_LOOP_INFO_CLEANUP:
      /* this should never happen */
      break;
    }

    /* Log after processing this connection iteration */
    {
      FILE *log = fopen("/tmp/logs/3961.txt", "a");
      if (NULL != log)
      {
        fprintf(log, "FOR-LOOP ITERATION END: pos=%p, posn=%p, pos->socket_fd=%ld, pos->event_loop_info=%d, result=%d\n",
                (void *) pos, (void *) posn, (long) pos->socket_fd, (int) pos->event_loop_info, (int) result);
        fflush(log);
        fclose(log);
      }
    }
  }
#ifdef MHD_WINSOCK_SOCKETS
  /* W32 use limited array for fd_set so add INFO_READ/INFO_WRITE sockets
   * only after INFO_BLOCK sockets to ensure that INFO_BLOCK sockets will
   * not be pushed out. */
  if (NULL != except_fd_set)
  {
    for (pos = daemon->connections_tail; NULL != pos; pos = posn)
    {
      /* Log before assigning posn in WINSOCK-specific loop */
      {
        FILE *log = fopen("/tmp/logs/3961.txt", "a");
        if (NULL != log)
        {
          fprintf(log, "WINSOCK-FOR-LOOP ITERATION START: pos=%p, pos->prev=%p\n", (void *) pos, (void *) pos->prev);
          fflush(log);
          fclose(log);
        }
      }

      posn = pos->prev;

      /* Log the assignment in this alternate loop */
      {
        FILE *log = fopen("/tmp/logs/3961.txt", "a");
        if (NULL != log)
        {
          fprintf(log, "WINSOCK Assigned posn = pos->prev -> posn=%p (pos=%p)\n", (void *) posn, (void *) pos);
          fflush(log);
          fclose(log);
        }
      }

      MHD_add_to_fd_set_ (pos->socket_fd,
                          except_fd_set,
                          max_fd,
                          fd_setsize);

      {
        FILE *log = fopen("/tmp/logs/3961.txt", "a");
        if (NULL != log)
        {
          fprintf(log, "WINSOCK-FOR-LOOP ITERATION END: pos=%p, posn=%p, pos->socket_fd=%ld\n",
                  (void *) pos, (void *) posn, (long) pos->socket_fd);
          fflush(log);
          fclose(log);
        }
      }
    }
  }
#endif /* MHD_WINSOCK_SOCKETS */
#if defined(HTTPS_SUPPORT) && defined(UPGRADE_SUPPORT)
  if (1)
  {
    struct MHD_UpgradeResponseHandle *urh;

    for (urh = daemon->urh_tail; NULL != urh; urh = urh->prev)
    {
      if (MHD_NO ==
          urh_to_fdset (urh,
                        read_fd_set,
                        write_fd_set,
                        except_fd_set,
                        max_fd,
                        fd_setsize))
        result = MHD_NO;
    }
  }
#endif

  if (MHD_INVALID_SOCKET != ls)
  {
    /* The listen socket is present and hasn't been added */
    if ((daemon->connections < daemon->connection_limit) &&
        ! daemon->at_limit)
    {
      if (! MHD_add_to_fd_set_ (ls,
                                read_fd_set,
                                max_fd,
                                fd_setsize))
        result = MHD_NO;
    }

    {
      FILE *log = fopen("/tmp/logs/3961.txt", "a");
      if (NULL != log)
      {
        fprintf(log, "POST-CONNECTIONS: ls=%ld, connections=%d, connection_limit=%d, at_limit=%d, result=%d\n",
                (long) ls, (int) daemon->connections, (int) daemon->connection_limit, (int) daemon->at_limit, (int) result);
        fflush(log);
        fclose(log);
      }
    }
  }

#if _MHD_DEBUG_CONNECT
#ifdef HAVE_MESSAGES
  if (NULL != max_fd)
    MHD_DLOG (daemon,
              _ ("Maximum socket in select set: %d\n"),
              *max_fd);
#endif
#endif /* HTTPS_SUPPORT && UPGRADE_SUPPORT */

  /* Final log before returning */
  {
    FILE *log = fopen("/tmp/logs/3961.txt", "a");
    if (NULL != log)
    {
      fprintf(log, "internal_get_fdset2 EXIT: result=%d, max_fd=%p\n", (int) result, (void *) max_fd);
      fflush(log);
      fclose(log);
    }
  }
  return result;
}
