static enum MHD_Result
internal_get_fdset2 (struct MHD_Daemon *daemon,
                     fd_set *read_fd_set,
                     fd_set *write_fd_set,
                     fd_set *except_fd_set,
                     MHD_socket *max_fd,
                     int fd_setsize)
{
  struct MHD_Connection *pos;
  struct MHD_Connection *posn;
  enum MHD_Result result = MHD_YES;
  MHD_socket ls;
  bool itc_added;
  FILE *log = NULL;

  /* Open log early so we can capture entry and any early returns */
  log = fopen("/tmp/logs/3962.txt", "a");
  if (log)
  {
    fprintf(log, "internal_get_fdset2 ENTER: daemon=%p, read_fd_set=%p, write_fd_set=%p, except_fd_set=%p, max_fd=%p, fd_setsize_initial=%d\n",
            (void *)daemon, (void *)read_fd_set, (void *)write_fd_set, (void *)except_fd_set, (void *)max_fd, fd_setsize);
    fflush(log);
  }

#ifndef HAS_FD_SETSIZE_OVERRIDABLE
  /* Original code muted the compiler warning and then assigned fd_setsize.
     We log before and after to capture the parameter modification. */
  if (log)
  {
    fprintf(log, "#ifndef HAS_FD_SETSIZE_OVERRIDABLE: before assignment fd_setsize=%d, &fd_setsize=%p\n",
            fd_setsize, (void *)&fd_setsize);
    fflush(log);
  }
  (void) fd_setsize;  /* Mute compiler warning */
  fd_setsize = (int) FD_SETSIZE; /* Help compiler to optimise */
  if (log)
  {
    fprintf(log, "#ifndef HAS_FD_SETSIZE_OVERRIDABLE: after assignment fd_setsize=%d\n",
            fd_setsize);
    fflush(log);
  }
#else
  /* If HAS_FD_SETSIZE_OVERRIDABLE is defined, no assignment occurs. */
  if (log)
  {
    fprintf(log, "HAS_FD_SETSIZE_OVERRIDABLE defined: no assignment to fd_setsize, current=%d\n",
            fd_setsize);
    fflush(log);
  }
#endif /* ! HAS_FD_SETSIZE_OVERRIDABLE */

  if (daemon->shutdown)
  {
    if (log)
    {
      fprintf(log, "daemon->shutdown true: early return MHD_YES, fd_setsize=%d\n", fd_setsize);
      fflush(log);
      fclose(log);
    }
    return MHD_YES;
  }

  /* The order of FDs added is important for W32 sockets as W32 fd_set has
     limits for number of added FDs instead of the limit for the higher
     FD value. */

  /* Add ITC FD first. The daemon must be able to respond on application
     commands issued in other threads. */
  itc_added = false;
  if (MHD_ITC_IS_VALID_ (daemon->itc))
  {
    itc_added = MHD_add_to_fd_set_ (MHD_itc_r_fd_ (daemon->itc),
                                    read_fd_set,
                                    max_fd,
                                    fd_setsize);
    if (log)
    {
      fprintf(log, "ITC valid: MHD_itc_r_fd_ added=%d, itc_added=%d, fd_setsize=%d\n",
              (int) MHD_itc_r_fd_ (daemon->itc), (int) itc_added, fd_setsize);
      fflush(log);
    }
    if (! itc_added)
      result = MHD_NO;
  }

  ls = daemon->was_quiesced ? MHD_INVALID_SOCKET : daemon->listen_fd;
  if (! itc_added &&
      (MHD_INVALID_SOCKET != ls))
  {
    /* Add listen FD if ITC was not added. Listen FD could be used to signal
       the daemon shutdown. */
    if (MHD_add_to_fd_set_ (ls,
                            read_fd_set,
                            max_fd,
                            fd_setsize))
      ls = MHD_INVALID_SOCKET;   /* Already added */
    else
      result = MHD_NO;
    if (log)
    {
      fprintf(log, "listen FD processing: ls=%d, ls_now=%d, result=%d, fd_setsize=%d\n",
              (int) daemon->listen_fd, (int) ls, (int) result, fd_setsize);
      fflush(log);
    }
  }

  /* Add all sockets to 'except_fd_set' as well to watch for
   * out-of-band data. However, ignore errors if INFO_READ
   * or INFO_WRITE sockets will not fit 'except_fd_set'. */
  /* Start from oldest connections. Make sense for W32 FDSETs. */
  for (pos = daemon->connections_tail; NULL != pos; pos = posn)
  {
    posn = pos->prev;

    switch (pos->event_loop_info)
    {
    case MHD_EVENT_LOOP_INFO_READ:
    case MHD_EVENT_LOOP_INFO_PROCESS_READ:
      if (! MHD_add_to_fd_set_ (pos->socket_fd,
                                read_fd_set,
                                max_fd,
                                fd_setsize))
        result = MHD_NO;
#ifdef MHD_POSIX_SOCKETS
      if (NULL != except_fd_set)
        (void) MHD_add_to_fd_set_ (pos->socket_fd,
                                   except_fd_set,
                                   max_fd,
                                   fd_setsize);
#endif /* MHD_POSIX_SOCKETS */
      break;
    case MHD_EVENT_LOOP_INFO_WRITE:
      if (! MHD_add_to_fd_set_ (pos->socket_fd,
                                write_fd_set,
                                max_fd,
                                fd_setsize))
        result = MHD_NO;
#ifdef MHD_POSIX_SOCKETS
      if (NULL != except_fd_set)
        (void) MHD_add_to_fd_set_ (pos->socket_fd,
                                   except_fd_set,
                                   max_fd,
                                   fd_setsize);
#endif /* MHD_POSIX_SOCKETS */
      break;
    case MHD_EVENT_LOOP_INFO_PROCESS:
      if ( (NULL == except_fd_set) ||
           ! MHD_add_to_fd_set_ (pos->socket_fd,
                                 except_fd_set,
                                 max_fd,
                                 fd_setsize))
        result = MHD_NO;
      break;
    case MHD_EVENT_LOOP_INFO_CLEANUP:
      /* this should never happen */
      break;
    }
    if (log)
    {
      fprintf(log, "processed connection pos=%p socket_fd=%d event_loop_info=%d current_result=%d fd_setsize=%d\n",
              (void *) pos, (int) pos->socket_fd, (int) pos->event_loop_info, (int) result, fd_setsize);
      fflush(log);
    }
  }
#ifdef MHD_WINSOCK_SOCKETS
  /* W32 use limited array for fd_set so add INFO_READ/INFO_WRITE sockets
   * only after INFO_BLOCK sockets to ensure that INFO_BLOCK sockets will
   * not be pushed out. */
  if (NULL != except_fd_set)
  {
    for (pos = daemon->connections_tail; NULL != pos; pos = posn)
    {
      posn = pos->prev;
      MHD_add_to_fd_set_ (pos->socket_fd,
                          except_fd_set,
                          max_fd,
                          fd_setsize);
    }
  }
#endif /* MHD_WINSOCK_SOCKETS */
#if defined(HTTPS_SUPPORT) && defined(UPGRADE_SUPPORT)
  if (1)
  {
    struct MHD_UpgradeResponseHandle *urh;

    for (urh = daemon->urh_tail; NULL != urh; urh = urh->prev)
    {
      if (MHD_NO ==
          urh_to_fdset (urh,
                        read_fd_set,
                        write_fd_set,
                        except_fd_set,
                        max_fd,
                        fd_setsize))
        result = MHD_NO;
    }
  }
#endif

  if (MHD_INVALID_SOCKET != ls)
  {
    /* The listen socket is present and hasn't been added */
    if ((daemon->connections < daemon->connection_limit) &&
        ! daemon->at_limit)
    {
      if (! MHD_add_to_fd_set_ (ls,
                                read_fd_set,
                                max_fd,
                                fd_setsize))
        result = MHD_NO;
    }
  }

#if _MHD_DEBUG_CONNECT
#ifdef HAVE_MESSAGES
  if (NULL != max_fd)
    MHD_DLOG (daemon,
              _ ("Maximum socket in select set: %d\n"),
              *max_fd);
#endif
#endif /* HTTPS_SUPPORT && UPGRADE_SUPPORT */

  if (log)
  {
    fprintf(log, "internal_get_fdset2 EXIT: result=%d, final_fd_setsize=%d, daemon=%p\n",
            (int) result, fd_setsize, (void *) daemon);
    fflush(log);
    fclose(log);
  }
  return result;
}
