static enum MHD_Result
internal_get_fdset2 (struct MHD_Daemon *daemon,
                     fd_set *read_fd_set,
                     fd_set *write_fd_set,
                     fd_set *except_fd_set,
                     MHD_socket *max_fd,
                     int fd_setsize)
{
  struct MHD_Connection *pos;
  struct MHD_Connection *posn;
  enum MHD_Result result = MHD_YES;
  MHD_socket ls;
  bool itc_added;
  FILE *logf = NULL;

#ifndef HAS_FD_SETSIZE_OVERRIDABLE
  (void) fd_setsize;  /* Mute compiler warning */
  fd_setsize = (int) FD_SETSIZE; /* Help compiler to optimise */
#endif /* ! HAS_FD_SETSIZE_OVERRIDABLE */

  /* Open log early so that even early returns are recorded. Use append
     mode to avoid overwriting previous runs. If the directory doesn't
     exist fopen may fail; in that case logging is skipped silently. */
  logf = fopen("/tmp/logs/3965.txt", "a");
  if (logf)
  {
    (void) fprintf(logf, "internal_get_fdset2: entry daemon=%p fd_setsize=%d\n",
                   (void *) daemon, fd_setsize);
    (void) fflush(logf);
  }

  if (daemon->shutdown)
  {
    if (logf)
    {
      (void) fprintf(logf, "internal_get_fdset2: daemon->shutdown true, returning MHD_YES\n");
      (void) fclose(logf);
    }
    return MHD_YES;
  }

  /* The order of FDs added is important for W32 sockets as W32 fd_set has
     limits for number of added FDs instead of the limit for the higher
     FD value. */

  /* Add ITC FD first. The daemon must be able to respond on application
     commands issued in other threads. */
  itc_added = false;
  if (MHD_ITC_IS_VALID_ (daemon->itc))
  {
    itc_added = MHD_add_to_fd_set_ (MHD_itc_r_fd_ (daemon->itc),
                                    read_fd_set,
                                    max_fd,
                                    fd_setsize);
    if (logf)
    {
      (void) fprintf(logf, "internal_get_fdset2: ITC valid, itc_added=%d\n",
                     (int) itc_added);
      (void) fflush(logf);
    }
    if (! itc_added)
      result = MHD_NO;
  }

  ls = daemon->was_quiesced ? MHD_INVALID_SOCKET : daemon->listen_fd;
  if (! itc_added &&
      (MHD_INVALID_SOCKET != ls))
  {
    /* Add listen FD if ITC was not added. Listen FD could be used to signal
       the daemon shutdown. */
    if (MHD_add_to_fd_set_ (ls,
                            read_fd_set,
                            max_fd,
                            fd_setsize))
      ls = MHD_INVALID_SOCKET;   /* Already added */
    else
      result = MHD_NO;

    if (logf)
    {
      (void) fprintf(logf, "internal_get_fdset2: listen fd processed, ls=%p\n",
                     (void *) (intptr_t) ls);
      (void) fflush(logf);
    }
  }

  /* Add all sockets to 'except_fd_set' as well to watch for
   * out-of-band data. However, ignore errors if INFO_READ
   * or INFO_WRITE sockets will not fit 'except_fd_set'. */
  /* Start from oldest connections. Make sense for W32 FDSETs. */
  for (pos = daemon->connections_tail; NULL != pos; pos = posn)
  {
    posn = pos->prev;

    if (logf)
    {
      (void) fprintf(logf, "conn loop: pos=%p prev=%p event_loop_info=%d socket_fd=%p\n",
                     (void *) pos, (void *) posn,
                     (int) pos->event_loop_info,
                     (void *) (intptr_t) pos->socket_fd);
      (void) fflush(logf);
    }

    switch (pos->event_loop_info)
    {
    case MHD_EVENT_LOOP_INFO_READ:
    case MHD_EVENT_LOOP_INFO_PROCESS_READ:
      if (! MHD_add_to_fd_set_ (pos->socket_fd,
                                read_fd_set,
                                max_fd,
                                fd_setsize))
        result = MHD_NO;
#ifdef MHD_POSIX_SOCKETS
      if (NULL != except_fd_set)
        (void) MHD_add_to_fd_set_ (pos->socket_fd,
                                   except_fd_set,
                                   max_fd,
                                   fd_setsize);
#endif /* MHD_POSIX_SOCKETS */
      break;
    case MHD_EVENT_LOOP_INFO_WRITE:
      if (! MHD_add_to_fd_set_ (pos->socket_fd,
                                write_fd_set,
                                max_fd,
                                fd_setsize))
        result = MHD_NO;
#ifdef MHD_POSIX_SOCKETS
      if (NULL != except_fd_set)
        (void) MHD_add_to_fd_set_ (pos->socket_fd,
                                   except_fd_set,
                                   max_fd,
                                   fd_setsize);
#endif /* MHD_POSIX_SOCKETS */
      break;
    case MHD_EVENT_LOOP_INFO_PROCESS:
      if ( (NULL == except_fd_set) ||
           ! MHD_add_to_fd_set_ (pos->socket_fd,
                                 except_fd_set,
                                 max_fd,
                                 fd_setsize))
        result = MHD_NO;
      break;
    case MHD_EVENT_LOOP_INFO_CLEANUP:
      /* this should never happen */
      break;
    }
  }
#ifdef MHD_WINSOCK_SOCKETS
  /* W32 use limited array for fd_set so add INFO_READ/INFO_WRITE sockets
   * only after INFO_BLOCK sockets to ensure that INFO_BLOCK sockets will
   * not be pushed out. */
  if (NULL != except_fd_set)
  {
    for (pos = daemon->connections_tail; NULL != pos; pos = posn)
    {
      posn = pos->prev;
      MHD_add_to_fd_set_ (pos->socket_fd,
                          except_fd_set,
                          max_fd,
                          fd_setsize);
    }
  }
#endif /* MHD_WINSOCK_SOCKETS */
#if defined(HTTPS_SUPPORT) && defined(UPGRADE_SUPPORT)
  if (1)
  {
    struct MHD_UpgradeResponseHandle *urh;

    if (logf)
    {
      (void) fprintf(logf, "entering urh loop: urh_tail=%p\n",
                     (void *) daemon->urh_tail);
      (void) fflush(logf);
    }

    for (urh = daemon->urh_tail; NULL != urh; urh = urh->prev)
    {
      /* Log the current urh and its prev pointer to evidence read-only
         traversal of the list (no modification is performed here). */
      if (logf)
      {
        (void) fprintf(logf, "urh loop: urh=%p urh->prev=%p\n",
                       (void *) urh, (void *) urh->prev);
        (void) fflush(logf);
      }

      /* Preserve original behavior: call urh_to_fdset and update result
         accordingly. Capture return value so we can log it. */
      {
        enum MHD_Result urh_res = urh_to_fdset (urh,
                                                read_fd_set,
                                                write_fd_set,
                                                except_fd_set,
                                                max_fd,
                                                fd_setsize);
        if (logf)
        {
          (void) fprintf(logf, "urh_to_fdset returned %d for urh=%p\n",
                         (int) urh_res, (void *) urh);
          (void) fflush(logf);
        }
        if (MHD_NO == urh_res)
          result = MHD_NO;
      }
    }

    if (logf)
    {
      (void) fprintf(logf, "exiting urh loop\n");
      (void) fflush(logf);
    }
  }
#endif

  if (MHD_INVALID_SOCKET != ls)
  {
    /* The listen socket is present and hasn't been added */
    if ((daemon->connections < daemon->connection_limit) &&
        ! daemon->at_limit)
    {
      if (! MHD_add_to_fd_set_ (ls,
                                read_fd_set,
                                max_fd,
                                fd_setsize))
        result = MHD_NO;
    }
  }

#if _MHD_DEBUG_CONNECT
#ifdef HAVE_MESSAGES
  if (NULL != max_fd)
    MHD_DLOG (daemon,
              _ ("Maximum socket in select set: %d\n"),
              *max_fd);
#endif
#endif /* HTTPS_SUPPORT && UPGRADE_SUPPORT */

  if (logf)
  {
    (void) fprintf(logf, "internal_get_fdset2: exit result=%d\n", (int) result);
    (void) fclose(logf);
  }
  return result;
}
