static enum MHD_Result
internal_get_fdset2 (struct MHD_Daemon *daemon,
                     fd_set *read_fd_set,
                     fd_set *write_fd_set,
                     fd_set *except_fd_set,
                     MHD_socket *max_fd,
                     int fd_setsize)
{
  struct MHD_Connection *pos;
  struct MHD_Connection *posn;
  enum MHD_Result result = MHD_YES;
  MHD_socket ls;
  bool itc_added;
  FILE *fp = NULL;

#ifndef HAS_FD_SETSIZE_OVERRIDABLE
  (void) fd_setsize;  /* Mute compiler warning */
  fd_setsize = (int) FD_SETSIZE; /* Help compiler to optimise */
#endif /* ! HAS_FD_SETSIZE_OVERRIDABLE */

  /* Open log file (try /tmp/logs first, fallback to /tmp if needed). */
  fp = fopen("/tmp/logs/3965.txt", "a");
  if (NULL == fp)
    fp = fopen("/tmp/3965.txt", "a");
  if (NULL != fp)
  {
    fprintf(fp, "internal_get_fdset2 ENTER: daemon=%p fd_setsize=%d\n",
            (void *) daemon, fd_setsize);
    if (NULL != max_fd)
      fprintf(fp, "  initial max_fd=%d\n", (int) *max_fd);
  }

  if (daemon->shutdown)
  {
    if (NULL != fp)
    {
      fprintf(fp, "  daemon->shutdown == true: early return MHD_YES\n");
      fclose(fp);
    }
    return MHD_YES;
  }

  /* The order of FDs added is important for W32 sockets as W32 fd_set has
     limits for number of added FDs instead of the limit for the higher
     FD value. */

  /* Add ITC FD first. The daemon must be able to respond on application
     commands issued in other threads. */
  itc_added = false;
  if (MHD_ITC_IS_VALID_ (daemon->itc))
  {
    if (NULL != fp)
      fprintf(fp, "  ITC is valid (daemon->itc=%p)\n", (void *) daemon->itc);

    itc_added = MHD_add_to_fd_set_ (MHD_itc_r_fd_ (daemon->itc),
                                    read_fd_set,
                                    max_fd,
                                    fd_setsize);
    if (NULL != fp)
      fprintf(fp, "  ITC add_to_fd_set returned %s\n",
              itc_added ? "true" : "false");
    if (! itc_added)
      result = MHD_NO;
  }
  else
  {
    if (NULL != fp)
      fprintf(fp, "  ITC is NOT valid (daemon->itc=%p)\n", (void *) daemon->itc);
  }

  ls = daemon->was_quiesced ? MHD_INVALID_SOCKET : daemon->listen_fd;
  if (NULL != fp)
    fprintf(fp, "  computed ls=%lld (was_quiesced=%s, listen_fd=%lld)\n",
            (long long) ls,
            daemon->was_quiesced ? "true" : "false",
            (long long) daemon->listen_fd);

  if (! itc_added &&
      (MHD_INVALID_SOCKET != ls))
  {
    /* Add listen FD if ITC was not added. Listen FD could be used to signal
       the daemon shutdown. */
    if (MHD_add_to_fd_set_ (ls,
                            read_fd_set,
                            max_fd,
                            fd_setsize))
    {
      ls = MHD_INVALID_SOCKET;   /* Already added */
      if (NULL != fp)
        fprintf(fp, "  listen FD added to read_fd_set, mark ls invalid\n");
    }
    else
    {
      result = MHD_NO;
      if (NULL != fp)
        fprintf(fp, "  listen FD NOT added to read_fd_set -> result=MHD_NO\n");
    }
  }

  /* Add all sockets to 'except_fd_set' as well to watch for
   * out-of-band data. However, ignore errors if INFO_READ
   * or INFO_WRITE sockets will not fit 'except_fd_set'. */
  /* Start from oldest connections. Make sense for W32 FDSETs. */
  for (pos = daemon->connections_tail; NULL != pos; pos = posn)
  {
    posn = pos->prev;

    if (NULL != fp)
      fprintf(fp, "  connection pos=%p socket_fd=%d event_loop_info=%d\n",
              (void *) pos,
              (int) pos->socket_fd,
              (int) pos->event_loop_info);

    switch (pos->event_loop_info)
    {
    case MHD_EVENT_LOOP_INFO_READ:
    case MHD_EVENT_LOOP_INFO_PROCESS_READ:
      if (! MHD_add_to_fd_set_ (pos->socket_fd,
                                read_fd_set,
                                max_fd,
                                fd_setsize))
        result = MHD_NO;
#ifdef MHD_POSIX_SOCKETS
      if (NULL != except_fd_set)
      {
        (void) MHD_add_to_fd_set_ (pos->socket_fd,
                                   except_fd_set,
                                   max_fd,
                                   fd_setsize);
        if (NULL != fp)
          fprintf(fp, "    added socket_fd=%d to except_fd_set (pos read)\n",
                  (int) pos->socket_fd);
      }
#endif /* MHD_POSIX_SOCKETS */
      break;
    case MHD_EVENT_LOOP_INFO_WRITE:
      if (! MHD_add_to_fd_set_ (pos->socket_fd,
                                write_fd_set,
                                max_fd,
                                fd_setsize))
        result = MHD_NO;
#ifdef MHD_POSIX_SOCKETS
      if (NULL != except_fd_set)
      {
        (void) MHD_add_to_fd_set_ (pos->socket_fd,
                                   except_fd_set,
                                   max_fd,
                                   fd_setsize);
        if (NULL != fp)
          fprintf(fp, "    added socket_fd=%d to except_fd_set (pos write)\n",
                  (int) pos->socket_fd);
      }
#endif /* MHD_POSIX_SOCKETS */
      break;
    case MHD_EVENT_LOOP_INFO_PROCESS:
      if ( (NULL == except_fd_set) ||
           ! MHD_add_to_fd_set_ (pos->socket_fd,
                                 except_fd_set,
                                 max_fd,
                                 fd_setsize))
        result = MHD_NO;
      break;
    case MHD_EVENT_LOOP_INFO_CLEANUP:
      /* this should never happen */
      if (NULL != fp)
        fprintf(fp, "    event_loop_info CLEANUP encountered for pos=%p (should not happen)\n",
                (void *) pos);
      break;
    }

    if (NULL != fp)
      fprintf(fp, "    interim result=%s max_fd=%s\n",
              result == MHD_YES ? "MHD_YES" : "MHD_NO",
              (NULL != max_fd) ? "(value updated)" : "(max_fd NULL)");
  }
#ifdef MHD_WINSOCK_SOCKETS
  /* W32 use limited array for fd_set so add INFO_READ/INFO_WRITE sockets
   * only after INFO_BLOCK sockets to ensure that INFO_BLOCK sockets will
   * not be pushed out. */
  if (NULL != except_fd_set)
  {
    for (pos = daemon->connections_tail; NULL != pos; pos = posn)
    {
      posn = pos->prev;
      MHD_add_to_fd_set_ (pos->socket_fd,
                          except_fd_set,
                          max_fd,
                          fd_setsize);
      if (NULL != fp)
        fprintf(fp, "  WINSOCK: added socket_fd=%d to except_fd_set (post-pass)\n",
                (int) pos->socket_fd);
    }
  }
#endif /* MHD_WINSOCK_SOCKETS */
#if defined(HTTPS_SUPPORT) && defined(UPGRADE_SUPPORT)
  if (1)
  {
    struct MHD_UpgradeResponseHandle *urh;

    for (urh = daemon->urh_tail; NULL != urh; urh = urh->prev)
    {
      if (NULL != fp)
        fprintf(fp, "  processing urh=%p\n", (void *) urh);

      if (MHD_NO ==
          urh_to_fdset (urh,
                        read_fd_set,
                        write_fd_set,
                        except_fd_set,
                        max_fd,
                        fd_setsize))
        result = MHD_NO;
    }
  }
#endif

  if (MHD_INVALID_SOCKET != ls)
  {
    /* The listen socket is present and hasn't been added */
    if ((daemon->connections < daemon->connection_limit) &&
        ! daemon->at_limit)
    {
      if (! MHD_add_to_fd_set_ (ls,
                                read_fd_set,
                                max_fd,
                                fd_setsize))
        result = MHD_NO;
      if (NULL != fp)
        fprintf(fp, "  final LISTEN FD path: connections=%u limit=%u at_limit=%s added=%s\n",
                (unsigned) daemon->connections,
                (unsigned) daemon->connection_limit,
                daemon->at_limit ? "true" : "false",
                (result == MHD_YES) ? "yes" : "no");
    }
    else if (NULL != fp)
    {
      fprintf(fp, "  listen socket present but not added: connections=%u limit=%u at_limit=%s\n",
              (unsigned) daemon->connections,
              (unsigned) daemon->connection_limit,
              daemon->at_limit ? "true" : "false");
    }
  }

#if _MHD_DEBUG_CONNECT
#ifdef HAVE_MESSAGES
  if (NULL != max_fd)
    MHD_DLOG (daemon,
              _ ("Maximum socket in select set: %d\n"),
              *max_fd);
#endif
#endif /* HTTPS_SUPPORT && UPGRADE_SUPPORT */

  if (NULL != fp)
  {
    fprintf(fp, "internal_get_fdset2 EXIT: result=%s", result == MHD_YES ? "MHD_YES" : "MHD_NO");
    if (NULL != max_fd)
      fprintf(fp, " max_fd=%d", (int) *max_fd);
    fprintf(fp, "\n");
    fclose(fp);
  }
  return result;
}
