static void log_internal_run_event(const char *tag, struct MHD_Daemon *daemon, int fd_setsize, const char *extra)
{
    FILE *f = fopen("/tmp/logs/2868.txt", "a");
    if (NULL == f)
        return;
    /* Basic daemon state that helps reproduce the assertion evaluation */
    fprintf(f, "[%s] tag=%s daemon=%p options=0x%08x tid=%p fd_setsize=%d extra=%s\n",
            __func__, tag, (void *) daemon,
            (unsigned int) (daemon ? daemon->options : 0U),
            (void *) (daemon ? (intptr_t) daemon->tid : 0), fd_setsize,
            (extra ? extra : "(null)"));
    fflush(f);
    fclose(f);
}

static void log_internal_assert_eval(const char *assert_desc,
                                     struct MHD_Daemon *daemon,
                                     int lhs_options_mask_nonzero,
                                     bool predicate_value)
{
    FILE *f = fopen("/tmp/logs/2868.txt", "a");
    if (NULL == f)
        return;
    fprintf(f, "[%s] assert:"" %s daemon=%p options=0x%08x mask_nonzero=%d predicate=%d\n",
            __func__, assert_desc, (void *) daemon,
            (unsigned int) (daemon ? daemon->options : 0U), lhs_options_mask_nonzero,
            predicate_value ? 1 : 0);
    fflush(f);
    fclose(f);
}

static enum MHD_Result
internal_run_from_select (struct MHD_Daemon *daemon,
                          const fd_set *read_fd_set,
                          const fd_set *write_fd_set,
                          const fd_set *except_fd_set,
                          int fd_setsize)
{
  MHD_socket ds;
#if defined(HTTPS_SUPPORT) && defined(UPGRADE_SUPPORT)
  struct MHD_UpgradeResponseHandle *urh;
  struct MHD_UpgradeResponseHandle *urhn;
#endif /* HTTPS_SUPPORT && UPGRADE_SUPPORT */

  /* Log entry into the function */
  log_internal_run_event("ENTER", daemon, fd_setsize, "start internal_run_from_select");

  /* Evaluate and log the first assertions without changing behaviour. */
  {
    /* Original: mhd_assert ((0 == (daemon->options & MHD_USE_SELECT_INTERNALLY)) || \
                   (MHD_thread_handle_ID_is_valid_ID_ (daemon->tid))); */
    int options_mask_nonzero = (0 == (daemon->options & MHD_USE_SELECT_INTERNALLY)) ? 0 : 1;
    bool is_valid_id = MHD_thread_handle_ID_is_valid_ID_ (daemon->tid);
    log_internal_assert_eval("(0 == (daemon->options & MHD_USE_SELECT_INTERNALLY)) || MHD_thread_handle_ID_is_valid_ID_", daemon, options_mask_nonzero, is_valid_id);
    mhd_assert ((0 == (daemon->options & MHD_USE_SELECT_INTERNALLY)) || (is_valid_id));
  }

  {
    /* Original: mhd_assert ((0 != (daemon->options & MHD_USE_SELECT_INTERNALLY)) || \
                   (! MHD_thread_handle_ID_is_valid_ID_ (daemon->tid))); */
    int options_mask_nonzero = (0 != (daemon->options & MHD_USE_SELECT_INTERNALLY)) ? 1 : 0;
    bool not_valid_id = ! MHD_thread_handle_ID_is_valid_ID_ (daemon->tid);
    log_internal_assert_eval("(0 != (daemon->options & MHD_USE_SELECT_INTERNALLY)) || !MHD_thread_handle_ID_is_valid_ID_", daemon, options_mask_nonzero, not_valid_id);
    mhd_assert ((0 != (daemon->options & MHD_USE_SELECT_INTERNALLY)) || (not_valid_id));
  }

  {
    /* This is the assertion that the external agent flagged as dead-code related.
       We explicitly evaluate the predicate, log its value and then assert the
       combined expression using the computed value to avoid double-evaluation. */
    bool current_thread_pred = MHD_thread_handle_ID_is_current_thread_ (daemon->tid);
    int lhs_options_mask_nonzero = (0 == (daemon->options & MHD_USE_SELECT_INTERNALLY)) ? 0 : 1;
    /* Log the predicate and related left-hand condition so test harness can observe evaluation. */
    log_internal_assert_eval("(0 == (daemon->options & MHD_USE_SELECT_INTERNALLY)) || MHD_thread_handle_ID_is_current_thread_", daemon, lhs_options_mask_nonzero, current_thread_pred);
    mhd_assert ((0 == (daemon->options & MHD_USE_SELECT_INTERNALLY)) || (current_thread_pred));
  }

  mhd_assert (0 < fd_setsize);
  (void) fd_setsize; /* Mute compiler warning */
#ifndef HAS_FD_SETSIZE_OVERRIDABLE
  (void) fd_setsize; /* Mute compiler warning */
  mhd_assert (((int) FD_SETSIZE) <= fd_setsize);
  fd_setsize = FD_SETSIZE; /* Help compiler to optimise */
#endif /* ! HAS_FD_SETSIZE_OVERRIDABLE */

  /* Clear ITC to avoid spinning select */
  /* Do it before any other processing so new signals
     will trigger select again and will be processed */
  if (MHD_ITC_IS_VALID_ (daemon->itc))
  { /* Have ITC */
    bool need_to_clear_itc = true; /* ITC is always non-blocking, it is safe to clear even if ITC not activated */
    if (MHD_SCKT_FD_FITS_FDSET_SETSIZE_ (MHD_itc_r_fd_ (daemon->itc),
                                         NULL, fd_setsize))
      need_to_clear_itc = FD_ISSET (MHD_itc_r_fd_ (daemon->itc), 
                                    (fd_set *) _MHD_DROP_CONST (read_fd_set)); /* Skip clearing, if not needed */
    if (need_to_clear_itc)
      MHD_itc_clear_ (daemon->itc);
  }

  /* Reset. New value will be set when connections are processed. */
  /* Note: no-op for thread-per-connection as it is always false in that mode. */
  daemon->data_already_pending = false;

  /* Process externally added connection if any */
  if (daemon->have_new)
    new_connections_list_process_ (daemon);

  /* select connection thread handling type */
  ds = daemon->listen_fd;
  if ( (MHD_INVALID_SOCKET != ds) &&
       (! daemon->was_quiesced) )
  {
    bool need_to_accept;
    if (MHD_SCKT_FD_FITS_FDSET_SETSIZE_ (ds, NULL, fd_setsize))
      need_to_accept = FD_ISSET (ds,
                                 (fd_set *) _MHD_DROP_CONST (read_fd_set));
    else                                       /* Cannot check whether new connection are pending */
      need_to_accept = daemon->listen_nonblk;  /* Try to accept if non-blocking */

    if (need_to_accept)
      (void) MHD_accept_connection (daemon);
  }

  if (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon))
  {
    /* do not have a thread per connection, process all connections now */
    struct MHD_Connection *pos;
    for (pos = daemon->connections_tail; NULL != pos; pos = pos->prev)
    {
      MHD_socket cs;
      bool r_ready;
      bool w_ready;
      bool has_err;

      cs = pos->socket_fd;
      if (MHD_INVALID_SOCKET == cs)
        continue;

      if (MHD_SCKT_FD_FITS_FDSET_SETSIZE_ (cs, NULL, fd_setsize))
      {
        r_ready = FD_ISSET (cs,
                            (fd_set *) _MHD_DROP_CONST (read_fd_set));
        w_ready = FD_ISSET (cs,
                            (fd_set *) _MHD_DROP_CONST (write_fd_set));
        has_err = (NULL != except_fd_set) &&
                  FD_ISSET (cs,
                            (fd_set *) _MHD_DROP_CONST (except_fd_set));
      }
      else
      { /* Cannot check the real readiness */
        r_ready = pos->sk_nonblck;
        w_ready = r_ready;
        has_err = false;
      }
      call_handlers (pos,
                     r_ready,
                     w_ready,
                     has_err);
    }
  }

#if defined(HTTPS_SUPPORT) && defined(UPGRADE_SUPPORT)
  /* handle upgraded HTTPS connections */
  for (urh = daemon->urh_tail; NULL != urh; urh = urhn)
  {
    urhn = urh->prev;
    /* update urh state based on select() output */
    urh_from_fdset (urh,
                    read_fd_set,
                    write_fd_set,
                    except_fd_set,
                    fd_setsize);
    /* call generic forwarding function for passing data */
    process_urh (urh);
    /* Finished forwarding? */
    if ( (0 == urh->in_buffer_size) &&
         (0 == urh->out_buffer_size) &&
         (0 == urh->in_buffer_used) &&
         (0 == urh->out_buffer_used) )
    {
      MHD_connection_finish_forward_ (urh->connection);
      urh->clean_ready = true;
      /* Resuming will move connection to cleanup list. */
      MHD_resume_connection (urh->connection);
    }
  }
#endif /* HTTPS_SUPPORT && UPGRADE_SUPPORT */
  MHD_cleanup_connections (daemon);

  /* Log exit */
  log_internal_run_event("EXIT", daemon, fd_setsize, "end internal_run_from_select");
  return MHD_YES;
}
